css:
@-moz-document url-prefix('http://teamcity'),  url-prefix('https://teamcity') {
body {
    background: #000000;
    color: #3b91f5 !important;
}

#footer {
    background-color: #000000;
}

table.overviewTypeTable {
    background-color: #000000;
}

.simpleTabs .tabs li.selected a:link, .simpleTabs .tabs li.selected a:hover {
    color: #418ff6;
}

.btn, .healthItemIndicator, .ui-datepicker-buttonpane button {
    background-color: #000000;
}

.tabCounter {
    border: 1px solid #D1E0F3;
    background: #000000;
}

span.pc.highlightChanges {
    background-color: #000000;
    border: 1px solid #ffffff;
}

.overviewTypeTable tr:hover {
    background-color: #111111;
    box-shadow: 29px 0 #111111;
}

.btn, .healthItemIndicator, .ui-datepicker-buttonpane button {
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.75);
}

.btn:hover, .healthItemIndicator:hover, .ui-datepicker-buttonpane button:hover {
    color: #000;
    background-color: #418ff6;
}

.BuildBranch__main--Cn {
    background-color: #418ff6;
}

.BuildBranch__link--1v:hover, .BuildBranch__link--1v:focus {
    background-color: #111111;
    border: 1px solid #418ff6;
}

#restPageTitle a:not(.error-url) {
    color: #418ff6;
}

.tableCaption a.buildTypeName {
    color: #418ff6;
}

.build .projectHeader {
    background-color: #171717;
}

.projectHeaderTable a, .projectHeaderTable a:hover, .projectHeaderTable a:visited {
    color: #418ff6;
}

.BuildBranch__link--1v {
    color: #ffffff;
    background-color: #418ff6;
}

.search-results-0 iron-list.search-results {
    background-color: #171717;
}

.container.style-scope.teamcity-breadcrumbs-popup {
    background-color: #171717;
}

.iron-list-0:not([grid]) #items.iron-list > * {
    background-color: #171717;
}

.tree-node-0 #anode.tree-node, .tree-node-0 #snode.tree-node {
    color: #838383;
}

.admin-ui:not(.admin-ui_old) .admin-sidebar {
    background: #000000;
}

.advancedSetting {
    background-color: #000000;
}

.headerNote {
    background: #0f1641;
}

#templateTabsContainer .tabs li.selected, #buildTypeTabsContainer .tabs li.selected {
    background: #418ff6;
}

#templateTabsContainer .tabs li:hover, #buildTypeTabsContainer .tabs li:hover {
    background: #101541;
}

.highlightable tr:hover, .highlightable tr:hover td {
    background: #0f1641 !important;
}

table.parametersTable th {
    background-color: #101541;
}

.runnerFormTable tr.groupingTitle td {
    background-color: #101441;
}

h2 {
    color: #418ff6;
}

#mainContent {
    background: #000000;
} 

#bodyWrapper {
    background-color: #000000;
}

.agentPoolNameAsGroupHeader, a.agentPoolNameAsGroupHeader:hover {
    color: #418ff6;
}

.agentsCompatibilityTable th {
    background-color: #101541;
}

.modalDialog {
    background-color: #111111;
    border-bottom-left-radius: 25px;
    border-bottom-right-radius: 25px;
}

.modalDialog .dialogHeader {
    background-color: #0e1239;
}

.popupDiv, .popupDiv#restProjectsPopup, .popupDiv#fastBreadcrumbsPopup {
    background-color: #171717 !important;
}

.simpleTabs .tabs li.selected a {
    color: #4d8af5;
}


.popup_0ad {
    background-color: #121212;
}

.BranchSelect__minimalisticButton--H6 {
    border: 1px solid #ffffff;
    background-color: transparent;
}

}
