css:
/*
* V2EX CSS THEME: LIGHTER
* BY: @jkjoke
* THANKS: @yanleijava @abelyao @zzutmebwd
* @kookxiang @DearTanker @picasso250
* @ceoimon @o00o @emdoe @Tumblr 
* UPDATE: 2019.09.26 09:00
* 推荐使用Stylish插件配合样式，样式地址：
* https://userstyles.org/styles/165971/v2ex
* https://userstyles.org/styles/165969/v2ex
*/
@-moz-document domain(v2ex.com) {
#Logo{margin-left:-1.5rem}
#Main{width:auto;margin:0 285px 0 0}
#Main td:nth-child(1) img.avatar{width:28px;height:28px;margin-top:0;margin-bottom:10px;-webkit-border-radius:50%;-moz-border-radius:50%;border-radius:50%}
#Main div[id*=r_].cell img.avatar{width:32px;height:32px}
 #Main .cell table tr td:nth-child(3),#Main .cell table tr td:nth-child(4){min-height:60px;padding-top:8px;padding-left:8px}
#Main .box:nth-child(2) .small{display:none}
#Main .box:nth-child(2) .cell:nth-child(2){padding:20px 23px!important}
#Main .box:nth-child(2) .cell:nth-child(n+2),#Main .cell{padding:5px 20px 7px 13px}
#Main div[id*=r_].cell table tr td:nth-child(1),#Main div[id*=r_].cell table tr td:nth-child(3){padding-top:10px}
#Main .cell table tr td:nth-child(1){padding-top:10px}
#Rightbar{margin-right:0!important}
#Rightbar .box .cell{border-bottom:0}
#Rightbar div:nth-child(2) .cell:first-child table:nth-child(1) tr td:nth-child(1){text-align:center}
#Rightbar div:nth-child(2) .cell:first-child table:nth-child(1) tr td:nth-child(2),#Rightbar div:nth-child(2) .cell:first-child table:nth-child(1) tr td:nth-child(3),.sep20,img[src*="flat_compose.png"]{display:none}
#Rightbar div:nth-child(2) .cell:first-child table:nth-child(1) tr td:nth-child(1) img.avatar{width:80px;height:80px;max-width:80px!important;max-height:80px!important;-webkit-border-radius:50%;-moz-border-radius:50%;border-radius:50%;margin-top:15px}
#Rightbar div:nth-child(2) .cell:first-child table:nth-child(3){padding-bottom:15px;padding-top:15px}
#TopicsHot img.avatar{border-radius:50%}
#Top{background:#ebebeb;height:100%;background-image:none;border:0}
#Top table tr td:nth-child(3){padding-top:0!important;height:50px;width:620px}
#Top table tr td:nth-child(3) a:last-of-type{margin-right:-1.1rem}
#Wrapper{background-color:#ebebeb !important;padding-top:5px;padding-bottom:50px;background-image:none !important}
#Wrapper a:hover{color:#2b89d0!important;-webkit-transition:color .25s ease-in-out,color .25s ease-in-out;-moz-transition:color .25s ease-in-out,color .25s ease-in-out;transition:color .25s ease-in-out,color .25s ease-in-out;text-decoration:none}
#Bottom{background:#333435}
#Bottom .sep20{display:block}
#Tabs a.tab,#Tabs a.tab_current{width:52px;text-align:center;line-height:36px!important;display:inline-block;padding:0!important;border-radius:0!important;border:1px solid transparent;-webkit-transition:border-color .15s ease-in-out,background-color .25s ease-in-out;-moz-transition:border-color .15s ease-in-out,background-color .25s ease-in-out;transition:border-color .15s ease-in-out,background-color .25s ease-in-out}
#Tabs a.tab_current{background-color:#f5f5f5!important;color:#000!important;line-height:36px;height:36px;border:1px solid #e1e2e3}
#Tabs a:hover{border:1px solid #ddd}
.header{padding-left:90px;position:relative;border-bottom: 1px solid #e2e2e2 !important;}
.box .header h1{margin-right:20px;margin-bottom:0!important;color: #333 !important;}
.header img.avatar{min-width:0!important;width:3rem;position:absolute;left:1.2rem;top:.9rem}
.header a[href="/"]{position:absolute;left:1.3rem}
.header .chevron{position:absolute;left:4.3rem;top:.5rem}
.mll{margin:10px 10px 10px 0;width:100%}
.topic_info{display:none}
.box{border-radius:0;box-shadow:none;border:1px solid #ddd;margin-bottom:-1px;background:#fefefe !important;border-bottom: 1px solid #ddd !important;}
.box .inner{border-radius:0}
.box .transparent{color:#666}
.cell{border-bottom:1px solid #f3f4f5 !important}
.cell table tr td:nth-child(3) a[href="/new"]{padding:7px 20px;background:#eee;border-radius:20px;margin:20px 78px 0 0;float:right;margin-bottom:20px}
.cell>table{table-layout:fixed}
.cell a.thank, .inner a.thank{background: transparent !important;background-color:rgba(0,0,0 ,0) !important;}
.fade{color:#aaa}
.inner{padding-top:10px;padding-bottom:10px}
.item_title{padding-top:10px}
.msl{width:615px}
.no{color:#bbb !important;padding:4px 5px;background-color: #f0f0f0 !important;}
.snow{color:#ccc}
.topic_content{margin-bottom:20px;padding-left: 5px;}
.topic_content, .reply_content{color: #333 !important}
.topic_buttons{background:#f3f3f3}
.super.button{background-image:none!important;opacity:.7;padding:4px 8px 4px 8px!important}
.balance_area,a.balance_area:link,a.balance_area:visited{background:#fff}
.mll, .sll, .mle{border: 1px solid #ccc !important;color: #333 !important;background-color: #fefefe !important;}
.subtle {background-color: #fafafa;border-left: 3px solid #ddd;border-bottom: 1px solid #eee;}
.subtle .topic_content{padding-left:10px;}
a.tag:link, a.tag:visited, a.tag:active{background-color:rgba(0,0,0 ,0) !important; border: 0 !important }
a:active,a:link,a:visited{color:#666e74 !important}
a.top:link,a.top:visited{color:#aaa;font-size:14px}
a.vote:link, a.vote:visited, a.vote:active, .page_normal:link, .page_normal:visited, .page_normal:active, .page_current:link, .page_current:visited, .page_current:active{border: 1px solid #e2e2e2 !important;}
a.top:hover{color:#333}
a.count_livid:link, a.count_livid:visited, a.count_livid:active{background-color: #aab0c6 !important;color: #fff !important;}
a.item_node{background:#fff;border:1px #fff solid}
a:hover{color:#5171bd;-webkit-transition:color .25s ease-in-out,color .25s ease-in-out;-moz-transition:color .25s ease-in-out,color .25s ease-in-out;transition:color .25s ease-in-out,color .25s ease-in-out;text-decoration:none}
div[id*=r_].cell table tr td:nth-child(1){vertical-align:baseline!important}
div.fr a[href*=member] img{margin-top:30px}
td[width="32"]{padding-right:20px}
img.avatar{-webkit-border-radius:50%;-moz-border-radius:50%;border-radius:50%}
}
