css:
@-moz-document domain("time.is") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("time.is") {
* {
	border: none!important;
	border: 0!important;
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

#q {
	width: 200px!important;
}

#q:focus {
	width: 600px!important;
}

/*___________________________________________________________________________________________*/

#fs-video-container,
#quote,
#slot-footer-wrapper,
.cad,
.noprint.tr.faded,
.rad {
	position: absolute!important;
	top: -9999px!important;
	left: -9999px!important;
}

/*___________________________________________________________________________________________*/

.tbx a {
	text-align: center!important;
}

/*___________________________________________________________________________________________*/

#custom_code {
	height: 142px!important;
}

/*___________________________________________________________________________________________*/

.dinfo,
.month,
.section.even,
.section.odd,
.tbx a {
	margin: 12px!important;
	padding: 6px!important;
}

.wtab input {
	margin: 2px 11px 2px 10px!important;
}

._1BN1N {
	top: -39px!important;
	left: 315px!important;
}

/*___________________________________________________________________________________________*/

#customize,
.cloud a.multizone.country,
.country,
.highlight,
.multizone.country,
div .button {
	padding: 6px!important;
}

/*___________________________________________________________________________________________*/

section {
	height: 37em!important;
}

/* ____________________________________________________________________________________________*/

section {
	top: 15em!important;
}
}

@-moz-document domain("time.is") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#favs li a .time,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

h1,
h2 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

#favs li a .time span,
[title="Click for calendar"],
time {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}

/*___ Misc _________________________________________________________________________*/

#clock0_bg time {
	color: #00cbd6!important;
	text-shadow: 6px 6px #000!important;
}
}

@-moz-document domain("time.is") {
html,
html body,
body,
selector {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___________________________________________________________________________________________*/

#overlayer {
	background: #000!important;
}

/*___________________________________________________________________________________________*/

#maptext .action_buttons a,
.CM td.headline,
.CM tr:hover,
.caln a:hover span,
.caln td.chosen a span,
.caln tr.dayheaders:hover,
.cloud .cloudfav,
.cloud .cloudhome,
.cloud .s1,
.cloud .s2,
.cloud li.on a,
.infotable tr:hover,
.inputframe,
.lg a:link,
.lg a:visited,
.ptab,
.susdiv tr,
.wb .diff_bar div,
.wb footer .hzlist li a:hover,
.wb footer nav .hzlist li.chosen a,
.wb nav li.chosen a,
.widget_examples,
div.action_buttons a:hover,
fieldset,
footer .hzlist a:hover,
legend,
nav a:hover,
nav li.chosen a,
nav li.chosen a:hover {
	background: rgba(0, 0, 0, 0)!important;
}

/*___________________________________________________________________________________________*/

.ptab tr:hover {
	border-left-color: #434343!important;
	background: -webkit-gradient(linear, left top, right top, from(#383838), to(#0000))!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*___________________________________________________________________________________________*/

.dateinput,
.infobox,
.txtin,
pre {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
}

/*__ Blue ________________________________________________________________________________________*/

div .button {
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

div .button:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

div .button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Plain _________________________________________________________________________________________*/

#compare_form .susdiv table,
#customize,
.dinfo,
.month,
.section.even,
.section.odd,
.susdiv,
.tbx a {
	background-color: #222!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
	box-shadow: 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#compare_form .susdiv table:hover,
#customize:hover,
.dinfo:hover,
.month:hover,
.section.even:hover,
.section.odd:hover,
.susdiv:hover,
.tbx a:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	box-shadow: 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Noise _________________________________________________________________________________________*/

#top_cities,
.even,
footer,
nav {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

#top_cities:hover,
.even:hover,
nav:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Noise Red _____________________________________________________________________________________*/

.bold {
	background-image: linear-gradient(to bottom, rgba(255, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

.bold,
div#language_cloud a:hover {
	background-image: linear-gradient(to bottom, rgba(255, 0, 0, .3), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Red Gradient _________________________________________________________________________________________*/

.highlight,
.susdiv .chosen,
.wb .diff_bar
{
	border-bottom: 1px solid #000;
	background: linear-gradient(to bottom, #b60000 0%, #7a0000 100%);
	font: normal 12px/14px Arial, sans-serif;
}

.diff_table tr:hover .diff_bar,
li:hover .highlight
{
	border-bottom: 1px solid #000;
	background: linear-gradient(to bottom, #00b6b6 0%, #015a5a 100%);
	font: normal 12px/14px Arial, sans-serif;
}

/*__ Shadow _________________________________________________________________________________________*/

.infobox {
	box-shadow: 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("time.is") {
li {
	filter: brightness(65%)!important;
}

/*___________________________________________________________________________________________*/

li:hover {
	filter: brightness(100%)!important;
}

/*___________________________________________________________________________________________*/

.cloud a.multizone.country,
.country,
.infobox,
.multizone.country,
.wb .diff_bar,
img {
	border-radius: 22px!important;
}

/*___________________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0px -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}
}
