css:
@-moz-document domain("winfuture.de") {
/* colors 
background dark #202123
background lighter #2f3136 
background light #3C4043
background wf light blue #D2E0ED
border darkest #202123;
text light #DFE0E0
text timestamp #999

*/


body {
    /* background: #ffffff !important; */
    background: #202123 !important;
    border-color: #3C4043 !important;
}

a {
    color: #DFE0E0 !important;
}

a.archive_link, a.archive_link:link {
    color: #999 !important;
}

.black {
    color: #999 !important;
}

.primary_content a.newslink {
    border-bottom: 1px solid white;
}

.primary_content a.newslink:hover {
    border-bottom: 1px dotted white;
    background-color: #3C4043;
    color: #DFE0E0;
}

#mainWrap, #header, #topnavi {
    background: #202123 !important;
    color: #DFE0E0 !important;
}

#mainWrap h3.bottom_topcontents, .topnews div.news210 span, .newsTicker li a {
    color: #DFE0E0 !important;
}

.newsTicker li .date {
    color: #999;
}

#news_content .spacer {
    width: 134px;
}

.news_teaser {
    width: 508px;
    padding-left:5px;
}

/* Main Content */ 

.content_wrap {
    border: 1px solid #3C4043;
    border-radius: 5px;
    clear: both;
    width: auto;
    overflow: hidden;
    line-height: 21px;
    position: relative;
    padding: 0px;
}

.primary_content .txt2 {
    padding: 0px 10px;
}

.content_wrap h1.centerTitle {
    background: #2f3136 !important;
    border-bottom: 1px solid #3C4043;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    padding: 5px 10px;
    font-size: 1em;
}

#news_content .hmedia.small_teaser_picture {
    left: 10px;
}

.centerTitle, 
.comment_write_form span, 
#news_content h2, 
.centerTitle h1, 
#header .navigation ul#breadcrumb li span {
    color: #DFE0E0 !important;
}

h3.bottom_topcontents {
    margin-top:20px;
    color: #DFE0E0 !important;
}

.downloads_teaser li, .generic_box li, .right_greenBx li, .right_orangeBx li {
    background: none;
    border-bottom: 1px solid #2f3136;
    color: #DFE0E0 !important;
}


/* News Info Box */

#rightWrap .metadata_box div.head {
    background: none;
    background-color: #2f3136 !important;
    height: 22px;
    font-size: 12px;
    font-weight: 700;
    padding: 9px 10px 0;
    margin: 0;
}

#rightWrap .metadata_box div.head::after {
    content: "Über diese News";
}

#rightWrap .metadata_box .rightBxIn ul {
    padding-top: 10px;
}

#rightWrap .metadata_box .rightBxIn ul li b {
    color: white;
}

.generic_box li {
    border: none;
}

/* News Info Box Ende */

.magazin_teaser li, .special_teaser li {
    background: none;
    border-bottom: 1px solid #2f3136;
    color: #DFE0E0;
}

.primary_content .weekday_date, .rightBx h3, .special_teaser h3, .newsList1 cite {
    color: #DFE0E0 !important;
}

.newsList1 li a {
    font-weight: 700;
}

.primary_content .weekday_date.newsTicker {
    margin: 5px 0 2px 2px;
    color: #DFE0E0 !important;
}

.rightBx .rightBxHead, .rightBx h3, .rightBxIn, .rightBx_btm, .rightBx_btm2, .special_teaser h3, #subNavigation {
    background-image: none !important;    
    color: #DFE0E0 !important;
}

.rightBx h3 {
    background-color: #2f3136 !important;
    border-radius: 5px;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}
/* 
#rightWrap .rightBx .rightBxHead, #rightWrap .rightBx h3, .rightBx_btm {
     broken
        margin-top: -10px; 
}
*/
.rightBx.generic_box .rightBxIn ul.borderless_tag_arrow li a {
    background-image: none;
    color:#3C4043 !important;
}

.rightBx, .rightBx_btm2, .special_teaser {
    background: #3C4043 !important;
    border: 1px solid #3C4043;
    border-radius: 5px;
}

.rightBx_btm {
    background: #2f3136 !important;
    border-left: 1px solid #3C4043;
    border-bottom: 1px solid #3C4043;
    border-right: 1px solid #3C4043;
    border-radius: 5px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}

.rightBx a span {
    color: #DFE0E0 !important;
}

.rightBx_btm a {
    background: url(https://i.imgur.com/eji4y3e.png) 0 -671px no-repeat;
}

.right_orangeBx dfn {
    background: url(https://i.imgur.com/eji4y3e.png) 0 -588px no-repeat;
    margin-top:4px;
}

ul.blue_teaser_tabs, ul.green_teaser_tabs, ul.orange_teaser_tabs {
    height: 22px;
}

ul.blue_teaser_tabs, ul.green_teaser_tabs, ul.orange_teaser_tabs {
    margin: -2px 0 0 1px;
    padding: 0 3px;
    border-left: none;
    border-right: none;
    background: #3C4043;   
    border-bottom: 1px solid #202123;
}

.blue_teaser_tabs li, .green_teaser_tabs li, .orange_teaser_tabs li {
    margin: 4px 4px 0 0;
    background: #3C4043;
    border: 1px solid #202123;
    border-radius: 5px;
    border-bottom-color:#202123;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    color: #e1e1e1;
}

.blue_teaser_tabs li em, .green_teaser_tabs li em, .orange_teaser_tabs li em {
    background-image: none;
}

.blue_teaser_tabs li.active, .blue_teaser_tabs li:hover, .green_teaser_tabs li.active, .green_teaser_tabs li:hover, .orange_teaser_tabs li.active, .orange_teaser_tabs li:hover {
    border-bottom-color: #3C4043;
}

/* Main Navigation */ 

#mainNavigation {
    background: #3C4043 !important;
    border-left: 1px solid #D2E0ED;
    border-bottom: 1px solid #D2E0ED;
    border-right: 1px solid #D2E0ED;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

#mainNavigation ul li a {
    text-shadow: #000 1px 1px 0;
}

#subNavigation {
    background: #2f3136 !important;
    border-left: 1px solid #3C4043;
    border-bottom: 1px solid #3C4043;
    border-right: 1px solid #3C4043;
    border-radius: 5px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}

ul.foldnavi li.first {
    background: #2f3136 !important;
    border-radius: 5px 5px 0 0;
    text-shadow: none;
    padding-top: 6px;
    height: 20px;
}

#header #topnavi #mainNavigation ul li ul.foldnavi li a {
    height: 18px;
    line-height: 18px !important;
    padding-bottom: 6px;
}

#header #topnavi #mainNavigation ul li ul.foldnavi li a:hover {
    background:#3C4043;
}

ul.foldnavi {
    border: none;
    background: #2f3136 !important;
    border-color: #3C4043 !important;
    line-height: 20px;
}

#subNavigation em.end, li.gplus {
    display: none
}

#subNavigation span {
    border-color: #3C4043;
}

#header #subNavigation ul.subnavigation li {
    border-right: 1px solid #3C4043;
}

#header .navigation ul#breadcrumb li:not(.last) a:after, #header .navigation ul#breadcrumb li:not(.last) span:after,
#header .navigation ul#breadcrumb li:not(.first) a:hover:before,
#header .navigation ul#breadcrumb li:not(.last) a:hover:after {
    content: url(https://i.imgur.com/9SykWE9.png) !important;
}

.rightBx {
    width: 298px;
}

a.archive_link {
    color: #DFE0E0 !important;
}

.pager a, .pager cite{
    background: #3C4043 !important;
    border: #3C4043 !important;
}

#header_logo_link {
    background: url(https://i.imgur.com/L2T7cOY.png);
}

.headerR {
    background: url(https://i.imgur.com/ALq3nLk.png);
}

.header_login {
    color: #202123 !important;
}

#header .header_login a {
    color: #202123 !important;
}

#header .navigation ul li a {
    line-height: 32px;
}

#header #subNavigation ul li a {
    line-height: 28px;
}

#tab_contents {
    padding-left: 5px;
}

.embedded_content_cut .more_content {
    background: #202123;
    color: #DFE0E0 !important;
}

.embedded_content_cut .gradient {
    background: linear-gradient(to bottom,#fff0 0,#202123 80%) !important;
}

span.exclusive a {
    color: #2f3136 !important;
}

.tabs {
    /* background: #F0F6FC; */
    background: #3C4043;
}

.mt10 {
    margin-top: 0px;
    padding-left: 10px;
}

.mt20 {
    margin-top: -4px !important;
    margin-left: 0px;
    margin-right: -9px;
    margin-bottom: 10px;
    padding-left: 10px;
}

.mb20 {
    padding-left: 10px;
}

.tabs .tab em{
    /* background: url(https://i.wfcdn.de/5/tabs.gif) 0 -52px #F0F6FC no-repeat;  problem im gif, hintergrund weiß */
   background: none;
}

.tabs, h3.bottom_topcontents {
    border-color: #3C4043;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}

.tabs .tab {
    background: #2f3136 !important;
    background-image: none;
    border-right: 1px solid #3C4043;
    border-left: 1px solid #3C4043;
    border-radius: 5px;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    color: #e1e1e1;
}

.bottom_topcontents {
    padding-top: 20px;    
}

/* CSS Button */

.css_button {
    display: inline-block;
    background: #3C4043;
    color: #DFE0E0;
    border-radius: 5px;
    border: 1px solid #144166;
    border-color: #DFE0E0;
    padding: 3px 20px;
    text-align: center;
    cursor: pointer;
    font-weight: 700;
}

/* Comment System */

#comments_box {
    width: calc(100% - 10px);
}

.komsys_h .css_button.green {
    margin-left: 6px;
    text-shadow: none;
    background: #88b740;
    border-color: #DFE0E0;
}

.komsys_h .css_button.red {
    margin-left: 4px;
    text-shadow: none;
    background: #c05649;
    border-color: #DFE0E0;
}

.comment_write_form {
    margin-left: 10px;
}

.comment_write_form form textarea {
    width: 644px;
}

.comment_write_form a.netiquette {
    margin-left: 314px;
    float: none;
    display: inline-block;
}

.comment_write_form .centForm12 div {
    width: 100%;
}

.comment_write_form a.css_button {
    margin-left: 10px;
    margin-right: 10px;
}

.komsys_h, .komsys_p1 .komsys_h, .komsys_p2 .komsys_h, .komsys_p3 .komsys_h, .komsys_p4 .komsys_h, .komsys_hl .komsys_h {
    background: #2f3136 !important; 
    border-top: 1px solid #3C4043 !important;
    border-left: 1px solid #3C4043 !important;
    border-right: 1px solid #3C4043 !important;
    border-radius: 5px;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    color: #DFE0E0;
    height: 22px;
    line-height: 22px;
}

.komsys_t {
    background: #3C4043;
    border: 1px solid #3C4043 !important;     
}

.komsys_h_c, .komsys_b_c {
    display: none;
}

.komsys_h .r {
    padding-top: 4px;
}

.komsys_edit {
    border: none;
}

.komsys_btn_unfold_unfolded, form.komsys_btn_r input, .komsys_btn_unfold, .komsys_btn_e {
    background-image: url(https://i.imgur.com/IFhi2f4.png);
}

.komsys_btn_unfold, .komsys_btn_unfold_unfolded  {
    height: 10px !important;
    margin-top: 4px;
}

.komsys_btn_e {
    height: 8px !important;
}

.comment_write_form .centForm12 div label {
    color: #DFE0E0;
}

#footer {
    border-top: 1px solid #3C4043 !important; /*CCDDF3 */
    border-left: 1px solid #3C4043 !important;
    border-right: 1px solid #3C4043 !important;
    border-radius: 5px;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    background: #3C4043;
    padding: 10px;
    margin-bottom: 0px;
}

#footer strong {
    color: #999;
}

#footer .search li a {
    font-weight: normal;
}

}
