css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("proxybay.app")
{
body{background-color:#444!important;
background:linear-gradient(#111, #444)!important;
margin-left: 0px;
margin-right: 0px}

div#header{color: #FFF!important}

div#header a{color:hsl(220, 78%, 40%)}

div#header img{content: url("http://image.noelshack.com/fichiers/2018/46/4/1542237724-tpb.png")}

div#navBar{background: #000!important;
border-bottom:none!important} 

div#navBar img{content:url("http://image.noelshack.com/fichiers/2018/46/4/1542237724-header-logo.png")}

ul#navLinks a{color: #FFF!important}

div#tpbnotice{background:#000!important;
border:2px solid #fff!important}

div#tpbnotice img{content:url("http://image.noelshack.com/fichiers/2018/46/4/1542280682-notice.png")}

div#moreInfo{border: 2px solid #000!important}

div.formContainer{opacity:1!important;
background:#313131a6!important}

td.report img{content: url("http://image.noelshack.com/fichiers/2018/46/4/1542238360-attention.png")}

table#proxyList td span,.hero-unit h2,.hero-unit p,table#proxyList tr a{
color:rgb(179, 174, 174)!important}

td.speed{color:#b3aeae!important}

form, input[type=text], textarea{font-family: helvetica neue,Helvetica,Arial,sans-serif!important}

table#proxyList tr a:visited{color:#088!important}

table#proxyList tr:not(.header):hover{
background-color:#444!important;
background:radial-gradient(ellipse farthest-corner at center bottom,hsl(220,99%,50%) 0%,transparent 99%),linear-gradient(180deg,#444 48%,#222 49%,#000 85%,#333)!important}

table#proxyList tr:not(.header):hover a{
color:#fff!important;
text-shadow:1px 1px #000!important}

table#proxyList tr:nth-child(2n){
background:#333!important}

table#proxyList tr:nth-child(odd) td{
background:#222!important}

table#proxyList tr{
background:#222!important;
border-top: 1px solid #000!important;
border-bottom: 1px solid #555!important}
}
