css:
@-moz-document domain("mac-torrents.com") {
div#main {
	padding-top: 0;
}
.page-title > .wf-wrap {
	display: none;
}
.page-title.title-center.breadcrumbs-off {
	min-height: 10px !important;
}
.wf-container-top {
	border: none !important;
}
div#content {
	margin-top: 20px;
}
div#phantom {
	display: none;
}
.top-bar {
	display: none;
}
section#text-2 {
	display: none;
}
section#search-2 {
	border: none;
}
section#wtpsw-post-list-widget-2 {
	border: none;
	padding: 0;
}
section#recent-posts-2 {
	border: none;
}
p {
	border-width: 0;
	box-sizing: border-box;
	clear: both;
	color: #3c3c3c;
	font-family: "myriad-pro", "Arial", "Helvetica", "Calibri", "sans-serif";
	font-size: 1.2em;
	font-style: normal;
	font-weight: 400;
	letter-spacing: 0;
	line-height: 1.618em;
	margin: 0 0 1.618em;
	padding: 0;
	text-align: left;
	text-transform: none;
	vertical-align: baseline;
}
a.download_next {
	display: none;
}
article h3 a {
	color: #52b31c;
	display: block;
	font-size: 2.5rem;
	margin-bottom: 20px;
	line-height: 60px;
}
article h3 a:before {
	background-image: url(data:image/svg+xml;utf8;base64,);
	background-size: 40px;
	content: "";
	display: inline-block;
	height: 40px;
	left: -10px;
	position: relative;
	top: 4px;
	width: 40px;
}
.masthead.inline-header.right.widgets.full-height.line-decoration.dt-parent-menu-clickable {
	border: none;
}
time.entry-date.updated {
	color: #000;
}
aside#sidebar {
	border: none;
}
header.header-bar {
	min-height: 80px !important;
}
span.separator-holder.separator-right {
	display: none;
}
li.wtpsw-post-li {
	border: none;
	opacity: 0.5;
	transition: all 0.4s ease-out;
}
li.wtpsw-post-li:hover {
	opacity: 1;
}
h6.wtpsw-post-thumb-right {
	font-size: 18px;
}
.wtpsw-post-thumb-right h6 a.wtpsw-post-title {
	font-size: 1.2rem;
}
.hr-thin {
	display: none;
}

.wf-container-main h3.entry-title a {
	font-size: 1.2rem;
	line-height: 20px;
}

.wf-container-main h3.entry-title a::before {
	display: none;
}

.wf-container-main .blog-media.wf-td {
	margin-right: 15px;
}

h3.entry-title {
	margin-bottom: 25px;
}

.blog-content.wf-td {
	top: 0;
}

.wf-container-main h3.entry-title {
	margin: 0;
}

.blog-content.wf-td {
	top: -100px;
	left: 90px;
}
p img {
	margin-top: -100px;
}
}
