css:
@-moz-document url-prefix("https://kuna.io/markets"), url-prefix("https://kuna.io/funds"), url-prefix("https://kuna.io/documents") {
body, #order_book_and_trades .view-all-wrapper .view-all {
    background-color: #161616 !important;
}
header.navbar .top-group .dropdown .dropdown-menu {
    background-color: #16191f !important;
}
div, header, header.navbar .header-nav, header.navbar .navbar-right:last-child {
    background-color: transparent !important;
    color: #87a8c6 !important;
}
footer {
    background: #18181b !important;
    color: #618bb9 !important;
}
body, div, header, footer {
    transition: background 0.3s ease !important;
}
input, a, ul, p, span, .h3 {
    color: #81a7ca !important;
}
.market li, .market a {
    text-decoration: none !important;
    border-bottom: none !important;
}
.market-menu .market ul a:hover {
    background-color: rgba(255,255,255,0.067) !important;
}
.market .active {
    background-color: rgba(255,255,255,0.067) !important;
}
.market .active:hover {
    border-left: 0 !important;
}
.scostart {
    text-align: center !important;
    z-index: 1001 !important;
    background: #ffd53b !important;
    width: 100% !important;
    color: black !important;
    font-size: 15px !important;
    letter-spacing: 1px !important;
    padding: 3px !important;
    font-family:"Roboto", sans-serif !important;
    text-decoration: none !important;
}
#my_orders .table:not(.exapnded), #order_book_and_trades .history-list:not(.exapnded), .night-mode#order_book_and_trades .history-list:not(.exapnded) {
    border-bottom: 1px solid #3fadf8 !important;
}
#order_book_and_trades .panel-body.panel-body-content .order_book_body tr td {
    border-top: 1px solid #143144 !important;
}
#headerTrandingView ul li, h1, h2, h3, h4, h5, h6 {
    color: #81a7ca !important;
}
.market-menu .market .active li {
    color:lightgrey !important;
}
#order_book_and_trades .volume, #order_book_and_trades .amount, #order_book_and_trades thead, .my-order-history thead {
    color:#81a7ca !important;
}
.searchAndFilters #searchMarkets #searchpool {
    background: #272d38 !important;
}
#my_orders .empty-row {
    color: #5a748c !important;
}
.account-balance {
    color: #6684be !important;
}
.assets, .asset, .assets_head {
    border-right: 1px solid #58b0f1 !important;
}
.market-menu li .currency {
    border-left:0px solid #00ADFF !important;
    transition: border 0.3s ease, border-radius 0.3s ease
}
.market-menu li .currency:hover {
    border-left:5px solid #00ADFF
}
.market-menu li .active:hover {
    border-left:0px solid #00ADFF
}
.trade_form_old .btn.ask {
    background: rgba(253, 42, 71, 0.11) !important;
    box-shadow: 0 0 1px #FD2A47 !important;
    border: 1px solid #FD2A47 !important;
}
.trade_form_old .btn.ask:hover {
    box-shadow: 0 0 12px #FD2A47 !important;
}
.trade_form_old .btn.bid {
    background: rgba(46, 113, 240, 0.11) !important;
    box-shadow: 0 0 3px #2E71F0 !important;
    border: 1px solid #2E71F0 !important;
}
.trade_form_old .btn.bid:hover {
    box-shadow: 0 0 12px #2E71F0 !important;
}
.trade_form_old .input-group .text-field {
    color: #bdc0d4 !important;
    border: 1px solid #1c2030 !important;
    background-color: #282e46 !important;
}
.trade_form_old .input-group .text-field::placeholder {
    color: #8f94ab !important;
}
.trade_form_old .input-group .text-field:focus {
    margin: 0 !important;
}
.trade_form_old .input-group {
    background-color: #202531 !important;
}
.nav-tabs>li.active>a {
    border: 1px solid #526892 !important;
}
.trade_form_old {
    background: #1c2030 !important;
    padding: 37px 0 29px !important;
    border-top: 1px solid #526892 !important;
    border-bottom: 1px solid #526892 !important;
}
.trade_form_old .flexbox .nav-tabs .active {
    background: #1c2030 !important;
}
#trade_form .flexbox .nav-tabs .active a {
    border-bottom: 0px !important;
}
#trade_form .flexbox .nav-tabs .nav-item {
    background: #16191f
}
#trade_form .flexbox .nav-tabs {
    border-bottom: 1px solid #1c2030 !important;
}
.asks>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: rgba(255, 0, 47, 0.09) !important;
}
.bids>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color:rgba(46, 113, 240, 0.09)
}
.asks>tbody>tr:hover>td {
    background-color: rgba(240, 46, 46, 0.09) !important;
}
#tvChartContainer {
    filter:invert(0.93) hue-rotate(40deg) saturate(200%) brightness(1.3) contrast(1.0)
}
footer .link a, footer .knowledge-base a {
    text-shadow: 0px 0px 1px #76a4ce !important;
    text-decoration: none !important;
}
.mainframe-body .col-xs-6 {
    border:1px solid #3fadf8 !important;
    transition: width 0.5s ease !important;
}
aside.sidebar ul.nav li, aside.sidebar .support {
    border-bottom: none !important;
}
aside.sidebar ul.nav li.active {
    background-color: rgba(77, 184, 255, 0.22) !important;
}
header.navbar .top-group .dropdown .dropdown-menu li:not(:last-child) {
    border-bottom: none !important;
    transition: opacity 0.5s ease !important;
}
header.navbar .top-group .profile-menu .dropdown .dropdown-menu li.signout a {
    border: 1px solid #3fadf8 !important;
    background-color: #181f23 !important;
}
.sidebar .signout a {
    border: 1px solid #3fadf8 !important;
    background-color: #181f23 !important;
}
header.navbar .top-group .dropdown .dropdown-menu li a:hover {
    background-color: rgba(63, 173, 248, 0.09) !important;
}
.deposit-history-container, .withdraw-history-container {
    border-top: 1px solid #3fadf8 !important;
}
.table>thead>tr>th, .table>thead>tr>td, .table>tbody>tr>th, .table>tbody>tr>td, .table>tfoot>tr>th, .table>tfoot>tr>td {
    border-top: 1px solid rgba(63, 173, 248, 0.34) !important;
}
.currencies-container {
    border-bottom: 1px solid #3fadf8 !important;
}
.form-control {
    color: #ffffff !important;
    background-color: #24272d !important;
    border: 1px solid rgba(63, 173, 248, 0.65) !important;
}
.btn {
    color: #d2d0d0 !important;
    background-color: rgba(63, 173, 248, 0.08) !important;
    border-color: #307eb1 !important;
}
.ngdialog.ngdialog-theme-default .ngdialog-content, .modal-dialog, .modal-body .table {
    background: #16191f !important;
}
.ngdialog-content>.table-hover>tbody>tr:hover>td, .ngdialog-content>.table-hover>tbody>tr:hover>th {
    background-color: rgba(245, 245, 245, 0.02) !important;
}
.api-token-tag {
    color: #192d33 !important;
}
.trade_form_old .input-group-addon {
    border: none !important;
}
.input-group-addon {
    border: 1px solid #307eb1 !important;
}
#documents-show blockquote {
    background: #1d2127 !important;
    color: #6f9fbd !important;
    border: 1px solid #3fadf8 !important;
}
.night-mode>body#documents-show {
    color: #b0cfe0 !important;
}
.label>span {
    color:black !important;
}
.modal-header {
    border-bottom: 1px solid #3fadf8 !important;
}
.modal-footer {
    border-top: 1px solid #3fadf8 !important;
}
.pagination a {
    background: #16191f !important;
    border-color: #3fadf8 !important;
}
label.required, #withdraw_receive, #withdraw_fees {
    color: #cecece !important;
}
.navbar-brand img {
    content:url("https://svgur.com/i/8hz.svg") !important;
    zoom: 1.8 !important;
}
*::selection {
    background: rgba(52, 151, 247, 0.4) !important;
}
header.navbar .navbar-right .profile-menu-mobile li:not(:last-child) {
    border-bottom: none !important;
}
header.navbar .navbar-right .profile-menu-mobile li.signout a {
    border: 1px solid #3193e7 !important;
}
.nav>li>a:hover, .nav>li>a:focus {
    background-color: rgba(255, 255, 255, 0.22) !important;
}
aside.sidebar ul.nav li.active {
    background-color: #193448 !important;
}
#qrcode {
	background-color: white !important;
}
}
