css:
@-moz-document domain("unifi.ubnt.com"), domain("demo.ubnt.com") {
body {
    background: #1c1e2d;
    color: #a7acb0;
}

.ubntMainPanel {
    background-color: var(--main-panel-background-color,#1c1e2d);
}

.ubntNavigation {
    background-color: var(--navigation-background-color,#242635);
}

.ubntHeaderPanel--global {
    --header-panel-background-color: var(--header-panel-global-background-color,#242635);
    --header-panel-font-color: var(--header-panel-global-font-color,#fff);
}
.modular {
    background-color: #1c1e2d;
}
.modulesDock--permanent {
    background: #1c1e2d;
}

.unifiImageMap {
    background-color: #1c1e2d;
}



.appTable--light {
    color: #a7acb0;
    background-color: #1c1e2d;
}
.appTable .thead, .appTable thead {
    border-bottom: 4px solid #454d51;
}
.appTable--striped .tbody .tr:nth-child(2n):not(.is-row-selected), .appTable--striped .tbody tr:nth-child(2n):not(.is-row-selected), .appTable--striped tbody .tr:nth-child(2n):not(.is-row-selected), .appTable--striped tbody tr:nth-child(2n):not(.is-row-selected) {

    background-color: #1a1c1f;
}



.unifiDonutChartLabel__main, .unifiDonutChartLabel__secondary {
    fill: #a7acb0;
}
.appSubHeading {
    color: #a7acb0;
}


.unifiLineChart__axis text {
    color: #a7acb0;
    font-family: sans-serif;
    font-size: 11px;

}


/*----------------*/
/*Right Side panel*/
/*----------------*/
.unifiPanelItemContent__section .panelTitle span, .unifiPanelItemContent__section .panelTitle a, .appTabs, .ubntSidePanel {
    color: #a7acb0;
}

.unifiPanel {
    background-color: #1c1e2d;
}
.unifiPanel > :not(.is-undocked) {
    border-bottom: 1px solid #1a1c1f;
}
.unifiPanelItemHeader {
    background-color: #161827;
    border-bottom: 1px solid #1a1c1f;
}
.unifiPanelItemBody {
    background-color: #1c1e2d;
}
.unifiDevicePortDiagram, .unifiPanelItemContent__section {
    border: 1px solid #1a1c1f;
    background: #161827;
}
.unifiDevicePortDiagram + .unifiDiagramKeys {
    border-top: 1px solid #1a1c1f;
}
.appTabs {
    border-bottom-color: #1a1c1f;
}




/*-----*/
/*Popup*/
/*-----*/
.appControlModal--light .appControlModal__close, .appControlModal--light .appControlModal__window, .appControlModal--light, .appControlModal--light .appControlModal__header {
    color: #a7acb0;
}
.appControlModal--light {
    background-color: #1c1e2d;
    border-color: #1a1c1f;
}
.appControlModal--light .appControlModalScrollableContent {
    border-color: #1a1c1f;
}
.ubntPopover {
    color: var(--popover-font-color,#a7acb0);
    background-color: var(--popover-background-color,#1c1e2d);
    border: 1px solid var(--popover-border-color,#1a1c1f);
}
.ubntModal {
    background: var(--modal-background-color,#1c1e2d);
    color: var(--modal-font-color,#a7acb0);
}
.ubntModal__header {
    color: var(--modal-header-font-color,#a7acb0);
}
.ubntButton--primary {
    --button-background-color: var(--button-primary-background-color,#00a0df);
    --button-font-color: var(--button-primary-font-color,#fff);
}
}
