css:
@-moz-document url-prefix("http://train.usaco.org/usacop"), url-prefix("http://train.usaco.org/usacoa") {
body {
    display: block;
    background-color: #fbf1c7;
    background-image: none;
    color: #3c3836;
    font-family: Arial, sans-serif;
    font-size: small;
    max-width: 6.25in;
    padding: 8px;
    margin: auto;
}

img {
    width: 0px;
    height: 0px;
}


td {
    font-size: small;
}

h3 {
    font-size: small;
    font-family: Arial, sans-serif;
}

p {
    display: block;
    margin-block-start: 1em;
    margin-block-end: 1em;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
    text-align: left;
}

pre,
xmp,
plaintext,
listing {
    display: block;
    font-family: monospace;
    white-space: pre;
    margin: 1em 0px;
}
}
