css:
@-moz-document domain("avenoel.org") {
.tex2jax_ignore {
    background-image: url('https://images4.alphacoders.com/748/thumb-1920-748708.jpg');
    background-repeat: no-repeat!important;
    background-attachment: fixed;
}

div,
section,
td,
tbody,
table,
ul,
th,
tr {
    background: none!important;
}

a,
h3,
h2, td {
    font-size: 11px;
    color: #fff!important;
    font-weight: 900;
    text-shadow: 2px 2px 1px red, -2px -2px 1px green, -2px 2px 1px red, 2px -2px 1px green;
    font-family: Verdana;
}

.navbar-collapse,
.navbar-fixed-top,
span {
    color: #000!important;
    text-shadow: 2px 1px 5px white, -2px -2px 5px white, -2px 2px 5px white, 2px -2px 5px white;
}
.navbar-collapse,
.navbar-fixed-top {
    background-color: #006CAE!important;
}
.board-picture {
    min-width : 80px;
    min-height : 60px;
}
.breadcrumb {
    font-size: 17px!important;
}

.topic-message.odd {
    background-color: #01304d;
}

.topic-message {
    background-color: #006CAE;
}

.message-content {
    font-size: 20px!important;
    color: white!important;
}

}
