css:
@-moz-document url-prefix("https://mynoise.net/NoiseMachines/") {
  /* Redundancy from my myNoise Declutter*/  
  #lilBoxR {
    display: none;
  }

  .menuNew {
    display: none;
  }

  .Lslide {
    display: none;
  }

  .player, .player.testimonial, .allContent { 
    width: 60vw;
  }

  /*Main*/
  div[class^="content"] {
    display: none;
  }
  div#footer {
    display: none;
  }
  .player {
    position: absolute;
    width: 716px !important;
    top: 50%;
    margin-top: -160px;
    left: 50%;
    margin-left: -360px;
  }
  .noiseTitle {
    top: 50%;
    margin-top: -165px
  }
  .LfixedImg {
    opacity: 0.15;
  }
}
