css:
@-moz-document domain("lms.nthu.edu.tw") {
body {
    background: #333333;
}

select,
div,
span,
td {
    color: #DDDDDD !important;
}
a {
    color: #7777FF !important;
}
a:hover {
    color: #9999FF !important;
}
a:visited {
    color: #CC7777 !important;
}
a:visited:hover {
    color: #FF7777 !important;
}


.mnuTop,
.mnuBody,
.mnuBottom {
    background: none;
}

.boxHeader,.mnuTitle{
    font-size: 13pt;
}
.boxHeader,
.boxBody,
.boxFooter {
    background: none !important;
}

/* change logo here */
div#logo {
    width: 0;
    background: url('https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png') no-repeat;
    /*     background: none; */
    margin-top: 10px;
    padding-left: 300px;
    height: 50px;
    background-size: contain;
}

div#logo img,#base img {
    width: 0;
    content: 'Test';
}

div#btnContainer div {
    background: #006dcc;
}

.row,
.postRow,
.listRow {
    background: #444444 !important;
}
.row2,
.postRow2,
.listRow2 {
    background: #222222 !important;
}
.rowOver,
.listRowOver,
.postRowOver {
    background: #CCCCCC;
}

.loginWrapper,
select,
.box2,
.box3,
.btn,
#d1,
td {
    background: #444444 !important;
}
.popup {
    background: #7777DD !important;
}

/* put your waifu here */
#banner {
    width: 0;
    background: url('https://i.imgur.com/ptyeyyL.jpg') no-repeat;
    /*         background: none; */
    margin-top: 10px;
    padding-left: 1000px;
    height: 275px;
    background-size: contain;
}

input, select{
    border-radius: 3px !important;
    border: none;
    background: #666666 !important;
    color: #DDDDDD !important;
}

input:hover, select:hover{
    background: #888888 !important;
}

#sysbar,
#login {
    /*     text-shadow: #FFFFFF 0px 0px 5px; */
    font-size: 14pt;
}
.BlockItem{
    border-bottom: 1px solid #555555 !important;
}

.popupbody {
    background: #333333;
    border: 0px;
}
}
