css:
@-moz-document domain("twitchls.com") {
/*New?*/
body { background-color: #141D26!important; color: #8798A5; text-decoration: none; }
.dark\:bg-gray-900, div[x-on\:click\.away="open = false"] {background: #11131a!important;}
.text-white,
.dark\:hover\:text-white:hover, .dark\:text-white,
.text-teal-100 { color: #8798A5!important; }
.dark\:bg-white { background-color: #8798A5!important; }
.bg-teal-500  { background-color: #11131a; }
input[x-ref="search"], div[x-show="open"] { background-color: #11131a; }

span[wire\:click*="loadMore("] {display: block;width: 100%;text-align: center;background: #11131a;cursor: pointer;border-radius: .5rem;padding: 5px 0;}
span[wire\:click*="loadMore("]:hover a, .text-teal-500, .hover\:text-teal-500:hover, .group:hover .group-hover\:text-teal-500 {color: #9147ff!important}
span[wire\:click*="loadMore("] > a > div, main h1 {display:none;}

.h-12 {height: 5rem;}
.absolute.bottom-0.z-10.w-0.h-2.transition-all.duration-200 {height: 0.1rem;}
.bg-teal-200, .group:hover .group-hover\:bg-teal-500 {background-color: #9147ff!important;}
path[d^="M36.3668"],
path[d^="M26.1023"],
path[d^="M44.0454"],
path[d^="M62.1393"]  {fill: #9147ff!important;}

.sm\:mb-16 {margin-bottom: .5rem;background:none;}

}

@-moz-document regexp("(?=.*?\\/embed).*(?=.*?\\/chat).*") {
.tw-root--theme-light .chat-room, .tw-c-background-base { background: #141D26!important; }
.tw-c-text-alt, .tw-c-text-base, .cMQeyU, .tw-textarea::placeholder, .tw-input::placeholder, .tw-input, .tw-textarea, .tw-link--inherit, .tw-link--inherit:visited, .tw-link--inherit[data-focus-visible-added] { color: #8798A5 !important; }
.tw-input, .tw-input:hover, .tw-select, .tw-select:hover, .tw-textarea, .tw-textarea:hover { background-color: #11131a!important; }
.tw-c-background-alt { background-color: #11131a!important; }
.tw-core-button--secondary { background-color: #11131a!important; color: #8798A5 !important; }
.tw-core-button--primary.tw-core-button--disabled { background-color: #11131a!important; color: #8798A5 !important; }

.hWwloK, .tw-root--theme-dark .clmgr-table__row, .tw-root--theme-dark .sunlight-expanded-nav-drop-down-menu-layout__scrollable-area, .tw-root--theme-dark .stream-manager--page-view .mosaic-window-body, .tw-root--theme-dark .emote-grid-section__header-title, .tw-root--theme-dark .ach-card, .tw-root--theme-dark .ach-card--expanded .ach-card__inner, .tw-root--theme-dark .room-upsell, .tw-root--theme-dark .chat-room, .tw-root--theme-dark .qa-vod-chat, .tw-root--theme-dark .video-card, html .clmgr-table__row, html .sunlight-expanded-nav-drop-down-menu-layout__scrollable-area, html .stream-manager--page-view .mosaic-window-body, html .emote-grid-section__header-title, html .ach-card, html .ach-card--expanded .ach-card__inner, html .room-upsell, html .chat-room, html .qa-vod-chat, html .video-card  { background-color: #11131a!important; }
}
