@-moz-document domain("nasioc.com") {

img[src$="buttons/reply.gif"] {
    display:none;
}

a[href^="newreply.php?do=newreply&noquote="]:after {
		content: "REPLY";
		display: inline-block;
		background: #cccccc;
		padding: 0 16px;
		border-radius: 2px;
		font-size: 13px;
		font-weight: 500;
		color: #454545;
		height: 34px;
		line-height: 34px;
		transition: .12s;
		box-shadow: #ffffff;
	}
	a[href^="newreply.php?do=newreply&noquote="]:hover:after {
		background:#eeeeee;
		box-shadow:#cccccc;
		color:#000000;
	}
	a[href^="newreply.php?do=newreply&noquote="]:focus:after {
		box-shadow: var(--bsh8);
	}
img[src$="quickreply.gif"] {
