current__artistName, .Marquee__wrapper__content, .Button--StationList--seeAll, .Nav__primaryMenu__item__link, .Nav__primaryMenu__item__link--active, .StationListItem__content {
    color: #FFF !important;
}
.Nav__primaryMenu__item__link--active {
    border-bottom-color: #777 !important;
}
.SingleToggleButton__button, .ButtonLink--cta, .ButtonLink--cta:focus, .ButtonLink--cta:hover, .ButtonLink--cta:visited, .Redeem__redeem__button, .UpgradeCard__signUp__link, .twitter, .facebook, .RadioButton, .ButtonLink--nav--upgrade--lightTheme, .ButtonLink--nav--upgrade--lightTheme:focus, .ButtonLink--nav--upgrade--lightTheme:hover, .ButtonLink--nav--upgrade--lightTheme:visited, .ButtonLink--nav--upgrade--darkTheme, .ButtonLink--nav--upgrade--darkTheme:focus, .ButtonLink--nav--upgrade--darkTheme:hover, .ButtonLink--nav--upgrade--darkTheme:visited {
    color: #FFF !important;
    background-color: #444 !important;
}
.SearchField--clear .SearchField__clear, .SearchField--placeholder .icon-search, .SearchField--placeholder .SearchField__placeholder, .SearchField--darkTheme .SearchField__placeholder {
    color: hsla(0, 0%, 40%, .8) !important;
}
.AddedHeader--scrolled, .Checkbox, .Redeem__input--text, .SearchField--inactive.SearchField--darkTheme .SearchField__input {
    color: hsla(0, 0%, 40%, .8) !important;
    background-color: hsla(0, 0%, 30%, .2) !important;
}
.MiniBar__content, .MiniBar {
    padding: 0 !important;
    border-top: 5px solid #171717 !important;
    border-bottom: 5px solid #171717 !important;
    background-color: #171717 !important;
}
.VolumeDurationControl__VolumeIconContainer:hover, .VolumeDurationControl, .TunerControl {
    fill: #444 !important;
}
.VolumeDurationControl--expanded .VolumeDurationControl__VolumeSlider, .VolumeDurationControl:hover .VolumeDurationControl__VolumeSlider {
    opacity: .3 !important;
}
.SingleToggleButton, .SimilarArtistsItem__art, .ImageLoader .ImageLoader__intrinsic, .Avatar__img, .nowPlayingTopInfo, .Nugget, .icon-down > use:nth-child(1) {
    filter: brightness(80%) !important;
    -webkit-filter: brightness(80%) !important;
}
body {
    color: #FFF !important;
    background: none !important;
    background-color: #1C1C1C !important;
}
.Search {
    transition: width .3s ease-in-out, background-color .3s ease-in-out;
    background-color: #1C1C1C !important;
}
.AutocompleteMenu__fullSearch--wrapper {
    background-color: #1C1C1C !important;
    color: #444 !important;
}
.DropdownOption--enabled {
    background-color: #1C1C1C !important;
    color: #444 !important;
}
.DropdownOption--enabled:hover {
    background-color: #444 !important;
    color: #1C1C1C !important;
}
.SideNav__list__link--active, .SideNav__list__link--active:visited {
    border-left: 2px solid #444 !important;
}
.Switch__slider {
    background-color: #444 !important;
}
.Switch__slider::before {
    background-color: #AAA !important;
}
.ShuffleButton__button__shuffleString {
    color: #999 !important;
}
.UpgradeCard, .UpgradeCard--anon {
    background-color: #222 !important;
    color: #444 !important;
}
.Redeem__button :disabled {
    background-color: rgba(0, 0, 0, .2) !important;
    color: #444 !important;
}
.Container__splash {
    background: none !important;
    background-color: #1C1C1C !important;
}
.Container__splash__logo::before {
    content:"Sheridan-Tech.com";
    position: relative;
    top: 8rem;
    left: 16rem;
    font-family: inherit;
    font-size: 3rem;
    color: #FFF;
}
.Container__splash__logo {
    opacity: 0.2 !important;
}
.MiniBar--rightRail, .region-topBar--rightRail, .region-bottomBar--rightRail, .region-main--rightRail {
    width: 100% !important;
}
.SearchModal--rightRail {
    margin: auto !important;
}
.AppBg.AppBg--showImg {
    display: none !important;
}
.SearchModal--navStyle {
    left: 35.5% !important;
    position: absolute !important;
}
.Search--rightRail.Search--active {
    -webkit-transform: translateX(0px) !important;
    transform: translateX(0px) !important;
}
.SearchField__placeholder {
    width: 36rem !important;
}
.Search--active, .Search--active.Search--rightRail, .Search {
    width: 40rem !important;
}
.SearchModal--focus {
    background-color: rgba(0, 0, 0, 0) !important;
}
.Search {
    position: absolute !important;
}
.RowItemCenterColumn__mainText, .RowItemCenterColumn, .RowItemCenterColumn__secondText, .RowItem__centerCol, .RowItem__leftCol, .RowItem__centerCol__mainText, .RowItem__columns, .RowItem__centerCol__secondText, .Switch__passwordWarning, .Switch__label, .StationDetailsListItem__secondaryText, .DescriptionBackstage__content__text, .HeaderBackstage__text__subTitle, .ContentSection__title, .ButtonT3, .GridItem__caption__, .GridItem__caption__second, .GridItem__caption__third, .Copyright__text, .Footer__copyright, .RowItem__rightCol__child {
    color: #FFF;
}
.ButtonRow__button, .NavSecondary__logo--icon, .Tuner__Controls {
    -webkit-filter: invert(100%);
}
}
