css:
@-moz-document domain("tutorialspoint.com") {
body{
    background-color: white;
}

header{
    opacity: 0.3;
    transition: all 0.25s ease-in !important;
}

header:hover{
    opacity: 1;
    transition: all 0.25s ease-in !important;
}

.main .container{
    width: 90%;
}

.main .content h2,
.main .content h3,
.main .content h4{
    text-align: center;
}
.main .content h2{
    margin-top: .9em;
}

.main .container .middle-col{
    width: 70%;
    padding: 20px;
    border: none;
}

.main .container #rightbar{
    display: none !important;
}

.main .container .col-md-2{
    width: 20%;
}

.main .container .sidebar{
    
}

.main .container .sidebar .mini-logo{
    display: none !important;
}

div.footer-copyright{
    display: none !important;
}
}
