css:
@-moz-document domain("wikipedia.org"), domain("wikimedia.org"), domain("wiktionary.org") {
/*
ADDITIONS
*/

/*
BODY
*/
body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    background: #fff !important;
}
a {
    color: #0088cc !important;
    text-decoration: none;
}
a:hover {
    color: #005580 !important;
    text-decoration: underline !important;
}
#content h1 {
    border: 0px !important;
    font-family: 'Arial' !important;
    font-weight: 700 !important;
    font-size: 36px !important;
    margin: 0px !important;
}
h2 {
    border: 0px !important;
    font-family: 'Arial' !important;
    font-weight: 400 !important;
    font-size: 26px !important;
}
h3 {
    border: 0px !important;
    font-family: 'Arial' !important;
    font-weight: 400 !important;
    font-size: 16px !important;
}
#content h3 {
    font-size: 20px !important;
}
p {
    font-weight: 300 !important;
    line-height: 1.9 !important;
}
table,
td,
th {
    border: 0px !important;
    border-collapse: collapse !important;
    padding: 5px !important;
}
li span {
    background: transparent;
    border: 0px !important;
    font-size: 14px !important;
}
.referencetooltip {
    background-color: #EEE !important;
}
dl {
    font-weight: 300 !important;
}
dt {
    font-style: italic;
    font-size: 16px !important;
    margin-top: 10px !important;
}
dd {
    font-size: 15px !important;
}
code {
    padding: 3px !important;
    background: transparent !important;
    color: #39892f !important;
}
tt {
    font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Courier New' !important;
    font-size: 16px !important;
}
.new,
.new:link,
.new:visited {
    color: #cd5b45 !important;
}
blockquote {
    border: 0px !important;
    font-style: italic !important;
    font-size: 16px !important;
    margin: 35px 75px !important;
    line-height: 1.5 !important;
    font-weight: 200 !important;
    background: transparent !important;
}
select {
    padding: 0px 10px;
}
.mw-ui-button,
.mw-ui-progressive {
    border-radius: 4px !important;
    border: 1px solid #5691ffed !important;
    color: #5691ffed !important;
    text-shadow: 0px 0px 0px;
}
.mw-ui-button.mw-ui-progressive:hover,
.mw-ui-button.mw-ui-progressive:focus,
.mw-ui-button.mw-ui-progressive:active,
.mw-ui-button:hover,
.mw-ui-progressive:hover,
.mw-ui-button:focus,
.mw-ui-progressive:focus {
    box-shadow: 0px 0px 0px !important;
    text-shadow: 0px 0px 0px !important;
    border: 1px solid #347Bff !important;
}
#wpSave,
#wpPreview,
#wpDiff {
    border-radius: 4px !important;
    border: 1px solid #347Bff !important;
    color: #347Bff !important;
    background: #fff !important;
    text-shadow: 0px 0px 0px;
    padding: 0.5em 1em;
    cursor: pointer;
}
#wpSave:hover,
#wpPreview:hover,
#wpDiff:hover {
    color: rgba(52, 123, 255, 0.5) !important;
    ;
}
#content {
    margin: 0 auto !important;
    margin-left: 2.5em !important;
    padding-top: 0em;
    border: none !important;
}
.image img:hover {
    opacity: 0.85 !important;
}
.globegris {
    background: transparent !important;
}
.mw-editsection-bracket,
.references-small b,
.mw-cite-backlink,
.plainlinksneverexpand {
    display: none !important;
}
#floating_object {
    display: none;
}
.navbox,
.nowraplinks {
    margin: 10px 0px !important;
    border-collapse: collapse !important;
}
.navbox td,
.navbox th,
.nowraplinks td,
.nowraplinks th {
    padding: 9px 6px !important;
    font-size: 14px !important;
    font-weight: 300 !important;
    border: 1px solid #fff !important;
}
.NavFrame {
    border: 0px !important;
    background: transparent !important;
    padding: 0px !important;
}
.NavHead {
    margin: 10px 0px !important;
    padding: 12px !important;
    font-size: 16px !important;
    font-weight: 700 !important;
    background: transparent !important;
}
.portal {
    background: transparent !important;
}
.wikitable {
    font-weight: 300 !important;
}
.wikitable td,
.wikitable th {
    border: 1px dashed rgba(0, 0, 0, 0.07) !important;
}
pre {
    line-height: 1.3 !important;
    font-family: "Courier New", Lucida Console, sans-serif !important;
    border: 0px !important;
    background: 0px !important;
    font-size: 16px !important;
}
.mw-code {
    border: 0px !important;
    padding: 17px !important;
    background: 0px !important;
    font-size: 16px !important;
}
.toc {
    background: transparent !important;
    border-style: dashed !important;
    display: block !important;
    max-width: 300px !important;
}
.toctoggle {
    display: none !important;
}
.thumbinner,
.thumb,
.toccolours,
.graytable {
    background: transparent !important;
    border: 0px !important;
    font-size: 14px !important;
}
.thumbcaption {
    font-style: italic !important;
    margin: 7px 0px !important;
}
.thumbimage:hover {
    opacity: 0.85 !important;
}
.dablink {
    margin: 14px 0px !important;
    font-weight: 300 !important;
    font-size: 18px !important;
}
#siteSub,
.flaggedrevs_preview {
    display: none !important;
}
.ambox,
.tmbox,
.Note,
.informationbox {
    margin: 14px 0px !important;
    font-size: 16px !important;
    line-height: 1.5 !important;
    border: 1px dashed #aaa !important;
/*     max-width: 700px !important; */
    min-width: 400px !important;
    font-weight: 300 !important;
}
.ambox td,
.tmbox td {
    padding: 10px !important;
}
.ambox-text-small,
.mbox-text,
.Note td {
    font-size: 14px !important;
    font-weight: 300 !important;
    color: #000 !important;
}
.ambox-image,
.mbox-image {
    display: none;
}
#request_for_deletion,
.ambox-serious {
    background: rgba(255, 0, 0, 0.1) !important;
    border: 1px dashed rgba(255, 0, 0, 0.15) !important;
    color: #a00000 !important;
}
.tmbox-notice,
.tmbox-move,
.Note {
    background: rgba(150, 75, 0, 0.1) !important;
    border: 1px dashed rgba(150, 75, 0, 0.15) !important;
}
.informationbox {
    background: #fff !important;
    padding: 10px !important;
    font-size: 14px !important;
}
.editOptions {
    background: #fff !important;
    border: 0px !important;
}
.catlinks {
    background: transparent !important;
    border: 1px dashed #aaa !important;
    font-size: 16px !important;
}
.infobox,
.tright,
.toccolours {
    border: 0px !important;
    background: transparent !important;
    border-collapse: collapse !important;
}
.infobox td,
.infobox th,
.tright td,
.tright th,
.toccolours td,
.toccolours th {
    padding: 5px !important;
    background: transparent !important;
    font-weight: 400 !important;
    border: 1px dashed rgba(0, 0, 0, 0.07) !important;
}
#bodyContent #siteSub,
#bodyContent #mw-fr-revisiontag,
#bodyContent #tfd.boilerplate {
    display: none !important;
}
#bodyContent #coordinates {
    padding: 0px 6px 3px 0px !important;
    border: 0px !important;
}
html[lang="de"] #bodyContent #coordinates {
    top: 53px !important;
    right: 32px !important;
}
#bodyContent #protected-icon,
#bodyContent #good-star,
#bodyContent #spoken-icon,
#bodyContent #featured-star {
    top: -45px !important;
}
p#wikifybarrr {
    margin-bottom: 20px !important;
}
#bodyContent #contentSub {
    margin: 0px !important;
/*     padding: 0 !important; */
}
#bodyContent .references {
    color: #111 !important;
    font-size: 10px !important;
}
.mediaContainer {
    z-index: 0;
}
span#coordinates.metadata.coord-missing {
    display: none !important;
}
#coordinates {
    margin: -20px 0 !important;
}




/*
HEAD
*/
#mw-navigation {
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
    right: 0 !important;
    background: white !important;
}
#mw-head #p-cactions,
#mw-head #ca-watch {
    display: none !important;
}
#mw-head-base{
    height: 3em;
}
#mw-head {
    position: relative !important;
    height: 3em !important;
    margin: 0 0 0 3em !important;
    width: auto !important;
    font-size: 1.1em !important;
}
#mw-head {
    opacity: 0.1 !important;
    transition: all 0.45s ease-out !important;
}
#mw-head:hover {
    opacity: 1 !important;
    transition: all 0.2s ease-in !important;
}
#mw-head a,
#mw-head #p-views ul,
#mw-head #p-namespaces ul,
#mw-head li,
#mw-head span,
#mw-head .vectorTabs,
#mw-head .vectorMenu,
#mw-head #p-variants-label {
    background: none !important;
    border: none !important;
}
#mw-head .vectorTabs ul{
    background: none !important;
}
#mw-head .vectorTabs span a{
    padding-top: 0.5em !important;
}
#mw-head ul{
    padding: 0em;
}
#mw-head #left-navigation {
    display: none;
}
#mw-head #right-navigation {
    float: left;
    margin-top: 0;
}
#mw-head #right-navigation .vector-menu-tabs li a {
    padding-top: 1em !important;
}
#mw-head #p-personal {
    display: inline-block;
}
#mw-head #p-personal #pt-anonuserpage{
    padding-left: 0.615em !important;
    margin-left: 0px !important;
}
#mw-head a,
#mw-head a:visited{
    color: #556 !important;
}
#simpleSearch {
    background: transparent !important;
    border-color: #ccc !important;
    max-width: 40em !important;
    width: 35vw !important;
}
#searchInput {
    padding-left: 10px !important;
}
.vectorMenuCheckbox{
    height: 0px !important;
    width: 0px !important;
}
.vectorMenu h3 span{
    padding-top: 0.5em !important;
}
.vectorMenu h3 span:after{
    top: 0.5em !important;
}
.vectorMenu .menu{
    top: 1.5em !important;
}
.mw-ui-input {
    border: 1px solid #347Bff !important;
    color: #000 !important;
    background: #fff !important;
    font-size: 13px !important;
}
.mw-ui-input:focus {
    box-shadow: 0px 0px 0px !important;
}
.mw-content-ltr pre {
    font-family: "Courier New", Lucida Console, sans-serif !important;
    color: #000d26 !important;
}
.suggestions {
    font-weight: 200 !important;
    font-size: 12px !important;
    max-width: 300px !important;
    margin-top: 15px !important;
    border: 0px !important;
    box-shadow: 2px 2px 10px 2px rgba(0, 0, 0, 0.5);
}
.suggestions-results {
    border: 0px !important;
}
.suggestions-result {
    padding: 5px !important;
}
.imeselector,
#searchEngines {
    display: none !important;
}
.mw-search-formheader {
    border: 0px !important;
    background: transparent !important;
}
.central-featured-logo {
    background-image: url('https://i.imgur.com/nRDTbeb.png') !important;
}
.mw-search-result-heading,
.searchresult,
.searchmatch {
    font-size: 12px !important;
    font-weight: 300 !important;
    padding: 3px 0px !important;
}
.searchmatch {
    font-weight: 400 !important;
}
.mw-search-result-data,
.mw-search-createlink {
    display: none;
}


/* 
left nav 
*/
#mw-navigation #mw-panel {
    position: fixed !important;
    z-index: 999 !important;
    font-size: 18px !important;
    top: 0px !important;
    left: -255px !important;
    width: 200px !important;
    height: 100% !important;
    overflow-y: auto !important;
    padding: 0 0 0 100px !important;
    background: #f9f9f9f2 !important;
    box-shadow: 1px 0 3px rgba(0, 0, 0, 0.25) !important;
    transition: all 0.4s ease-in-out !important;
    opacity: 0.2 !important;
}

#mw-navigation #mw-panel:hover {
    left: -100px !important;
    transition: all 0.15s ease-in-out !important;
    opacity: 1 !important;
}
#mw-navigation #p-navigation,
#mw-navigation .first {
    margin-bottom: 0 !important;
    margin-top: 2em !important;
}

#mw-navigation #p-lang:before {
    content: "" !important;
    font-size: 0 !important;
    display: inline-block !important;
    height: 0px !important;
    width: 100% !important;
    border-top: 1px solid rgba(0, 0, 0, 0.2) !important;
    border-bottom: 1px solid rgba(255, 255, 255, 0.75) !important;
}

#mw-navigation #p-lang {
    margin-top: -15px !important;
}

.uls-settings-trigger {
    display: none !important;
}

#mw-navigation > #mw-panel > div:not(#p-lang):not(#p-navigation):not(.first),
#mw-navigation #p-lang .wbc-editpage {
    display: none !important;
}



/*
FOOTER
*/
.Boxmerge {
    border: 0px !important;
}
#coordinates {
    display: none !important;
}
#mw-fr-revisiontag,
#mw-fr-reviewnotice {
    display: none !important;
}
#footer {
    left: 0 !important;
    right: 0 !important;
    margin: 0 !important;
    margin-bottom: -22px !important;
    padding: 15px 0 !important;
    background-color: #f1f1f1 !important;
}
#footer-info {
    max-width: 860px !important;
    margin: 0 auto !important;
    font-size: 18px !important;
}
#footer li:not(#footer-info-lastmod) {
    display: none !important;
}
}
