css:
@-moz-document url-prefix("https://ets2c.com") {
/**
  * ETS2C Dark Theme
  * Created by Digital (https://forum.truckersmp.com/index.php?/profile/4963-digital)
  */
#body, body {
    background: #252525;
}
#login {
    background: #222222;
}
.left {
    color: #888888;
}
a {
    color: #72C02C;
}
a:hover {
    color: #72C02C;
    text-decoration: underline;
}
.content h2, .form .row label, .form .row>div.desc, header #logo {
    color: #CCCCCC;
}
.prof_list img {
    border: 2px solid #72C02C;
}
#footer {
    background: #1C1C1C;
}
#footer .title {
    color: #EEEEEE;
}
#footer a {
    color: #999999;
}
.form textarea {
    background: #202020;
    border: 1px dashed #303030;
    color: #888888;
}
#chat .messagelist {
    background: #222222;
    color: #888888;
}
#chat .submitmessage {
    background: #222222;
    color: #888888;
}
#chat .submitmessage input[type=text], #chat .chatcontainer {
    background: #252525;
    border-color: #303030;
}
#chat .submitmessage input[type=text]:focus, #chat .chatcontainer {
    border-color: #555555;
}
#chat {
    border-color: #333333;
}
#chat .chattitle div {
    background-color: #72C02C;
}
img, input, select, textarea {
    color: #888888;
}
.button {
    background-color: #5CB85C;
    border-color: #4CAE4C;
    border-radius: 0px;
    opacity: 1;
    text-shadow: none;
}
.button:hover {
    background-color: #449d44;
    border-color: #398439;
    border-radius: 0px;
    opacity: 1;
    text-shadow: none;
}
.button.yes {
    background-color: #5CB85C;
    border-color: #4CAE4C;
    border-radius: 0px;
    opacity: 1;
}
.button.yes:hover {
    background-color: #449d44;
    border-color: #398439;
    border-radius: 0px;
    opacity: 1;
}
.button.maybe {
    background-color: #F0AD4E;
    border-color: #EEA236;
    border-radius: 0px;
    opacity: 1;
}
.button.maybe:hover {
    background-color: #EC971F;
    border-color: #D58512;
    border-radius: 0px;
    opacity: 1;
}
.table_list .row:not(:last-child)>div {
    background-color: #222222;
    border-color: #333333;
    color: #888888;
}
.table_list .row.title, .table_list .row.title>div{
    background: #1C1C1C;
}
.table_list {
    border-color: #333333;
}
.table_list .row:not(.title):hover>div {
    background: #292929;
}
img, input, select, textarea {
    background-color: #222222;
    border-width: 1px;
    border-style: solid;
    border-color: #333333;
}
input:focus, select:focus, textarea:focus {
    border-color: #555555;
}
input:disabled {
    background-color: rgba(128, 128, 128, 0.1);
}
}
