css:
@-moz-document url-prefix("https://courses.thinkful.com") {
body {
    background-color: #101119;
    color: #DDD;
}

.node-name {
    color: #FFF;
}

.main-flow code {
    background-color: #000;
    box-shadow: 0 0 0 2px #000;
    color: #6799FF;
}

a {
    color: #6799FF;
}

a:hover {
    color: #6799FF;
}

a.return-to-content-button,
a.course-prev-button,
a.course-next-button {
    color: #679FFF;
}

a.return-to-content-button:hover,
a.course-prev-button:hover,
a.course-next-button:hover {
    color: #4970b3;
}



/*Code Window*/
.main-flow pre,
.main-flow [data-course-node='codesnippet'] {
    background-color: #010104;
    color: #DDD;
    border: 1px solid black;
}

.hljs-keyword,
.css .rule .hljs-keyword,
.hljs-winutils,
.nginx .hljs-title,
.hljs-subst,
.hljs-request,
.hljs-status {
    color: #2766e3;
}

.hljs-symbol,
.ruby .hljs-symbol .hljs-string,
.lisp .hljs-keyword,
.clojure .hljs-keyword,
.scheme .hljs-keyword,
.tex .hljs-special,
.hljs-prompt {
    color: #ff8000;
}

.hljs-title,
.hljs-id,
.scss .hljs-preprocessor {
    color: #da0101;
}

.hljs-number,
.hljs-hexcolor,
.ruby .hljs-constant {
    color: #01a6a6;
}

.hljs-built_in {
    color: #c001c9;
}

.hljs-tag,
.hljs-tag .hljs-title,
.hljs-rule .hljs-property,
.django .hljs-tag .hljs-keyword {
    color: #0101d5;
}

.hljs-class .hljs-title,
.hljs-type,
.vhdl .hljs-literal,
.tex .hljs-command {
    color: #5dbd82;
}
}
