css:
@-moz-document domain("wiimmfi.de") {
body {
    background-color: #161616;
}
.text, .text-wd, .text2, .text-info, .contents, table.ask {
    background-color: #202020;
    color: #cccccc;
    border: solid 1px #000000;
    box-shadow: 5px 5px 10px #0f0f10;
    -moz-box-shadow: 5px 5px 10px #0f0f10;
    -webkit-box-shadow: 5px 5px 10px #0f0f10;
    -khtml-box-shadow: 5px 5px 10px #0f0f10;
}
small, div.subhead, label, b, table.ask caption, p, h1.narrow {
    color: #cccccc;
}
span.tr3 {
    background-color: #c7620a;
}
span.tr2 {
    background-color: #b163b1;
}
.important {
    color: #bd0707;
}
.text-warn {
    color: #000000;
    border: solid 1px #a00000;
    box-shadow: 5px 5px 10px #0f0f10;
    -moz-box-shadow: 5px 5px 10px #0f0f10;
    -webkit-box-shadow: 5px 5px 10px #0f0f10;
    -khtml-box-shadow: 5px 5px 10px #0f0f10;
}
.text.orange {
    color: #000000;
    box-shadow: 5px 5px 10px #0f0f10;
    -moz-box-shadow: 5px 5px 10px #0f0f10;
    -webkit-box-shadow: 5px 5px 10px #0f0f10;
    -khtml-box-shadow: 5px 5px 10px #0f0f10;
}
h1, h2, h3, h4, h5, h6, a, dt.changelog, .opt-checked {
    color: #00c044;
}
td:focus, span:focus, a:focus {
    background-color: #161616;
}
td {
    color: #000000;
}
span.nb-frame.nb-classic:after {
    content: ' | Dark Wiimmfi v1.0.5 by CodyMKW' !important;
    color: cyan;
}
}
