css:
@-moz-document domain("dcinside.com") {

/* background-color(green) */
/* main site */
.top_search,
.word_close,
.gnb_bar,
.depth2,
.btn_inout,
.gall .rank_num,
.dcheader.bg,
/* gall main */
.area_links .btn_top_loginout,
.pop_wrap .btn_apply,
.btn_blue_round,
/* gall */
.array_tab button.on,
.btn_boardlist.on,
.btn_albumlist .inner,
.btn_blue,
.bottom_array,
.bottom_array .select_area .inner,
.bottom_search,
.btn_lightblue,
/* manage */
.minor_uadmin_menu li a.on,
/* dccon */
.dccon_menu li.on a,
.bluegray .btn_tab.on {
  background-color: #3ec729 !important;
}

/* color(green) */
/* main site */
.word_tit,
/* gall main */
.user_info,
.pop_wrap.type3 .pop_head,
/* gall */
.page_head h2 a,
.btn_white,
/* dccon */
.bluegray .btn_tab,
.font_blue {
  color: #3ec729 !important;
}

/* color(black) */
/* main site */
.user_name,
.rank_gall,
.head_tit,
.head_tit a,
.tab_comm li a.on,
.tab_comm li a:hover,
.tab_comm li a.on:hover,
.dc_all .tit,
/* gall main */
.visit_history .tit,
.cate_tit,
/* gall */
.bottom_array .select_area,
.bottom_array .option_box li,
/* dccon */
.gnb_depth2_list li a,
.top_head h2.tit,
.dccon_menu li a {
  color: #000 !important;
}

/* border(1 solid green) */
/* main site */
.top_search,
.gnb_bar,
.login_wrap,
.btn_inout,
.gall .rank_num,
/* gall main */
.area_links .btn_top_loginout,
.login_box,
.pop_wrap.type3,
.pop_wrap.file,
.pop_wrap .btn_apply,
.btn_blue_round,
.mgall_list_wrap,
/* gall */
.array_tab button.on,
.bottom_array,
.bottom_search,
/* manage */
.minor_uadmin_menu li a.on,
/* dccon */
.dccon_menu li.on a,
.bluegray .btn_tab.on,
.bluegray .btn_tab {
  border: 1px solid #3ec729 !important;
}


/* main site */
.sp_main {
  background-image: url(https://questcoinn.github.io/images/sp_main.png) !important;
}

.auto_wordwrap {
  border: 2px solid #3ec729 !important;
}

.btn_inout {
  text-shadow: 0px 1px #3ec729 !important;
}

.con_head {
  border-bottom: 2px solid #3ec729 !important;
}

.dc_all {
  border-top: 2px solid #3ec729 !important;
  border-bottom: 1px solid #3ec729 !important;
}

.login_wrap .login_page {
  border: 2px #3ec729 solid !important;
}

/* gall main */
.sp_img {
  background-image: url(https://questcoinn.github.io/images/sp_img.png) !important;
}

.area_links .btn_top_loginout {
  text-shadow: 0px -1px #3ec729 !important;
}

.pop_wrap .btn_apply {
  text-shadow: 0px -1px #3ec729 !important;
}

.cont_head {
  border-bottom: 2px solid #3ec729 !important;
}

.btn_blue_round {
  text-shadow: 0px -1px #3ec729 !important;
}

.cate_wrap {
  border-top: 3px solid #3ec729 !important;
}

.cate_box {
  border-top-color: #3ec729 !important;
}

/* gall */
.page_head h2 {
  font-size: 26px;
}

.issue_wrap {
  border-top: 2px solid #c1c1c1 !important;
}

.array_tab button.on {
  text-shadow: 0px -1px #3ec729 !important;
}

.btn_boardlist {
  border: 1px solid #fff !important;
}

.btn_albumlist {
  border-color: #fff !important;
}

.gall_list {
  border-bottom: 1px solid #3ec729 !important;
}

th {
  border-color: #3ec729 !important;
}

.btn_blue {
  border-color: #3ec729 !important;
  text-shadow: 0px -1px #3ec729 !important;
}

.btn_white {
  border-color: #3ec729 !important;
}

.bottom_array .select_area {
  font-weight: bold !important;
}

.bottom_array .option_box {
  border: 2px solid #3ec729 !important;
}

.comment_box {
  border-top: 2px solid #3ec729 !important;
}

.cmt_write_box {
  border-top: 2px solid #3ec729 !important;
}

.btn_lightblue {
  border-color: #31ea14 !important;
  text-shadow: 0px -1px #3ec729 !important;
}

.view_comment .cmt_write_box {
  border-bottom: 2px solid #3ec729 !important;
}

.editor_wrap {
  border-top: 2px solid #3ec729 !important;
}

.stickyunit {
  max-height: 0 !important;
  max-width: 0 !important;
  display: none !important;
}

.pop_wrap {
  border: 2px solid #3ec729 !important;
}

/* manage */
.minor_uadmin_wrap {
  border-top: 2px #3ec729 solid !important;
  border-bottom: 2px #3ec729 solid !important;
}

.minor_uadmin_menu li a.on {
  text-shadow: 0px -1px #3ec729 !important;
}

/* dccon */
.top_head {
  border-bottom: 2px #3ec729 solid !important;
}

.mandu_mall .info_policy {
  border-top: 1px #3ec729 solid !important;
}

.dccon_menu li.on a {
  text-shadow: 0px 1px #3ec729 !important;
  color: #fff !important;
}

.bluegray .btn_tab.on {
  color: #fff !important;
  text-shadow: 0px 1px #3ec729 !important;
}

.dccon_wrap .info_policy {
  border-top: 1px #3ec729 solid !important;
}

/* delete */
.con_box.bluebrd {
  border: 2px #3ec729 solid !important;
}

.noacs_img {
  background-image: url(https://questcoinn.github.io/images/sp_img.png) !important;
}

footer {
  border-top: 1px #3ec729 solid !important;
}

