css:
@-moz-document domain("code.visualstudio.com") {
body {
	color: white;
	background-color: #313131;
}

#docs-navbar > .nav > .active > a,
#docs-navbar > .nav > .active .active > a {
	color: #8e66ff !important;
}

#docs-navbar > .nav > .active > a:hover,
#docs-navbar > .nav > .active .active > a:hover {
	border: none !important;
}

#docs-navbar .nav li a {
	border: none;
}

#docs-navbar .nav li a,
#docs-subnavbar .nav li a {
	color: #989898;
}

a {
	color: #30aafc;
}

#docs-navbar .nav li a:hover,
#docs-subnavbar .nav li a:hover {
	background: none;
	color: #e6e6e6;
	text-decoration: none !important;
}

hr {
	border-top: 1px solid #525252;
}

a:hover {
	color: #79d8ff;
	text-decoration: none;
}
code {
	color: #fb809f;
	background-color: #252525;
}

.docs h2 {
	margin-top: 40px;
	margin-bottom: 20px;
}

h3 {
	margin-bottom: 15px;
	margin-top: 30px;
}

pre {
	display: block;
	padding: 9.5px;
	margin: 0 0 10px;
	font-size: 13px;
	line-height: 1.42857143;
	color: #e0e0e0;
	word-break: break-all;
	word-wrap: break-word;
	background-color: #252525;
	border: none;
	border-radius: 4px;
}

.hljs {
	display: block;
	overflow-x: auto;
	padding: 0.5em;
	background: #252525;
	-webkit-text-size-adjust: none
}

.hljs,
.hljs-subst,
.hljs-tag .hljs-title,
.nginx .hljs-title {
	color: #efefef
}

.hljs-string,
.hljs-title,
.hljs-constant,
.hljs-parent,
.hljs-tag .hljs-value,
.hljs-rule .hljs-value,
.hljs-preprocessor,
.hljs-pragma,
.hljs-name,
.haml .hljs-symbol,
.ruby .hljs-symbol,
.ruby .hljs-symbol .hljs-string,
.hljs-template_tag,
.django .hljs-variable,
.smalltalk .hljs-class,
.hljs-addition,
.hljs-flow,
.hljs-stream,
.bash .hljs-variable,
.pf .hljs-variable,
.apache .hljs-tag,
.apache .hljs-cbracket,
.tex .hljs-command,
.tex .hljs-special,
.erlang_repl .hljs-function_or_atom,
.asciidoc .hljs-header,
.markdown .hljs-header,
.coffeescript .hljs-attribute {
	color: #ff6b91
}

.smartquote,
.hljs-comment,
.hljs-annotation,
.diff .hljs-header,
.hljs-chunk,
.asciidoc .hljs-blockquote,
.markdown .hljs-blockquote {
	color: #79ce79
}

.hljs-number,
.hljs-date,
.hljs-regexp,
.hljs-hexcolor,
.smalltalk .hljs-symbol,
.smalltalk .hljs-char,
.go .hljs-constant,
.hljs-change,
.lasso .hljs-variable,
.makefile .hljs-variable,
.asciidoc .hljs-bullet,
.markdown .hljs-bullet,
.asciidoc .hljs-link_url,
.markdown .hljs-link_url {
	color: #67bb67
}

.hljs-label,
.hljs-javadoc,
.ruby .hljs-string,
.hljs-decorator,
.hljs-filter .hljs-argument,
.hljs-localvars,
.hljs-array,
.hljs-attr_selector,
.hljs-important,
.hljs-pseudo,
.hljs-pi,
.haml .hljs-bullet,
.hljs-doctype,
.hljs-deletion,
.hljs-envvar,
.hljs-shebang,
.hljs-literal,
.apache .hljs-sqbracket,
.nginx .hljs-built_in,
.tex .hljs-formula,
.erlang_repl .hljs-reserved,
.hljs-prompt,
.asciidoc .hljs-link_label,
.markdown .hljs-link_label,
.vhdl .hljs-attribute,
.clojure .hljs-attribute,
.asciidoc .hljs-attribute,
.lasso .hljs-attribute,
.coffeescript .hljs-property,
.hljs-phony {
	color: #9797ff
}

.hljs-keyword,
.hljs-id,
.hljs-title,
.hljs-built_in,
.css .hljs-tag,
.hljs-javadoctag,
.hljs-phpdoc,
.hljs-dartdoc,
.hljs-yardoctag,
.smalltalk .hljs-class,
.hljs-winutils,
.bash .hljs-variable,
.pf .hljs-variable,
.apache .hljs-tag,
.hljs-type,
.hljs-typename,
.tex .hljs-command,
.asciidoc .hljs-strong,
.markdown .hljs-strong,
.hljs-request,
.hljs-status {
	color: #89d1ff;
}

.asciidoc .hljs-emphasis,
.markdown .hljs-emphasis {
	font-style: italic
}

.nginx .hljs-built_in {
	font-weight: normal
}

.coffeescript .javascript,
.javascript .xml,
.lasso .markup,
.tex .hljs-formula,
.xml .javascript,
.xml .vbscript,
.xml .css,
.xml .hljs-cdata {
	opacity: 0.5
}

.json .hljs-attribute {
	color: #4fa3ff
}

.json .hljs-string {
	color: #fa809f
}
footer {
	background-color: #171717;
}
a.value-prop {
	color: #c3c3c3 !important;
}

.tweet-body {
	color: #2f2f2f;
}

.glyphicon-chevron-left:before,
.glyphicon-chevron-right:before {
	color: black;
}

div#powerful-debugging p,
div#powerful-debugging h2 {
	color: black;
}

div#hundreds-of-extensions p,
div#hundreds-of-extensions h2 {
	color: black;
}

.home .getting-started .mask {
	background: linear-gradient(to bottom, #313131, rgba(255, 255, 255, 0), rgba(255, 255, 255, 0), #313131);
	background-image: linear-gradient(rgb(49, 49, 49), rgba(255, 255, 255, 0), rgba(255, 255, 255, 0), rgb(49, 49, 49));
}


.download td strong {
	color: black;
}
.docs .keybinding {
	color: #4eb0fb;
	background-color: #252525;
}
.table-striped > tbody > tr:nth-of-type(odd) {
	background-color: #272727;
}

.table > tbody > tr > td,
.table > tbody > tr > th,
.table > tfoot > tr > td,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > thead > tr > th {
	border: none;
}

#docs-navbar > .nav .active .active a:before {
	display: none;
}


ul a:hover {
	background-color: #232323 !important;
}

ul a {
	color: white !important;
}
ul a .info .description {
	color: white !important;
}

ul a .info::before {
	color: white !important;
}

ul.video-list .video:not(:last-of-type) a {
	border: none !important;
}
.community ul.community-list .community-content:not(:last-of-type) a {
	border: none;
}

blockquote {
	margin: 30px 0;
}
.docs p span.ts:hover {
	color: inherit;
	background-color: #8b82fd;
}
.docs p span.ts[aria-expanded='true']:hover {
	background-color: #8b82fd;
}
}
