html {background: var(--theme_default_bg);}
html, body {font-family: 'Russo One', sans-serif !important;}
body {background-color: var(--theme_default_bg); color: var(--theme_default_text);}

div[style="width: 100%; height: 100%; position: fixed; top: 0px; left: 0px; z-index: 2000000000; background-color: rgb(255, 255, 255); opacity: 0.5;"] {background-color: var(--theme_default_bg) !important;}

::-webkit-scrollbar {width: 14px; height: 14px;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background: var(--theme_default_scroll); border: 2px solid var(--theme_default_scrollborder);}
::-webkit-scrollbar-thumb:active {background: var(--theme_default_scrollactive);}
::-webkit-scrollbar-track {background: var(--theme_default_postbg);}
::-webkit-scrollbar-corner {background: var(--theme_default_postbg);}

a, .a-link-emulator {color: var(--theme_default_link);}
a:hover, .a-link-emulator:hover {color: var(--theme_default_hover);}

font[color="red"], font[color="#ff0000"], span[style="font-size:10px; color:red;"], .closed-board, .archive-thread, .thanks-abu {color: var(--theme_default_red) !important;}

.hdescription, .hdescription-stats, .ui-widget, .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {font-family: inherit;}
.postscontainer {border-width: 2px;}
.hboardlist .column .boardsubj {font-weight: normal;}

.adminbar {color: var(--theme_default_alttext); text-align: center;}
.adminbar__menu {border-bottom-width: 2px;}
.adminbar__boards_sticky {border-bottom-width: 2px; transform: translateY(-28px);}
.adminbar__boards_hovered {transform: translateY(0);}
.adminbar a {color: var(--theme_default_alttext)}
.adminbar a:hover, .adminbar a:focus {color: var(--theme_default_navhover);}

hr {border-top-width: 2px;}

.na {border: 2px solid var(--theme_default_border); border-radius: 0; padding: 10px;}
.na:hover, .na:active {background: var(--theme_default_altpostbg);}
.na__icon {fill: var(--theme_default_text);}

.mainlogo, a.logo {background: none; color: var(--theme_default_logo) !important; padding: 0;}
.mainlogo::before, a.logo::before {content: "\f005"; font: normal normal normal 60px/1 FontAwesome; padding-right: 10px;}
.welcomefriend {color: var(--theme_default_logo);}
.logo, #title {color: var(--theme_default_logo); font-family: 'Russo One', sans-serif; text-transform: uppercase; letter-spacing: 2px;}

.rmenu, #plashque, #mobile-plashque, .logo img, .logo br, .mmm {display: none;}

.cntnt__left_sticky {scrollbar-color: var(--theme_default_scroll) var(--theme_default_postbg); scrollbar-width: thin;}

.fm__header {background: none; text-transform: uppercase; letter-spacing: 1px;}
.fm__header:hover {background: none; color: var(--theme_default_link);}
.fm__sub a {color: var(--theme_default_text); display: block; padding: 2px 4px;}
.fm__sub a:hover, .fm__sub a:focus {color: var(--theme_default_link);}

.thread_wrap {border-width: 2px; margin: 5px;}
.thread_wrap.expandble:hover .cthread {box-shadow: 0 0 0 2px var(--theme_default_border);}

.thread-nav {color: transparent; font-size: 0.92em;}
.thread-nav a {color: var(--theme_default_navlink); transition: .1s;}
.thread-nav a:hover, .thread-nav a:focus {color: var(--theme_default_navhover);}
.thread-nav span {color: var(--theme_default_text);}

.oppost, .post_type_oppost {background: var(--theme_default_postbg); border: 2px solid var(--theme_default_border); display: inline-block; min-width: 33em;}

.thread:after {border-top-width: 2px; margin: 16px 0;}

.post {padding: 3px 4px 4px;}
#posts-form > .reply.post, .post_preview {border: 2px solid var(--theme_default_border) !important;}
.post_type_reply:before {display: none;}
.post_type_reply {border-width: 2px; border-radius: 0;}
.post__details {color: var(--theme_default_alttext2);}
.reply.hiclass, .post_type_highlight {background: var(--theme_default_postbghighlight); border-color: var(--theme_default_postborderhighlight);}

.post_type_watched .post__anon::before {background: var(--theme_default_link); content: "Вы"; color: var(--theme_default_postbg); padding: 0 4px; margin-right: 5px; transition: .1s;}
.post_type_replied .post__anon::before, .post_type_replied .post__email::before {background: var(--theme_default_postbg); border: 2px solid var(--theme_default_link); content: "Ответ"; color: var(--theme_default_link); padding: 0 4px; margin-right: 5px;}
.post_type_replied.post_type_highlight .post__anon::before {background: var(--theme_default_postbghighlight); border: 1px solid var(--theme_default_link); border-radius: 3px; content: "Ответ"; color: var(--theme_default_link); padding: 0 4px; margin-right: 5px;}

.post__title {font-weight: normal;}
.post__ophui {color: var(--theme_default_ophui);}
.post__mod {color: var(--theme_default_mod);}

.reflink a, a.post__reflink {color: var(--theme_default_alttext);}
.reflink a:hover, a.post__reflink:hover, .reflink a:focus, a.post__reflink:focus {color: var(--theme_default_link);}

.post__rate_type_like:hover .post__rate-icon, .post__rate_liked .post__rate-icon {fill: var(--theme_default_link);}
.post__rate_type_dislike:hover .post__rate-icon, .post__rate_disliked .post__rate-icon {fill: var(--theme_default_red);}
.post__rate_type_like, .post__rate_type_dislike {color: var(--theme_default_alttext2);}

.post .webm-file, .post__file-webm {border: none; border-bottom: 2px dashed var(--theme_default_alttext2);}

.modal {border-width: 2px; border-radius: 0;}
.modal a {font-family: unset; color: var(--theme_default_text); padding: 4px 10px;}
.modal a:hover {background: var(--theme_default_altpostbg); color: var(--theme_default_text);}

.post__refmap a {color: var(--theme_default_altlink);}
.post__refmap a:hover {color: var(--theme_default_althover);}

.warningcover {background-color: var(--theme_default_bg);}
.warningbox {border-color: var(--theme_default_border);}

.sticker {scrollbar-color: var(--theme_default_scroll) var(--theme_default_postbg); scrollbar-width: thin;}
.qr {border-width: 2px;}
.qr__header {background: none;}
.settings__header {font-weight: normal;}
.settings__prefs {margin-left: 12px;}
.settings__label select {margin-left: 4px;}
.settings__edit {background: none; color: var(--theme_default_text);}
.settings__edit:hover {color: var(--theme_default_link);}
.settings__edit::before {content: "\f044"; font: normal normal normal 14px/1 FontAwesome;}
#setting-editor-textarea-textarea {font-family: Consolas, monospace;}
.qr_reply .qr__footer {display: none;}

.captcha-image img, #unban-captcha-div, .qr-settings .setting-edit-btn {filter: var(--theme_default_invert);}

input[type="checkbox"] {-webkit-appearance: none; -moz-appearance: none; appearance: none; background: var(--theme_default_altbtnbg); border: 2px solid var(--theme_default_altborder); height: 15px; width: 15px; outline: none; vertical-align: -3px;}
input[type="checkbox"]:focus {background: var(--theme_default_altbtnbghover);}
input[type="checkbox"]:checked {border: 2px solid var(--theme_default_altborder); box-shadow: inset 0 0 0 2px var(--theme_default_altbtnbg), inset 0 0 0 6px var(--theme_default_alttext);}

input[type="text"], input[type="email"], input[type="password"], textarea, .input {background-color: var(--theme_default_altbtnbg); border: 2px solid var(--theme_default_border); border-radius: 0; color: var(--theme_default_text); outline: none;}
input[type="text"]:focus, input[type="email"]:focus, input[type="password"]:focus, textarea:focus, .input:focus {background-color: var(--theme_default_altbtnbghover);}
input[type="text"]::placeholder, input[type="password"]::placeholder, textarea::placeholder {color: var(--theme_default_alttext); opacity: .5;}

input[type="button"], input[type="submit"], .arch-searchform input[type="submit"] {background-color: var(--theme_default_btnbg); border: 2px solid var(--theme_default_btnborder); border-radius: 0; color: var(--theme_default_btntext);}
button[disabled], html input[disabled] {background: var(--theme_default_btnbg) !important; color: var(--theme_default_alttext2);}

select {background: var(--theme_default_altbtnbg); border: 2px solid var(--theme_default_border); color: var(--theme_default_text); padding: 1px 4px !important; outline: none;}
select:focus {border-color: var(--theme_default_altborder);}
.mselect, select {-webkit-appearance: none; -moz-appearance: none; appearance: none; border-right-width: 10px !important;}

.markup {padding: 1px 3px; opacity: 0;}
.m-bold:hover, .m-italic:hover, .m-underline:hover, .m-overline:hover, .m-strike:hover, .m-sup:hover, .m-sub:hover {color: var(--theme_default_link);}
.m-bold:before {content: "\f032"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-italic:before {content: "\f033"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-quote {color: var(--theme_default_postnum);}
.m-quote:hover {color: var(--theme_default_postnum2);}
.m-quote:before {content: "\f054"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-underline:before {content: "\f0cd"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-overline:before {content: "O"; font: bold 14px/20px serif; position: absolute; height: 18px; width: 22px; text-decoration: overline; text-align: center;}
.m-spoiler {color: var(--theme_default_alttext2); width: 31px;}
.m-spoiler:hover {color: var(--theme_default_text);}
.m-spoiler img {width: 31px; height: 18px;}
.m-spoiler:before {background: var(--theme_default_spoiler); content: "SP"; line-height: 16px; position: absolute; margin-top: 1px; height: 16px; width: 31px; text-align: center;}
.m-strike:before {content: "\f0cc"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-sup:before {content: "\f12b"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-sub:before {content: "\f12c"; font: normal normal normal 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}

.filer__limits {color: var(--theme_default_drag);}
.filer__thumb {border-width: 2px;}
.oekaki-width, .oekaki-height {width: 44px;}

.literally .lc-picker, .literally .horz-toolbar, .literally .lc-options .lc-font-settings {background-color: var(--theme_default_postbg);}
.literally .lc-picker, .literally.toolbar-at-top .lc-options {border-color: var(--theme_default_border);}
.literally .lc-picker .toolbar-button {border-radius: 0;}
.literally .lc-picker .toolbar-button:hover:not(.disabled) {background-color: var(--theme_default_border) !important; border-color: var(--theme_default_border);}
.literally .lc-picker .toolbar-button.selected:not(.disabled) {background-color: var(--theme_default_postborderhighlight) !important; border-color: var(--theme_default_postborderhighlight) !important; color: var(--theme_default_postbg);}
.literally .color-well-color-container {background-color: transparent !important; border-color: var(--theme_default_border); border-radius: 0; border-width: 2px;}
.literally .color-well-color-container:hover:not(.disabled) {border-color: var(--theme_default_link);}
.literally .color-well-color-container .color-well-color, .literally .color-well-color-container .color-well-checker-top-left, .literally .color-well-color-container .color-well-checker-bottom-right {border-radius: 0;}
.literally .horz-toolbar .square-toolbar-button {border-color: var(--theme_default_border); border-width: 2px; border-radius: 0; height: 30px; width: 30px;}
.literally .horz-toolbar .square-toolbar-button.selected:not(.disabled) {background-color: var(--theme_default_postbghighlight); border-color: var(--theme_default_postborderhighlight) !important;}
.literally .horz-toolbar .square-toolbar-button:hover:not(.disabled) {border-color: var(--theme_default_altborder);}
.lc-font-settings select {line-height: 16px; margin-right: 4px;}
.square-toolbar-button circle {fill: var(--theme_default_text);}
.literally .color-well {padding-bottom: 2px; border-radius: 3px 0 0 3px;}
.literally .color-well.open {background-color: var(--theme_default_altpostbg);}
.literally .color-picker-popup {background-color: var(--theme_default_postbg);}

