.lc-pick-tool[style="background-image:url(/makaba/templates/js/lcanvas/img/text.png);"]::before {content: "\f031"; font: normal normal normal 22px/22px FontAwesome;}
.lc-pick-tool[style="background-image:url(/makaba/templates/js/lcanvas/img/polygon.png);"]::before {content: "\f275"; font: normal normal normal 22px/22px FontAwesome;}
.lc-pick-tool[style="background-image:url(/makaba/templates/js/lcanvas/img/pan.png);"]::before {content: "\f047"; font: normal normal normal 22px/22px FontAwesome;}
.lc-pick-tool[style="background-image:url(/makaba/templates/js/lcanvas/img/eyedropper.png);"]::before {content: "\f1fb"; font: normal normal normal 22px/22px FontAwesome;}
