css:
@-moz-document domain("reddit.com") {
/*Main page : */
/*Wallpaper here */
.cVwMdj,
.dtrVxj,
._29IbETWb5VVDcfk_-GumWz, 
.s11ikvvk-5 {
    background-image: url(https://images5.alphacoders.com/842/842679.jpg)!important;
    background-attachment: fixed!important;
    background-repeat: no-repeat!important;
    background-size: cover!important;
}

.Art {
    background-color: #000000a3 !important;
    background-image: none !important;
}



/*Chat Reddit*/
.kjnGUC,
.hvLLYX,
.bFXjsc {
    background: #5cc6d1 !important;
}

.ckWwzh {
    border-bottom: 2px solid rgb(110, 246, 246) !important;
}



/*custom buttons : */
.eZQ5o2PrhR59wkAtPbxMU,
.cq0sTeCPC4GI78UNPdClD {
    color: aqua !important;
    fill: aqua !important;
}

.hQPbDS {
    background: white !important;
    color: black !important;
}


.hmPEcF,
.hYCqCb,
.elXrPm {
    fill: #f248b2 !important;
    color: #f248b2 !important;
    border-color: #f248b2 !important;
}


/*Getting started with Reddit : */
.AnimatedForm__bottomNav,
.AnimatedForm__bottomNav,
.AnimatedForm__nextLink,
.Onboarding__usernameSuggestion,
.SubredditPicker__categories {
    background-color: #222222e8 !important;
    color: #fc8bd7 !important;
}

.AnimatedForm__nextButton,
.rTggD,
.cLMAbv,
.Onboarding__layout .AnimatedForm__subscribeButton {
    background: #f248b2 !important;
    color: #fdfdfd !important;
}

._1poyrkZ7g36PawDueRza-J {
    background-color: #0000007d !important;
    color: #6ff8f8 !important;
}

.rc-anchor-light {
    background: #1b1b1bd6 !important;
    color: #ccc !important;
}



/*hover:*/
.SubredditPicker__category.m-selected .SubredditPicker__count,
.SubredditPicker__category.m-selected .SubredditPicker__icon,
.SubredditPicker__category:hover .SubredditPicker__categoryDescription,
.SubredditPicker__category:hover .SubredditPicker__count,
.SubredditPicker__category:hover .SubredditPicker__icon {
    color: #5cc6d1 !important;
    fill: #5cc6d1 !important;
}
.SubredditPicker__categoryDescription {
    color: #dfdfdf !important;
}
.SubredditPicker__subreddit {
    background: #222c;
    padding: 12px 0;
    margin: 5px !important;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    border-bottom: 1px solid rgba(0, 0, 0, .1);
}

/*upvotes, downvotes:*/
.dUkgny:hover,
.gwerfb {
    color: #f248b2!important;
}

.hEPTih:hover,
.VzVyB {
    color: #5cc6d1 !important;
}

._1rZYMD_4xY3gRcSS3p8ODO {
    color: white !important;
}
}
