css:
@-moz-document domain("vivo.sx")
{
#fb-root {
    display: none !important;
}
header {
    display: none !important;
}
footer {
    display: none !important;
}
#light-background {
    display: none !important;
}
#player {
    border: solid 1px #b56700;
}
.watch-footer {
    display: none !important;
}
.stream-share {
    display: none !important;
}
section.app_overview {
    background: #000;
}
div#alert-stream, div#alert-browser, div#alert-throttle {
    display: none !important;
}
.clearfix {
    display: none !important;
}
.vivo-content-box {
    border-radius: 5px;
    background: #000;
    color: #eee;
    padding: 0;
}
section.vivo-content.stream {
    padding-top: 0;
    padding-bottom: 0;
}
.vivo-content-box-wrapper .vivo-content-box h1 {
    color: #ea8500;
    font-size: 16px;
}
.vivo-content-box-wrapper .vivo-content-box-watch h1 strong {
    font-weight: 100;
    color: #ea8500;
    font-size: 15px;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    font-family: inherit;
    font-weight: 100;
    line-height: 1;
    color: inherit;
}
#_atssh {
    display: none !important;
}
.fp-filters, .fp-logo, .watch-footer {
    display: none !important;
}
.addthis-smartlayers, div#at4-follow, div#at4-share, div#at4-thankyou, div#at4-whatsnext {
    display: none !important;
}
.container {
    width: 90%;
}
.vivo-content-box-wrapper {
    padding-left: 0;
    padding-right: 0;
}
html {
    font-size: 62.5%;
    -webkit-tap-highlight-color: #555;
    background-color: #000;
}
#AdWidgetContainer {
    display: none !important;
}
div[onclick*="("] {
    display: none !important;
}

.fp-ui, .fp-icon {
    color: #bd6300!important;
    font-style: normal!important;
    text-decoration: none!important;
}

.fp-color, .fp-progress, .fp-bar>.fp-progress.animated{
    background-color: #6d4200!important;
    border: 1px solid #4a2a00!important;
    color: #bd6300!important;
    font-style: normal!important;
    text-decoration: none!important;
}

.fp-elapsed, .fp-duration {
    color: #dedede!important;
}

.fp-duration:before {
    color: #dedede!important;
}
#preload {
    background: #000!important;
}
.preload_loader div {
    background: rgba(255, 118, 46, 0.82)!important;
    border-radius: 1px!important;
}
}
