css:
@-moz-document url-prefix("https://www.backstabbr.com/") {
  table.dataTable thead .sorting:after, table.dataTable thead .sorting_asc:after, table.dataTable thead .sorting_desc:after, table.dataTable thead .sorting_asc_disabled:after, table.dataTable thead .sorting_desc_disabled:after {
    left: 11px;
  }

  table.dataTable.table-sm .sorting:after, table.dataTable.table-sm .sorting_asc:after, table.dataTable.table-sm .sorting_desc:after {
    top: 4px;
  }

  table.dataTable.table-sm .sorting:before, table.dataTable.table-sm .sorting_asc:before, table.dataTable.table-sm .sorting_desc:before {
    left: 5px;
    top: 3px;
  }

  table.dataTable.table-sm>thead>tr>th {
    padding-left: 23px;
  }

  table.dataTable.dtr-column>tbody>tr>td.control:before, table.dataTable.dtr-column>tbody>tr>th.control:before {
    line-height: 115%;
  }

  .table-bordered, .table-bordered-old {
    border: 1px solid #dee2e6;
    border-right: 0px;
  }

  .table th, .table td {
    border-right: 1px solid #dee2e6;
  }

  body {
    margin-bottom: 50px;
  }

  .footer {
    position: fixed;  
    bottom: 0px;
    z-index: 51;
    height: 40px;
    background-color: white;
    box-shadow: 5px 10px 8px 10px #888888;
    padding-top: 10px;
  }

  ::-webkit-scrollbar {
    width: 5px;
  }
  ::-webkit-scrollbar-track {
    background: #e6e6e6;
  }
  ::-webkit-scrollbar-thumb {
    background: #999;
    border-radius: 5px;
  }
  ::-webkit-scrollbar-thumb:hover {
    background: #737373;
    border-radius: 5px;
  }
}

@-moz-document url-prefix("https://www.backstabbr.com/game") {
  .form-control:focus {
    color: #212529;
  }
  
  /*
  @media (min-width: 576px) {
    .gr__backstabbr_com, #body-container, #content, .row, .col-sm-6, .game-columns {
      height: 100%;
    }
    body {
      height: calc(100% - 60px);
    }
    .game-columns {
      overflow: auto;
      padding: 5px;
    }
    .footer {
      bottom: 30px;
    }
  }
  */

  @media (min-width: 576px) {
    #press_thread_and_compose {
      overflow: auto;
      padding: 5px;
      height: calc(100vh - 128px);
    }
  }

  #tooltip {
    background: rgba(0, 0, 0, 0.2);
    color: #fff;
    text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
  }

  #message_ajax_status {
    margin-top: 5px;
    margin-bottom: -5px;
  }
}
