css:
@-moz-document domain("neogaf.com") {
/* General Stuff. */


body {
    background-color:#222222;
}

a{
        text-decoration: none!important;
}

.p-breadcrumbs{
    color:rgba(0,0,0,0);
}

.menu-header{
    color:#FF9900;
    border-bottom: 1px dashed #fff;
}

.p-body-pageContent{
    box-shadow:none!important;
    max-width:90%;
     margin: auto;

}


/* Colors Orange Reply Box. */

.fr-box.fr-basic .fr-wrapper, .fr-toolbar.fr-top, .fr-separator{
    border:1px solid #404040;
}



/* Leave quotes open. */

.bbCodeBlock, .quoteContainer .quote, .bbCodeBlock--expandable {
    max-height: none;
    overflow: hidden;
    padding-bottom: 1px;
}

.bbCodeBlock--expandable.is-expanded .bbCodeBlock-expandContent, .bbCodeBlock--expandable .bbCodeBlock-expandContent
{
    max-height: none;
}

.bbCodeBlock .quoteContainer .bbCodeBlock--expandable
{		
background:transparent!important;
    display:none;
}

.rellax{
    display:none;
}

.bbImage{ 
    max-width:50%!important;
    max-height:50%!important;
}

.menu-content{
    border-radius: 0px;
}
.js-alertsMenuBody{
    border-bottom: 0px dashed #fff;
}

*:hover{
    -o-transition: .2s;
    -ms-transition: .2s;
    -moz-transition: .2s;
    -webkit-transition: .2s;
    transition: .2s;
}

.avatar img:not(.cropImage), .avatar{
    border-radius:6px!important;
}

.structItemContainer-group--sticky{
    background-color:#312e25!important
    
}

.structItemContainer-group--sticky .structItem--thread {
    border-left: 0px solid #FF9900;
    border-bottom: 2px solid #FF9900;
}

.structItem-pageJump a {

    color: #FF9900;
    border: 1px solid #FF9900;
    text-decoration: none;
    border-radius: 6px;
    padding: 0 3px;
    opacity: .5;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    
}

.structItem-status--poll::before{
    color:#ff9900;
}

html{
 background-color:rgb(48, 48, 48);
    
    font-family:'Open Sans'!important;
}


#header {
    background:#303030!important;
}

.secondaryContent, .sidebar {
background-color:rgb(48, 48, 48);
    
    
}



.Menu .secondaryContent{
    background-color:rgb(48, 48, 48);
}

.breadcrumb {
    background-color: rgb(34, 34, 34)!important;
    border: 0px none #222222;
    padding-left:12px;
}
.breadcrumb .crust a.crumb > span {
    color: #CCCCCC !important;
}
.breadcrumb .crust:first-child a.crumb,
.breadcrumb .crust.firstVisibleCrumb a.crumb {
    border-bottom-left-radius: 0px !important;
    padding-left: 0px !important;
}
.Popup .PopupControl.PopupOpen,
.Popup.PopupContainerControl.PopupOpen {
    background-color: rgb(34, 34, 34) !important;
}
.Popup .PopupControl:hover,
.Popup.PopupContainerControl:hover {
    background-color: #FF9900!important
}

.Menu {
    border-color: rgb(51, 51, 51) !important;
}

#content .pageContent {
    background: #0f1013 !important;
    color: rgb(135, 135, 135) !important;
}
.discussionList .sectionHeaders,
.sectionFooter {
    background:#FF9900!important;
    color: #a3a6c1 !important;
}
.discussionList .sectionHeaders dd a[href]:hover {
    background:#FF9900!important;
}

.PageNav a {
    background-color: rgb(34, 34, 34) !important;
    border: 1px solid rgb(51, 51, 51) !important;
}



.DiscussionListOptions {
    border: 1px solid rgb(51, 51, 51) !important;
}
.discussionListItem,
.discussionListItem .stats,
.discussionListItem .stats dl {
    background-color: rgb(37, 38, 40) !important;
    border-color: rgb(51, 51, 51) !important;
}
.discussionListItem .itemPageNav {
    visibility: visible;
}
.pairs dt,
.pairsInline dt,
.pairsRows dt,
.pairsColumns dt,
.pairsJustified dt {
    color: rgb(135, 135, 135) !important;
    border: 0px none rgb(34, 34, 34) !important;
}

.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    /*background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;
    background-color: transparent !important;*/
    
    background: rgb(34, 34, 34) !important;
    color: #a3a6c1 !important;
    border: 1px solid rgb(51, 51, 51) !important;
}
.nodeList .forumNodeInfo {
    color: #CCCCCC !important;
}
.nodeList > li:first-child,
.nodeList > li:last-child,
.node .nodeLastPost {
    border: 0px none rgb(34, 34, 34) !important;
}
body .itemPageNav a {
    background-color: rgb(34, 34, 34) !important;
    border: 1px solid rgb(51, 51, 51) !important;
}
a.callToAction span {
    color: rgb(255, 255, 255) !important;
    background-color: #FF9900!important;
    border: 1px solid rgb(51, 51, 51) !important;
}
#DiscussionListOptionsHandle a {
    background: #0f1013 !important;
}
.discussionListItems .unread .title a {
    font-weight: normal;
}
.navPopup .listItem {
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
/* Help */

.heading,
.xenForm .formHeader {
    font-weight: normal !important;
    font-size: 12px;
    background: #FF9900!important;
    padding: 10px;
}
.subHeading {
    background: #FF9900!important;
}
.textHeading,
.xenForm .sectionHeader {
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
.dataTable tr.dataRow th {
    background: #222222 !important;
    color: #CCCCCC !important;
    border-top: 0px solid rgb(51, 51, 51) !important;
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
.bbCode > dl > dd {
    /*background: -webkit-gradient(linear, left top, left bottom, from(#3c414d), to(#2f343d)) !important;*/
    
    border: 1px solid rgb(51, 51, 51) !important;
    background: rgb(47, 52, 61);
}
/* Misc */

.node .nodeLastPost{
 background-color:rgb(34, 34, 34);   
}

.footer, .footerLegal{
    background-color:#303030!important;
}

#QuickSearch,
#QuickSearch.active {
    background-color: transparent;
    
    border: 0px solid rgb(51, 51, 51) !important;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0);
}


.searchResult {
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}

.button{
background-color:transparent!important;
}

.sidebar .section .primaryContent h3,
.sidebar .section .secondaryContent h3,
.profilePage .mast .section.infoBlock h3 {
    color: #CCCCCC !important;
}
.profilePage .mast .sectionFooter {
    border-left: 0px solid rgb(126, 141, 165) !important;
    border-right: 0px solid rgb(126, 141, 165) !important;
}
/* Sign In */

#SignupButton {
    background: transparent !important;
    padding: 0px;
}
#SignupButton .inner {
    background-color: #FF9900!important;
}
.xenOverlay #pageLogin {
    background: rgb(34, 34, 34) !important;
    color: #CCCCCC !important;
}
.heading,
.xenForm .formHeader {
    color: #CCCCCC !important;
}



.breadcrumb {
    background-color: rgb(34, 34, 34)!important;
    border: 0px none #222222;
}
.breadcrumb .crust a.crumb > span {
    color: #CCCCCC !important;
}



.navTabs {
    margin: 0; padding: 0;
    text-transform: none!important;
    position: relative;
   /* width: -webkit-fill-available!important;*/
    z-index: 1000;
    left:110px!important;
    top:22px;
    padding-left: 7%;
    padding-right: 7%;
/*    background-color:#212121;*/
    background-color:transparent;
    color:transparent;
    box-shadow:none;
}





.messageText {
    color: #CCCCCC !important;
}
.messageUserInfo {
    background-color: rgb(34, 34, 34)!important
}
.message .messageMeta .control {
    background-color: rgb(47, 52, 61)
}
.quickReply,
.redactor_toolbar {
    background-color: rgb(34, 34, 34) !important;
    border: 1px solid rgb(51, 51, 51) !important;
    padding-bottom: 2px;
}
.redactor_box {
    border: 1px solid rgb(51, 51, 51) !important;
    border-bottom: 1px solid rgb(51, 51, 51) !important;
}
.button,
.button.primary {
    color: rgb(255, 255, 255) !important;
    background-color: #FF9900!important;

}
.button--icon--cancel{
    background-color:rgba(0,0,0,0)!important;
}
.button--icon--cancel{
    background-color:rgba(0,0,0,0)!important;
}

.button[name~="more-options"], .button[name~="more-options"]{
    background-color: rgba(0,0,0,0)!important;
}
.button:hover,
.button.primary:hover {
    color: #FF8B01 !important;
}
.footer .pageContent {
    background: #FF9900!important;
}
.messageList .newMessagesNotice {
    background: #FF9900!important;
    border: 1px solid rgb(51, 51, 51) !important;
}
.PageNav a.currentPage {
    background: #FF9900!important;
}
.xenTooltip {
    background: #FF9900!important;
}






/* Show the whole quote and hide the gradient/click to expand text */

.bbCodeQuote .quoteContainer .quote {
    max-height: none;
    overflow: hidden;
    padding-bottom: 1px;
}

.bbCodeQuote .quoteContainer .quoteExpand
{		
background:transparent!important;
    display:none;
}


/* Threads */

.titleBar h1,
.idstats dt,
.idstats dd {
    color: #CCCCCC !important;
}


/*Animations */

.secondRow{
    -o-transition: .2s;
    -ms-transition: .2s;
    -moz-transition: .2s;
    -webkit-transition: .2s;
    transition: .2s;
    opacity: 0.30;
}




.secondRow:hover{
    opacity: 1;
}


.xenOverlay.timedMessage{
    background:#303030!important;
}

#StackAlerts .stackAlertContent{ 
 background-color:#222222!important;   
}





#unreadLink, .fa-circle{ 
    color:rgb(127, 81, 186)!important;
    
}







}
