css:
@-moz-document domain("serienjunkies.org")
{
body {
    color: #fff !important;
    background: #636363 url(https://c0d3d.net/stylish/userstyles/css.img/serienjunkies.org.jpg) no-repeat fixed center top / cover !important;
}
#sidebar, #top_banner {
    display: none !important;
}
#rap {
    background: rgba(0, 0, 0, 0.42) !important;
}
#search {
    background: rgba(0, 0, 0, 0) !important;
    margin-bottom: 0 !important;
}
#content {
    background: rgba(0, 0, 0, 0) !important;
}
a.letter {
    color: #fff !important;
}
a.letter[style] {
    background: rgba(237, 237, 237, 0.27) !important;
}
a#cl_only, a#dl_only {
    background: rgba(0, 0, 0, 0.45) none repeat scroll 0 0 !important;
    color: #ffffff !important;
    border: rgba(255, 255, 255, 0.7215686274509804) solid 1px !important;
}
.tabs {
    border-bottom: none !important;
}

h2 {
    color: #eee !important;
}
.cat-title {
    color: #e8e8e8 !important;
    font-weight: 100 !important;
    padding-left: 0 !important;
}
.grey-box, #return-to-top {
    background: rgba(0, 0, 0, 0.54) !important;
    border: rgba(255, 255, 255, 0.7215686274509804) solid 1px !important;
}
a:link, a:visited {
    color: #fff !important;
}
a:hover, a:active {
    background: rgba(0, 0, 0, 64) !important;
    color: #464646 !important;
}
#gnav li a:link, #gnav li a:visited {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%,rgb(0, 0, 0) 100%) !important;
    color: #fff !important;
}
#gnav li a:hover, #gnav li a:active {
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%,rgb(0, 0, 0) 100%) !important;
    color: rgba(214, 102, 0, 0.5) !important;
}
.post-content fieldset {
    color: #fff !important;
}
.post-content legend {
    background: rgba(0, 0, 0, 0.54) !important;
    border: rgba(255, 255, 255, 0.7215686274509804) solid 1px !important;
}
p.qsep {
    background-color: rgba(0, 0, 0, 0.31);
    border: 1px solid #ffffff !important;
}
.download_main {
    background-color: rgba(0, 0, 0, 0.23) !important;
    border: 1px solid #ffffff !important;
}
#submitSearch {
    background: rgba(0, 0, 0, 255) !important;
    border: rgba(255, 255, 255, 0.7215686274509804) solid 1px !important;
}
#calendar {
    background: rgba(0, 0, 0, 0.54) !important;
    border: rgba(255, 255, 255, 0.7215686274509804) solid 1px !important;
}
.streams {
    color: #fff !important;
}
.streams tr:hover td {
	background-color: rgba(255, 255, 255, 0.49019607843137253) !important;
	color: #000;
}
}
