css:
@-moz-document domain("xataka.com.mx"), domain("xataka.com"), domain("xatakamovil.com"), domain("xatakandroid.com"), domain("genbeta.com"), domain("espinof.com"), domain("applesfera.com"), domain("xatakahome.com"), domain("vidaextra.com")  {
    img {background-color: #111 !important;filter: grayscale(30%) brightness(70%) !important;}
    .comment-thread-close {background-color: #434b54 !important;}
    .comment-author a, .desvio-container a {color: #ccc !important;}
    #commentBox, .section-instagram-container {background-color: #222222 !important;color: #bbb !important;border: 0 !important;}
    .content-container, .section-modal-container, .foot, .post-related-content li {background-color: #171717 !important; color: #bbb !important;}
    .post-related-content span, .section-instagram-container a {color: #bbb !important;}
    .section-modal, .head-menu, .head-menu a {background-color:#222; color: #bbb !important;}
    #search-field-1, #search-field-2, #search-field-3 {background-color:#222 !important; color: #bbb !important; border: 0 !important;}
    main { background-color: #222222 !important; }
    .article, #newsletter { background-color: #111 !important; color: #bbb !important; }
    .actualizacion { color: #888 !important; }
    .article-home-header a { color: #bbb !important; }
    div#comments, .comment-section { background-color: #111111 !important; }
    .abstract-title a {  color: #bbb !important; }
    .abstract-article, .abstract-longform, .abstract-ecommerce, .abstract-respuestas, .abstract {  background:  #111 !important; }
    h1, h2, h3, h4, h5, h6, p, pre, blockquote { color: #bbb !important;}
    .article-header-branded-container {background: #333333 !important;}
    .section-recent-container { background: #171717 !important;}
    .section-recent-aside{display: none !important;}
    .comment-content.blob p, .comment-content.blob ul, .comment-content.blob ol, .comment-content.blob blockquote, .comment-content.blob h2, .comment-content.blob h3, .comment-content.blob h4 {color: #bbb !important;}
    .comment-write-form, .comment-item {background: #171717 !important;color: #bbb !important;}
    .comment-write-input textarea, .comment-write-input textarea:focus {background: #171717 !important;color: #bbb !important;border: 1px solid #666 !important;}
    .comment-badge em {background: #333 !important;}
    .related-item {background: #171717 !important;}
    .related-item a:hover {background: #222 !important;}
    .home-video-stream .featured-video a, .home-video-stream .vertical-container .elastislide-list li a {background: #0a0a0a !important;}
    .section-pinned-container {background: #171717 !important;color: #bbb !important;}
    .section-pinned-header a {color: #eee !important;}
    .shortcut-title a{color: #bbb !important;}
    .article-social-share.m-v1 {background: #171717 !important;color: #bbb !important;}
    .article-social-share.is-visible.m-v1 q {;color: #bbb !important;}
    .module-type-table table, .data-table, .tabla-datos {background: transparent !important;}
    .module-type-table table thead th, .data-table thead th, .tabla-datos thead th {background: transparent !important;}
    .error, .wsl_error {color: #b94a48 !important;}
    .comment-author-staff {background: #ca8414;color: #171717;}
    .alert-message {background: #333;}
    .alert-message::before {color: #d98a0c !important;}
    .comment-actions-abuse a {color: #222;background: #d98a0c;}
    .actualizacion span, .actualizacion .actualizacion-inner, .article-asset-actualizacion > .asset-content {background: #222 !important;}
    .js-dailymotion {position: absolute !important;}
    .close-corner {display: none !important;}
}
