css:
@-moz-document domain("youtube.com") {
#masthead-container {
    opacity: 0 !important;
}
#masthead-container:hover {
    opacity: 1 !important;
}

#page-manager {
   margin-top: 0px !important;
}

#masthead-container.ytd-app {
	bottom: 0px !important;
	height: 0px !important;
}

#logo-icon-container.ytd-topbar-logo-renderer
ytd-watch-flexy:not([theater]):not([fullscreen]) #primary.ytd-watch-flexy, ytd-watch-flexy:not([theater]):not([fullscreen]) #secondary.ytd-watch-flexy {
	padding-top: 0px !important;
}

#show-hide-button.ytd-live-chat-frame > ytd-toggle-button-renderer{
   margin-top: -100px !important;
   margin-left: 0px !important;
   opacity: 0.7 !important;
   z-index:10000 !important; }
}

@-moz-document domain("youtube.com") {
ytd-watch-flexy {
    --ytd-watch-flexy-sidebar-width: 360px!important;
}
ytd-watch-flexy:not([theater]):not([fullscreen]) #primary.ytd-watch-flexy,
ytd-watch-flexy:not([theater]):not([fullscreen]) #secondary.ytd-watch-flexy {
    padding-top: 0!important;
}

ytd-watch-flexy[flexy_] #secondary.ytd-watch-flexy {
    min-width: 200px!important;
}

ytd-watch-flexy[flexy_] #chat.ytd-watch-flexy:not([collapsed]).ytd-watch-flexy {
    min-height: calc(54vmax)!important;
}

yt-live-chat-item-list-renderer[allow-scroll] #item-scroller.yt-live-chat-item-list-renderer {
    overflow-y: hidden!important;
}

yt-live-chat-item-list-renderer[allow-scroll] #item-scroller.yt-live-chat-item-list-renderer:hover {
    overflow-y: scroll!important;
    margin-right: -15px!important;
}

yt-live-chat-text-message-renderer,
yt-live-chat-message-input-renderer,
#show-hide-button.ytd-live-chat-frame > ytd-toggle-button-renderer.ytd-live-chat-frame,
yt-live-chat-header-renderer,
#panel-pages.yt-live-chat-renderer,
div.yt-live-chat-item-list-renderer,
.yt-live-chat-ticker-renderer {
    background: #0f0e11!important;
    color: #dad8de!important;
}

#message.yt-live-chat-text-message-renderer,
#label-text.yt-dropdown-menu,
#overflow.yt-live-chat-header-renderer,
button.yt-icon-button,
#label.yt-live-chat-text-input-field-renderer,
#count.yt-live-chat-message-input-renderer {
    color: #eee!important;
    font: inherit!important;
    font-family: arial!important;
    vertical-align: baseline!important;
}

#menu.yt-live-chat-text-message-renderer {
    background: none!important;
}

yt-live-chat-text-message-renderer {
    font-size: 13px!important;
    padding: 0 !important;
}

img.yt-img-shadow {
    max-height: 100%!important;
    max-width: 100%!important;
}

#show-hide-button.ytd-live-chat-frame > ytd-toggle-button-renderer.ytd-live-chat-frame {
    max-width: 250px!important;
    width: auto!important;
    color: #dd0!important;
    background: transparent!important;
    position: relative!important;
    top: 48px!important;}
}
