css:
@-moz-document url-prefix(https://minecraft-fr.gamepedia.com)
{
a, .jslink
{color: #8a92e1 !important;}
.mw-body, h1, h2, h3, h4, h5, h6
{color: #DDDBDB !important;}
.notaninfobox
{
	background-color: #2a2a2a;
	border: 1px solid #4B4B4B;
}
.alternaterows tr:nth-child(2n), .infobox-rows tr:nth-child(2n)
{
	background-color: #393939;
}
.mcwiki-header
{
	background-color: #365B83;
	border: 1px solid #365B83;
	border-bottom: 4px solid #172332;
	border-right: 4px solid #233b56;
}

#mw-page-base
{background-image: url(https://minecraft-fr.gamepedia.com/media/minecraft-fr.gamepedia.com/2/2c/Header-background-dark-altsearch.png);}
div.vectorTabs span::before, div#mw-head div.vectorMenu h3::before, div.vectorTabs span, div#mw-head div.vectorMenu h3,
div.vectorTabs span, div.vectorTabs span::before, div #mw-head div.vectorMenu > h3, div #mw-head div.vectorMenu > h4, div#p-variants.vectorMenu #mw-vector-current-variant, div #mw-head div.vectorMenu > h3:first-child::before, div #mw-head div.vectorMenu > h4:first-child::before, div#p-variants.vectorMenu #mw-vector-current-variant::before
{background: url(https://minecraft-fr.gamepedia.com/media/minecraft-fr.gamepedia.com/a/a4/Vector-tabs-dark.png) no-repeat left top;}
#left-navigation div.vectorTabs li:first-child span::before
{background-image: url(https://minecraft-fr.gamepedia.com/media/minecraft-fr.gamepedia.com/6/6e/Vector-tabs-dark-first.png);}
div#mw-head div.vectorMenu h3
{background-position-x: -6px !important;}
div.vectorTabs li.selected span, div.vectorTabs li span:hover, div.vectorTabs li span:focus
{background-position: left bottom;}
div.vectorMenu h3 span, div.vectorMenu h3:hover span, div.vectorMenu h3:focus span
{color: #939393;}
div.vectorMenu div.menu
{
	background-color: #393939;
	border: 1px solid #4b4b4b;
}
#p-search form::after
{
	border-top: 4px solid #294517;
	border-left: 4px solid #000;
}
#p-search form
{
	border-right: 4px solid #354828;
	border-bottom: 4px solid #382314;
}
#simpleSearch::before, #simpleSearch::after
{border-top: 1px solid transparent;}
.suggestions-results, .suggestions-result, .suggestions-special
{
	background-color: #393939;
	border: 1px solid #4b4b4b;
	color: #e6e6e6;
}
.suggestions-special .special-query
{color: #e6e6e6;}
.suggestions-result-current
{background-color: #565656;;}


div#content
{
	background-color: #222222 !important;
	border-top: 6px solid #363636;
	border-left: 6px solid #171717;
}

code
{
	color: #e4e4e4;
	background-color: #222;
	border: 1px solid #4d4d4d;
}

.toc, .mw-warning, .toccolours
{
	background-color: #2a2a2a !important;
	border: 1px solid #4B4B4B !important;
}

.tocnumber
{color: #ADADAD;}
table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td, .navbox > tr > th, .navbox > tr > td, .navbox > * > tr > th, .navbox > * > tr > tdtable.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td, .navbox th
{
	border: 1px solid #4B4B4B;
}

table.wikitable > tr > th, table.wikitable > * > tr > th, .navbox > tr > th, .navbox > * > tr > th, .navbox th
{
	background-color: #393939;
}

table.wikitable, .navbox, table
{
	background-color: #2a2a2a !important;
	color: #e6e6e6;
	border: 1px solid #4B4B4B;
}
.infobox-rows
{border: none;}
.list-style-none.nomobile
{border: navajowhite;}
.plainlinks.main-page-table
{border: none;}

.loadbox-navbox
{
	background: none !important;
	border: 1px solid #4B4B4B !important;
}

#content .loadbox-navbox > p, .navbox-top
{
	background-color: #393939 !important;
	border: 1px solid #4B4B4B !important;
}

.catlinks
{
	border: 1px solid #4b4b4b;
	background-color: #2a2a2a;
}

.nbttree-inherited
{
	background-color: #404051;
}

.msgbox
{
	background-color: #717155 !important;
	border: 2px solid #414141 !important;
}

li.gallerybox div.thumb
{
	border: 1px solid #4b4b4b;
	background-color: #2a2a2a;
}

hr
{background-color: #4b4b4b;}
.mw-changeslist-legend
{border-color: #4b4b4b;}

div#simpleSearch
{
	border-top: 3px solid #4B4B4B;
	border-left: 3px solid #484848;
	background: #2a2a2a;
}
#simpleSearch input
{
	color: #e6e6e6;
}

.style-level1
{
	border-color: #4b4b4b !important;
	background-color: #2a2a2a !important;
}
.style-level2
{
	border-color: #4b4b4b !important;
	background-color: #393939 !important;
}
.style-level3
{
	background-color: #474747 !important;
	border: 1px solid #4e4e4e !important;
}
.style-level4
{
	background-color: #4e4e4e !important;
	border: 1px solid #6b6b6b !important;
}

.style-color1
{
	border-color: #4b4b4b !important;
	background-color: #514E3E !important;
	color: #d2d2d2 !important;
}
.style-color2
{
	border-color: #2a2a2a !important;
	background-color: #413f3f !important;
}
.style-color3
{
	border-color: #4b4b4b !important;
	background-color: #514E3E !important;
}
.style-color4
{
	border-color: #413f3f !important;
	background-color: #454444 !important;
}
.style-color5
{
	border-color: #413f3f !important;
	background-color: #413c3c !important;
}
.style-color6
{
	background-color: #565675 !important;
}
.style-color7
{
	background-color: #0B7820 !important;
	color: #adc3ad !important;
}
.style-color8
{
	background-color: #D84A5B !important;
	color: #000 !important;
}
.style-color8
{
	background-color: #D84A5B !important;
	color: #000 !important;
}
.style-color9
{
	background: #B32828 !important;
	color: #ffa1a5 !important;
}
.style-color10
{
	background: #A79346 !important;
	color: #EADEC7 !important;
}
.style-color11
{
	background: #E1A61B !important;
	color: #4a3105 !important;
}
.style-color12
{
	background: #049820 !important;
	color: #d7e4d7 !important;
}

.style-border-color1
{
	border-color: #787878 !important;
}

pre, .mw-code
{
	color: #dedede;
	background-color: #2a2a2a;
	border: 1px solid #4b4b4b;
}

.mw-highlight .nc
{color: #6B6BFF;}
.mw-highlight .nn
{color: #5050FF;}
.mw-highlight .p
{color: #D8D8D8;}
.mw-highlight .mh
{color: #a4a4a4;}
.mw-highlight .mi
{color: #7b5e5e;}
.mw-highlight .kt
{color: #DD6A94;}

.editOptions
{
	background-color: #2c2c2c !important;
	color: #dddbdb !important;
	border: 1px solid #414141 !important;
}

select, input, .mw-ui-input, .oo-ui-textInputWidget input, .oo-ui-textInputWidget textarea, .wikiEditor-ui-toolbar .group .tool-select
{
	background-color: #414141;
	color: #e6e6e6;
	border-color: #6e6e6e;
}
.mw-ui-input
{box-shadow: inset 0 0 0 0.1em #5f5f5f}

.wikiEditor-ui .wikiEditor-ui-view
{
	border: 1px solid #414141;
	border-bottom: 1px;
}
.wikiEditor-ui .wikiEditor-ui-top
{border-bottom: 1px solid #6e6e6e;}
.mw-editfont-default
{
	background: #262626;
	color: #dddbdb;
}
.wikiEditor-ui-toolbar
{background-image: linear-gradient(#363636 0,#3c3c3c 32px);}
.wikiEditor-ui-toolbar .sections .section
{
	border-top: 1px solid #6e6e6e;
	background-color: #3c3c3c;
}
.wikiEditor-ui-toolbar .group .label
{color: #b1b1b1;}
.wikiEditor-ui-toolbar .group
{border-right: 1px solid #6e6e6e;}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button
{
	background: #4a4a4a;
	color: white;
	border-color: #6e6e6e;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover
{
	background-color: #727272;
	color: white;
	border-color: #6e6e6e;
}
.mw-ui-button:disabled, .mw-ui-button:disabled:hover, .mw-ui-button:disabled:active
{
	background-color: #292929;
	color: #7d7d7d;
	border-color: #6e6e6e;
}
.mw-ui-button#wpDraftSave
{
	background: #5d5d5d;
	color: white;
	border-color: #6e6e6e;
}
.mw-ui-button:hover
{background-color: #5f5f5f !important;}
.oo-ui-checkboxInputWidget [type="checkbox"] + span
{
	background-color: #414141;
	border: 1px solid #6e6e6e;
}

div.thumbinner
{
	border: 1px solid #4b4b4b;
	background-color: #2d2d2d;
}
html .thumbimage
{
	background-color: #444;
	border: 1px solid #4b4b4b;
}

.documentation
{
	background-color: #393939 !important;
	border: 1px solid #4B4B4B !important;
}
.documentation > :nth-child(1), .documentation > :nth-last-child(1)
{background-color: #474747 !important;}

td.diff-context
{background-color: #3b3b3b;}
.diff-context
{
	border-color: #4b4b4b;
	color: #dddbdb;
}
td.diff-addedline, td.diff-deletedline
{background-color: #454545;}
.diff-deletedline
{border-color: #958459;}
.diff-addedline
{border-color: #787878;}
.diff-addedline .diffchange
{background: #23415f;}
.diff-deletedline .diffchange
{background: #746032;}
}
