css:
@-moz-document domain("bilibili.com") {
/* 顶部：画友 */
[report-id="playpage_huahua"],
.z_top_nav .hbili,
/* 顶部：音频 */
[report-id="Webtab_click_audio"],
/* 顶部：游戏中心 */
[report-id="playpage_game"],
.z_top_nav .b-gc,
/* 顶部：会员购 */
[report-id="playpage_buy"],
.z_top_nav .b-ml,
/* 顶部：下载 APP */
[report-id="playpage_download"],
.z_top_nav .shouji,
/* 顶部：周边 */
.z_top_nav .b-zb,
/* 顶部：大会员 */
[report-id="big_tab_click"],
.z_top_nav .hbili,

/* 导航栏：右侧导航 */
ul.nav-menu > li.side-nav,
/* 导航栏：动图 */
.gif-menu,
/* 导航栏：国创 | 舞蹈 | 游戏 | 生活 | 鬼畜 | 时尚 | 广告 | 娱乐 | 影视 | 放映厅 */
ul.nav-menu > li:nth-child(4),
ul.nav-menu > li:nth-child(6),
ul.nav-menu > li:nth-child(7),
ul.nav-menu > li:nth-child(9),
ul.nav-menu > li:nth-child(10),
ul.nav-menu > li:nth-child(11),
ul.nav-menu > li:nth-child(12),
ul.nav-menu > li:nth-child(13),
ul.nav-menu > li:nth-child(14),
ul.nav-menu > li:nth-child(15),
ul.nav-menu > li:nth-child(16),
ul.nav-menu > li:nth-child(17),
ul.nav-menu > li:nth-child(18),
ul.nav-menu > li:nth-child(19),

/* APP 推广浮层 */
#fixed_app_download,

/* 首页推广 */
#home_popularize > div.l-con,
/* 直播区 */
#bili_live,
/* 音频 */
#bili_audio,
/* 娱乐 */
#bili_ent,
/* 电影 */
#bili_movie,
/* 电视剧 */
#bili_teleplay,
/* 影视 */
#bili_cinephile,
/* 纪录片 */
#bili_documentary,
/* 时尚 */
#bili_fashion,
/* 广告 */
#bili_ad,
/* 生活 */
#bili_life,
/* 游戏 */
#bili_game,
/* 舞蹈 */
#bili_dance,
/* 鬼畜 */
#bili_kichiku,
/* 国创 */
#bili_guochuang,
/* 特别推荐 */
#special_recommend,
/* 番剧动态 -> 特别推荐 */
.bangumi-module > .r-con,

/* 底部合作伙伴 */
.bili-footer .partner,

/* 底部推广 */
.boston-postcards > li:nth-child(3),

/* 右侧广告 */
.adpos,

/* 分区顶部广告 */
.gg-floor-module,

/* 分区调序栏 */
.elevator-module

{ display: none }

/* 底部高度调整 */
.bili-footer .footer-wrp {
  padding-bottom: 40px !important;
}

@keyframes shake-crazy {
    10% {
        transform: translate(11px, -15px) rotate(-7deg);
        opacity: .57
    }

    20% {
        transform: translate(18px, -11px) rotate(-6deg);
        opacity: .63
    }

    30% {
        transform: translate(1px, -12px) rotate(0deg);
        opacity: .33
    }

    40% {
        transform: translate(-5px, -10px) rotate(4deg);
        opacity: .76
    }

    50% {
        transform: translate(7px, -18px) rotate(4deg);
        opacity: .8
    }

    60% {
        transform: translate(-5px, -11px) rotate(9deg);
        opacity: .43
    }

    70% {
        transform: translate(10px, -2px) rotate(-5deg);
        opacity: .93
    }

    80% {
        transform: translate(3px, 13px) rotate(5deg);
        opacity: .81
    }

    90% {
        transform: translate(14px, 7px) rotate(4deg);
        opacity: .92
    }

    0%,100% {
        transform: translate(0, 0) rotate(0)
    }
}

[title="GeekTR"]:hover,
[alt="GeekTR"]:hover,
[href="//space.bilibili.com/3162440"]:hover,
[href="//space.bilibili.com/3213304/#/index"],
[href="//space.bilibili.com/3213304/#!/index"]:hover,
[href="https://space.bilibili.com/3162440"]:hover,
[href="https://space.bilibili.com/3213304/#/index"],
[href="https://space.bilibili.com/3213304/#!/index"]:hover
{
    animation-name: shake-crazy;
    animation-duration: 100ms;
    animation-timing-function: ease-in-out;
    animation-iteration-count: infinite;
    animation-play-state: running;
}
}
