css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.smashingmagazine.com") {
.latestJobs, .banner-list, #gsmall, #rss, #comments, noscript, script, .an, .an img {display: none !important; }

h2, ul.topic-list h2, h1.title { 
font-size: 20px !important;
 }

.topic h3, h3 { font-size: 18px !important; }
.topic h4 { font-size: 16px !important; }
#footer p { font-size: 11px !important; }

p { font-size: 12px !important;}

[href="http://twitter.com/smashingmag"] { display:none !important; }
[href*="ad"] { display:none !important; }
img[src*="rss-feed2.jpg"]{display: none !important; }
img[src*="shadow_leaf.gif"]{display: none !important; }
img[src*="qp.png"]{display: none !important; }

}
