css:
@-moz-document domain("fourtoutici.top"), domain("fourtoutici.xyz"), domain("fourtoutici.info"), domain("fourtoutici.pro") {
@font-face {
    font-family : Roboto;
    src: url('https://github.com/google/fonts/blob/master/apache/roboto/Roboto-Regular.ttf');
}

center {
    font-family : Roboto!important;
}

td[face="Verdana"] {
    background-color : #fff0!important;
}

img[src="images/refresh.gif"] {
    content: url(https://www.materialui.co/materialIcons/navigation/refresh_black_144x144.png) !important;
}

img[src="images/download.gif"] {
    content: url(data:image/png;base64,) !important;
}

img[src="images/calendar.gif"] {
    content: url(data:image/png;base64,) !important;
}

a[href="index.php"] {
    text-align : justify!important;
}

center {
    font-family : Trebuchet MS!important;
}

table[border="0"] {
  position: relative;
  margin: 0.5rem 0 1rem 0;
  background-color: #ffffff!important;
  -webkit-transition: -webkit-box-shadow .25s;
  transition: -webkit-box-shadow .25s;
  transition: box-shadow .25s;
  transition: box-shadow .25s, -webkit-box-shadow .25s;
  border-radius: 2px;
  -webkit-box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
}
}
