css:
@-moz-document domain("avenoel.org") {
.message-content {
    font-size: 22px!important;
    color: white;
    font-family: Comic Sans MS;
}

.tex2jax_ignore {
    background-image: url('https://nsa39.casimages.com/img/2018/11/22/181122094933587198.png')!important;
}

.navbar {
    background-color: rgb(13, 47, 137);
    outline-style: solid;
    outline-offset: -5px;
    outline-width: 5px;
    outline-color: white;
}

section {
    background-color: rgb(13, 47, 137)!important;
}

.navbar:after {
    color: white!important;
    background-color: white !important;
}

.container-content {
    background-color: rgb(13, 47, 137);
    outline-style: solid;
    outline-offset: -5px;
    outline-width: 5px;
    outline-color: white;
}

a,
li {
    color: white;
    font-family: Comic Sans MS;
    font-size: 13px;
    text-overflow: ellipsis;
}
.aside {
    display: none!important;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: rgb(13, 47, 137);
    border: 3px solid #fff;
}

.table-striped > tbody > tr:nth-of-type(even) {
    border: 3px solid #fff;
}

.col-xs-1,
.col-sm-1,
.col-md-1,
.col-lg-1,
.col-xs-2,
.col-sm-2,
.col-md-2,
.col-lg-2,
.col-xs-3,
.col-sm-3,
.col-md-3,
.col-lg-3,
.col-xs-4,
.col-sm-4,
.col-md-4,
.col-lg-4,
.col-xs-5,
.col-sm-5,
.col-md-5,
.col-lg-5,
.col-xs-6,
.col-sm-6,
.col-md-6,
.col-lg-6,
.col-xs-7,
.col-sm-7,
.col-md-7,
.col-lg-7,
.col-xs-8,
.col-sm-8,
.col-md-8,
.col-lg-8,
.col-xs-9,
.col-sm-9,
.col-md-9,
.col-lg-9,
.col-xs-10,
.col-sm-10,
.col-lg-10,
.col-xs-11,
.col-sm-11,
.col-md-11,
.col-lg-11,
.col-xs-12,
.col-sm-12,
.col-md-12,
.col-lg-12 {
    position: relative;
    min-height: 10px;
    min-width: 1100px!important;
    padding-left: 5px;
    padding-right: 5px;
}

.topic-message.odd {
    background: rgb(0, 70, 255);
    border: 3px solid #fff;
}

.topic-message {
    background: rgb(0, 70, 255);
    border: 3px solid #fff;
}

.message-content-quote,
.spoiler-content {
    background: white!important;
}

a {
    color: yellow!important;
    font-weight: 900!important;
}

.spoiler .spoiler-btn {
    color: #fcff00;
    cursor: pointer;
}

button {
    background-color: yellow!important;
    color: rgb(0, 70, 255)!important;
    font-weight: 900!important;
}

.board-picture {
    min-width: 80px!important;
    min-height: 60px!important;
}

.bbcodes {
    background-color: yellow!important;
}

h2,
p {
    color: #fcff00;
}

.profile .profile-section,
.breadcrumb {
    background-color: rgb(0, 70, 255);
    padding: 15px;
    border-radius: 6px;
    margin-bottom: 1rem;
}

div {
    color: white;
}

.navbar-logo {
    content: url('https://nsa39.casimages.com/img/2018/11/22/181122103355110787.png')!important;
    min-width: 30px;
    min-height: 70px;
    padding: 5px;
}

.navbar-logo:after {
    background-image: url('https://nsa39.casimages.com/img/2018/11/22/181122103355110787.png')!important;
    visibility: visible;
}
}
