css:
@-moz-document domain("warframe.market") {
/* -------- Background -------- */
.btn__inside-input,
.chat__controls,
.chat__empty,
.chat__form textarea,
.content .content__header,
.content__body select option,
.content__header flex--left,
.content__header select option,
.content__header--second select option,
.content__header--second select,
.floating-menu__section .root-menu-button > ul,
.form-control,
.form-control:focus,
.item-statistics__header,
.item_orders .item_orders__sort,
.review__zero,
.seeker .seeker__input,
.selector .selector__body,
.statistics__header,
.statistics__numbers,
.user-orders__sort {
    background: #1c1c24;
}
.ducats,
.floating-menu__section .root-menu-button > ul li.online-switch > span:hover,
.message.own,
.order-row,
.order-unit:nth-child(odd),
.widget-order__top ul li:nth-child(odd) {
    background: #202029;
}
#cdm-zone-0b,
#wfm-bottom-zone-lg,
.ad__line,
.cdm-zone-0b,
.content .content__body,
.content .content__header--second,
.floating-menu,
.floating-menu__section .root-menu-button > .combobox,
.floating-menu__section .root-menu-button > a,
.seeker .seeker__dropdown,
.slideout-panel,
.sorting,
.user__about--edit textarea,
.widget-order__content .widget-order__header,
.widget-order__content {
    background: #24242f;
}
.ducats:nth-child(even),
.floating-menu__section .root-menu-button > ul li.online-switch > span.active,
.messenger__block .messenger__chat_block .chat__block,
.messenger__block .messenger__chat_list,
.messenger__block.opened .chat__row,
.order-row:nth-child(even),
.order-unit,
.seeker .seeker__dropdown li:hover,
.seeker .seeker__dropdown li.selected,
.widget-order__top ul li {
    background: #282835;
}

/* -------- Color -------- */
.content h1,
.user__about--edit textarea,
.widget-order__content .widget-order__header {
    color: #77939b;
}
.form-control:focus,
.seeker .seeker__input .seeker__real-input input {
    color: #fff;
}
.content h3 {
    color: #ba55d3;
}
.filter__unit input[type=number] {
    color: #a5aeb2;
}
a,
.content a {
    color: #5490a0;
}

/* -------- Border -------- */
.ad__line .flex--left .selector .selector__body,
.btn__inside-input,
.chat__controls,
.sort-button,
.statistics__header,
.user-orders__sort,
.widget-order__content .widget-order__header {
    border-bottom: 1px solid #265663;
}
.selector .selector__body {
    border-left: 1px solid #265663;
}
.btn__inside-input {
    border-right: 1px solid #265663;
}
.btn__inside-input,
.selector .selector__body {
    border-top: 1px solid #265663;
}
.ad__line {
    border: none;
}
.chat__form textarea,
.seeker .seeker__input,
.widget-order__top ul {
    border: 1px solid #0000;
}

/* ---- Statistics ---- */
.group__controls > rect {
    fill: #1c1c24;
}
.group__legend > rect {
    fill: #1c1c24;
    stroke: #282835;
}
.item-statistics select {
    background: #1c1c24;
    border: 1px solid #282835;
    color: #77939b;
}

/* ---- ... ---- */
section.content + section.flex--root {
    background-color: #24242f;
}
.settings__page .settings__content,
.content__body .container {
    background: #24242f !important;
}
.settings__content input,
.settings__content select,
input.form-control,
a.just_link,
.social_links,
.achievement__block {
    background: #1c1c24 !important;
    border: 1px solid #282835 !important;
    color: #77939b !important;
}
.settings__content .input-group-addon {
    background: #282930;
    border-color: #1c1c24 !important;
    color: #77939b;
}

/* ---- Reviews ---- */
.review__form select,
.review__form .subtext {
    background: #1c1c24;
    border: 1px solid #282835;
    color: #77939b;
}
.review__form textarea,
.review__form textarea:invalid {
    background: #1c1c24;
    border: 1px solid #282835;
    box-shadow: 0 0 3px 1px #0000;
    color: #77939b;
    transition: box-shadow .2s ease-in-out, border .2s ease-in-out;
}
.review__form textarea:focus {
    border: 1px solid #0000;
    box-shadow: 0 0 3px 1px #a500ff;
}

.review__block,
.review__block p,
.review__listing + nav li a.active {
    background: #1c1c24;
    border: 1px solid #0000 !important;
    color: #77939b;
}

/* ---- Chat ---- */
.chat__form textarea {
    background: #1c1c24;
    border: 1px solid #282835;
    box-shadow: 0 0 3px 1px #0000;
    color: #77939b;
    transition: box-shadow .2s ease-in-out, border .2s ease-in-out;
}
.chat__form textarea:focus {
    border: 1px solid #0000;
    box-shadow: 0 0 3px 1px #a500ff;
}
.chat__row .chat__row__link.active {
    background: #202029;
}
.chat__row {
    background: #24242f;
}
.chat__row:hover {
    background: #282835;
}
.chat__messages .chat__options {
    background: #24242f;
    border-bottom: 1px solid #3c3b49;
}

/* ---- Search Filter Box ---- */
.filter__search input {
    box-shadow: 0 0 3px 1px #0000;
    transition: box-shadow .2s ease-in-out, border .2s ease-in-out;
}
.filter__search input:focus {
    box-shadow: 0 0 3px 1px #a500ff;
    border: 1px solid #0000 !important;
}
.filter__search.wrong input {
    box-shadow: 0 0 3px 1px #f31b43;
    border: 1px solid #0000 !important;
}
.filter__search.active input {
    box-shadow: 0 0 3px 1px #39a57e;
    border: 1px solid #0000 !important;
}

/* ---- ... ---- */
.item_orders--empty {
    background-color: #202029;
}
.transition__group,
.verification__tutorial .compose__box,
.verification__tutorial li:nth-child(even) > div {
    border: none;
    background-color: #282835;
}
.achievement__block * {
    border-color: #282835 !important;
}
.review__listing + nav li a.btn.active {
    color: #39a57e;
}
.floating-menu .logotype {
    border-left: 5px solid #0000;
}
.floating-menu .logotype img {
    filter: invert(1) hue-rotate(290deg) drop-shadow(0px 0px 10px #808080);
    overflow: visible;
}
.description__flex,
.floating-menu__section li.separator {
    border: 0px;
}
.floating-menu__section .root-menu-button > ul li.button > a:hover,
.floating-menu__section .root-menu-button > ul li.button > div:hover,
.floating-menu__section .root-menu-button > ul li.button > a:focus,
.floating-menu__section .root-menu-button > ul li.button > div:focus {
    background-color: #282835 !important;
    color: #399ea5;
}
.floating-menu__section .root-menu-button > ul li.button > a.active,
.floating-menu__section .root-menu-button.active > a,
.floating-menu__section .root-menu-button.active > .combobox,
.floating-menu__section .root-menu-button.active > ul li.button > a.active {
    color: #ba55d3 !important;
}
.floating-menu__section .root-menu-button > ul li.button > a:hover .wfm-icon,
.floating-menu__section .root-menu-button > ul li.button > div:hover .wfm-icon,
.floating-menu__section .root-menu-button > ul li.button > a:focus .wfm-icon,
.floating-menu__section .root-menu-button > ul li.button > div:focus .wfm-icon {
    fill: #399ea5;
}
.floating-menu__section .root-menu-button > ul li.button > a.active .wfm-icon,
.floating-menu__section .root-menu-button.active > .combobox .wfm-icon,
.floating-menu__section .root-menu-button.active > a .wfm-icon {
    fill: #ba55d3 !important;
}
.floating-menu__section .root-menu-button > ul,
.floating-menu__section .root-menu-button > ul li.online-switch {
    border-top-color: #282835;
    border-top-style: solid;
}
.floating-menu__section .root-menu-button > ul li.online-switch {
    border-bottom-color: #282835;
    border-bottom-style: solid;
}
.sell_color {
    color: #a83983 !important;
}
.buy_color {
    color: #359b76 !important;
}
.order-unit .order-unit__icon img {
    border-color: #265663;
}
.order__type-marker.marker_buy {
    background-color: #265663 !important;
    color: #54bfdc !important;
}
.order__type-marker.marker_sell {
    background-color: #632632 !important;
    color: #dc546f !important;
}

/* ---- Buttons ---- */
.btn.focus,
.btn:focus {
 box-shadow: none;
}

.btn__light {
    background: #24242f;
    border: 1px solid #31303e;
    color: #77939b;
}
.btn__light:hover {
    background: #31303e !important;
    border: 1px solid #31303e;
    box-shadow: none;
    color: #7da5b0;
}
.btn__light.active,
.btn__light:active,
.btn__light:focus {
    background: #31303e !important;
    border: 1px solid #31303e;
    box-shadow: none;
    color: #83b1bd;
}

.btn__action.red,
.btn__filter.red,
.red.btn__action {
    background: #86262b;
}
.btn__action.red:hover,
.btn__filter.red:hover,
.red.btn__action:hover {
    background: #a63238;
}
.btn__action.red:active,
.btn__filter.red:active,
.red.btn__action:active {
    background: #c5323e;
}

.btn__light.red {
    border: 1px solid #31303e;
    color: #bf1a1a;
}
.btn__light.red:hover {
    border: 1px solid #31303e;
    box-shadow: none;
    color: #d62923;
}
.btn__light.red:active {
    border: 1px solid #31303e;
    color: #e5342a;
}

.btn.btn__action.active.wtb,
.btn.btn__action.active.wts,
.btn.btn__radio.active,
.btn.btn__action.active {
    background: #39a57e !important;
}

.filter__region select {
    background: #1c1c24;
    border: 1px solid #282835;
    color: #77939b;
}

/* ---- Orders ---- */
.order__inactive:nth-child(even) {
    border-left: 5px solid #86262b;
}
.order__inactive:nth-child(odd) {
    border-left: 5px solid #651e20;
}
.order__own_line,
.order__own_line:nth-child(odd) {
    background: #1e001e !important;
    box-shadow: inset 0 0 3px 1px #ba55d3;
}

/* ---- Order Widget ---- */
.widget-order__button .big-red__button {
    background: #ba55d3;
}
.widget-order__button:hover .big-red__button {
    background: #daa520;
}
.widget-order__button.active .big-red__button,
.widget-order__button:active .big-red__button,
.widget-order__button:focus .big-red__button {
    background: #ffd700;
}

.widget-order__button .big-red__text {
    color: #ba55d3;
}
.widget-order__button:hover .big-red__text {
    color: #daa520;
}
.widget-order__button.active .big-red__text,
.widget-order__button:active .big-red__text,
.widget-order__button:focus .big-red__text {
    color: #ffd700;
}

.widget-order__form .seeker .seeker__input {
    border: 1px solid #282835;
}

.widget-order__image {
    border: 3px solid #a500ff;
    box-shadow: 0 5px 20px -1px #a500ff;
}
.widget-order__image.icon_zoomable img {
    position: absolute;
    z-index: 1;
}

/* ---- ... ---- */

.content__header--transparent {
    background: #1c1c24bf;
}
.item__page .content__header--transparent .container a {
    border: 3px solid #6d4a80;
    box-shadow: 0 7px 15px -3px #265663;
}
.item__page .content__header--transparent .container a:hover {
    border: 3px solid #9370db;
    box-shadow: 0 5px 20px -1px #9370db;
}
.item__page .content__header--transparent .container a.active {
    border: 3px solid #a500ff;
    box-shadow: 0 5px 20px -1px #a500ff;
}
.item__page .content__header--transparent .container .item__name .item__name-highlight {
    color: #9370db;
}

/* ---- Seeker Bar ---- */
.seeker .seeker__input.wrong {
    box-shadow: 0 0 10px 1px #f00;
}
.seeker .seeker--focus {
    box-shadow: 0 0 10px 1px #a500ff;
}

.header .search-field button {
    background: #ba55d3;
    box-shadow: none;
}
.header .search-field button:hover {
    background: #d354f3;
}
.header .search-field button:active,
.header .search-field button:focus {
    background: #de68fc;
}

/* ---- Aletrs ---- */
.alert-success {
    background: #cfefb6;
    border-color: #cfefb6;
    color: #58882e;
}
.alert-warning {
    background: #fde1ac;
    border-color: #fde1ac;
    color: #97761e;
}

/* ---- Online Status ---- */
.s-ingame {
    color: #9761d4;
}
.s-online {
    color: #54a617;
}
.s-offline {
    color: #d32f4a;
}
.s-invisible {
    color: #9ea9a9;
}

/* ---- Ducantor ---- */
.ducats__per-platinum {
    color: #daa520;
}
.ducats__position--positive {
    color: #4eb2cc;
}
.ducats__position--positive .wfm-icon {
    fill: #4eb2cc !important;
}
.ducats__position--negative {
    color: #cc444f;
}
.ducats__position--negative .wfm-icon {
    fill: #cc444f !important;
}
}
