css:
@-moz-document url-prefix("https://www.twitch.tv") {
.scrollable-area .simplebar-track.vertical .simplebar-scrollbar {
	width: 0rem;
	background: Transparent;
	visibility: hidden;
}

.tw-pd-l-2 {
	padding-left: 0.5rem!important;
	padding-right: 0.5rem!important;
}
.tw-pd-b-2 {
	padding-left: 0.5rem!important;
	padding-right: 0.5rem!important;
}
.chat-line__message,
.chat-line__moderation,
.chat-line__status {
	padding: .5rem 2rem;
	padding-top: 0.5rem;
	padding-right: 0.5rem;
	padding-bottom: 0.5rem;
	padding-left: 0.5rem;
	word-wrap: break-word;
}
.tw-justify-content-between {
	justify-content: space-between!important;
}
.chat-room-header-label {
	Width: 0px;
	height: 0px;
}
.tw-c-text-alt {
	font-size: 0px;
}
.room-selector__header {
	Width: 0px;
	height: 0px;
}
.tw-svg__asset--roomlist {
	Width: 0px;
	height: 0px;
}
.chat-list__more-messages.tw-full-width {
	width: 150%!important;
	margin-left: -25%;
}
#root > div > div.tw-flex.tw-flex-column.tw-flex-nowrap.tw-full-height > div > div.right-column.right-column--theatre.tw-fixed.tw-flex-shrink-0.tw-full-height.tw-right-0.tw-top-0 > div > div > div.tw-flex.tw-flex-column.tw-flex-grow-1.tw-flex-nowrap.tw-full-height.tw-full-width.tw-relative > div.room-selector__header.tw-align-items-center.tw-border-b.tw-border-l.tw-border-r.tw-c-background-alt.tw-flex.tw-flex-shrink-0.tw-full-width.tw-justify-content-between.tw-pd-l-2.tw-pd-r-1 > div.tw-relative > button > span > div {
	background-color: transparent;
	font-size: 0%;
}
#root > div > div.tw-flex.tw-flex-column.tw-flex-nowrap.tw-full-height > div > div.right-column.right-column--theatre.tw-fixed.tw-flex-shrink-0.tw-full-height.tw-right-0.tw-top-0 > div > div > div.tw-flex.tw-flex-column.tw-flex-grow-1.tw-flex-nowrap.tw-full-height.tw-full-width.tw-relative > section > div > div.chat-input.tw-block.tw-pd-b-2.tw-pd-x-2 {
	padding-bottom: 0.5rem!important;
}
}
