/* Workflowy Colored Underlines */

@-moz-document domain(workflowy.com) {

.wrong-proj > .name > .content,
.bad-proj > .name > .content,
.error-proj > .name > .content,
.incorrect-proj > .name > .content,
.no-proj > .name > .content {
    text-decoration: underline wavy Red;
}


.warning-proj > .name > .content {
    text-decoration: underline dashed Goldenrod;
}

.info-proj > .name > .content {
    text-decoration: underline dotted CornflowerBlue;
}

.correct-proj > .name > .content,
.good-proj > .name > .content,
.yes-proj > .name > .content,
.right-proj > .name > .content {
    text-decoration: underline solid ForestGreen;
}

}
