/* Workflowy Colored Special Tags
Here are places you can find more color options:
 - CSS named colors:        http://www.colors.commutercreative.com/grid/
 - Hexadecimal color codes: http://html-color-codes.info/        

Here is the template:

    span[title="Filter <<<tag>>>"] {
        background-color: <<<background color>>> !important;
        color: <<<font color>>> !important;
        box-shadow: 2px 2px 2px #424242;
    }

Replace the `<<<xyz>>>` parts. That `box-shadow` property is optional.
*/

@-moz-document domain(workflowy.com) {

.contentTagText {
    text-decoration: none !important;
}

/* Personal */

span[title="Filter #TODO"], span[title="Filter #ToDo"], span[title="Filter #todo"] {
    background-color: LawnGreen !important;
    color: Black !important;
}

span[title="Filter @/*[[Tag1Text]]*/"], span[title="Filter #/*[[Tag1Text]]*/"] {
    background-color: Green !important;
    color: White !important;
}

span[title="Filter @/*[[Tag2Text]]*/"], span[title="Filter #/*[[Tag2Text]]*/"] {
    background-color: Red !important;
    color: White !important;
}

span[title="Filter @/*[[Tag3Text]]*/"], span[title="Filter #/*[[Tag3Text]]*/"] {
    background-color: Orange !important;
    color: Black !important;
}

span[title="Filter @/*[[Tag4Text]]*/"], span[title="Filter #/*[[Tag4Text]]*/"] {
    background-color: Yellow !important;
    color: Black !important;
}

/* Color Blocks */

span[title="Filter #bk"]:hover, span[title="Filter @bk"]:hover,
span[title="Filter #gn"]:hover, span[title="Filter @gn"]:hover,
span[title="Filter #rd"]:hover, span[title="Filter @rd"]:hover,
span[title="Filter #bl"]:hover, span[title="Filter @bl"]:hover,
span[title="Filter #pk"]:hover, span[title="Filter @pk"]:hover,
span[title="Filter #pl"]:hover, span[title="Filter @pl"]:hover,
span[title="Filter #bn"]:hover, span[title="Filter @bn"]:hover,
span[title="Filter #gr"]:hover, span[title="Filter @gr"]:hover {
    color: white !important;
}

span[title="Filter #yl"]:hover, span[title="Filter @yl"]:hover,
span[title="Filter #rg"]:hover, span[title="Filter @rg"]:hover,
span[title="Filter #wt"]:hover, span[title="Filter @wt"]:hover,
span[title="Filter #cn"]:hover, span[title="Filter @cn"]:hover {
    color: black !important;
}

span[title="Filter #bk"], span[title="Filter @bk"],
span[title="Filter #gn"], span[title="Filter @gn"],
span[title="Filter #rd"], span[title="Filter @rd"],
span[title="Filter #bl"], span[title="Filter @bl"],
span[title="Filter #pk"], span[title="Filter @pk"],
span[title="Filter #pl"], span[title="Filter @pl"],
span[title="Filter #bn"], span[title="Filter @bn"],
span[title="Filter #gr"], span[title="Filter @gr"] {
    caret-color: white !important;
}

span[title="Filter #yl"], span[title="Filter @yl"],
span[title="Filter #rg"], span[title="Filter @rg"],
span[title="Filter #wt"], span[title="Filter @wt"],
span[title="Filter #cn"], span[title="Filter @cn"] {
    caret-color: black !important;
}

span[title="Filter #bk"], span[title="Filter @bk"],
span[title="Filter #wt"], span[title="Filter @wt"] {
    border-style: solid;
    border-width: 1px;
    margin: -1px  !important;
}

span[title="Filter #bk"], span[title="Filter @bk"] {
    background-color: Black !important;
    color: Black !important;
    border-color: white;
}

span[title="Filter #wt"], span[title="Filter @wt"] {
    background-color: White !important;
    color: White !important;
    border-color: Black;
}

span[title="Filter #gn"], span[title="Filter @gn"] {
    background-color: Green !important;
    color: Green !important;
}

span[title="Filter #rd"], span[title="Filter @rd"] {
    background-color: Red !important;
    color: Red !important;
}

span[title="Filter #bl"], span[title="Filter @bl"] {
    background-color: Blue !important;
    color: BLue !important;
}

span[title="Filter #pk"], span[title="Filter @pk"] {
    background-color: DeepPink !important;
    color: DeepPink !important;
}

span[title="Filter #pl"], span[title="Filter @pl"] {
    background-color: BlueViolet !important;
    color: BlueViolet !important;
}

span[title="Filter #yl"], span[title="Filter @yl"] {
    background-color: Yellow !important;
    color: Yellow !important;
}

span[title="Filter #rg"], span[title="Filter @rg"] {
    background-color: Orange !important;
    color: Orange !important;
}

span[title="Filter #bn"], span[title="Filter @bn"] {
    background-color: SaddleBrown !important;
    color: SaddleBrown !important;
}

span[title="Filter #gr"], span[title="Filter @gr"] {
    background-color: Gray !important;
    color: Gray !important;
}

span[title="Filter #cn"], span[title="Filter @cn"] {
    background-color: Cyan !important;
    color: Cyan !important;
}
}
