css:
@-moz-document url(http://haloprint/knowledgebase/) {
html {
    background-color: #4a4c4f;
}
body {
    height: initial;
    width: initial;
}
#detail > tbody {
    display: flex;
    flex-wrap: wrap;
}
#detail tr {
    background-color: lightblue;
    padding: 2px;
}
#detail input,
#detail select {
    font-size: 14px;
    font-family: sans-serif;
    border: none;
}
#detail input {
    height: 19px;
}
#detail tr:last-child,
#detail tr td:first-child {
    display: none;
}
#Suggestion {
    color: lightblue;
}
#up1 > .Contentleft30 > h2 {
    display: none;
}
#up1 > .Contentleft30:first-child {
    width: 70%;
}
#up1 > .Contentleft30:first-child h2 {
    margin: 6px 0 0 0;
    min-width: 210px;
}
#up1 > .Contentleft30:nth-child(2) {
    display: none;
}
#up1 > .Contentright30:nth-child(3) {
    float: right;
}
#up1 > .Contentright30:nth-child(3) a {
    padding-right: 10px;
}
#up1 > .contentbot + br,
#up1 > .contentbot + br + br,
#up1 > .contentbot + br + br + br,
#up1 > .contentbot + br + br + br + br {
    display: none;
}
#up1 > table {
    width: 100% !important;
}
.content {
    background-color: #4a4c4f;
    border: none;
    left: 0;
    padding: 10px;
    position: absolute;
    text-align: left;
    top: 0;
    width: calc(100% - 20px);
}
.whiteback {
    font-size: 14px;
}
.whiteback a {
    text-decoration: none;
}
.whiteback tbody tr {
    border-top: 3px solid #bbb !important;
}
.whiteback tbody tr:last-child {
    border-bottom: 3px solid #bbb !important;
}
.whiteback tbody tr td:nth-child(2),
.whiteback tbody tr td:nth-child(5) {
    display: none;
}
.whiteback tr td {
    padding: 6px 8px !important;
    border-color: #bbb;
}
.paddinleft30 {
    padding: 10px;
}
.paddinleft30 h4 {
    margin: 4px 0 10px 0;
}
#viewProductCatagory > tbody .paddinleft30 {
    background-color: #bbb;
}
#viewProductCatagory > tbody td {
    background-color: initial !important;
}
#viewProductCatagory tr tr td:nth-child(4) {
    width: 4% !important;
}
#ProductLine a {
    background-color: white;
    font-size: 14px;
    font-family: sans-serif;
    padding: 4px 6px !important;
    border: 4px solid white;
    margin: 0;
}
input.altologo {
    display: none;
}
.Datalistspacer {
    padding: 3px;
    border: none !important;
}
.Datalistspacer input {
    border: none;
    font: 14px sans-serif;
    padding: 4px 6px;
    background-color: white;
}
.Datalistspacer input,
.Datalistspacer a {
    border: 4px solid white;
    border-radius: 4px !important;
}
.Datalistspacer:hover input,
.Datalistspacer:hover a {
    cursor: pointer;
    color: black;
    margin: -2px !important;
    border: 6px solid lightblue !important;
}
.Datalistspacer[style="display:inline-block;background-color:Green;border-width:1px;border-style:Solid;"],
.Datalistspacer[style="display:inline-block;background-color:Green;border-width:1px;border-style:Solid;"] input,
.Datalistspacer[style="display:inline-block;background-color:Green;border-width:1px;border-style:Solid;"] a {
    background-color: initial !important;
    color: white;
    font-weight: 600;
}
input[value="SRV service instructions"],
input[value="Display Cases"],
input[value="Cook & Hold"] {
    background-color: #efffa0 !important;
    border-color: #efffa0;
}
input[value="Technical Service manual"],
input[value="Smokers"],
input[value="Xcelerate"] {
    background-color: #ffa0a0 !important;
    border-color: #ffa0a0;
}
input[value="cheat sheet"],
input[value="Quickchiller"] {
    background-color: #a0c4ff !important;
    border-color: #a0c4ff;
}
input[value="operation & care manual"],
input[value="Heated Holding"],
input[value="Wells"] {
    background-color: #a0fff3 !important;
    border-color: #a0fff3;
}
input[value="spec sheet"],
input[value="Spec Sheet"],
input[value="Convection"] {
    background-color: #a0ffa6 !important;
    border-color: #a0ffa6;
}
input[value="wire diagram"],
input[value="Wire Diagram"],
input[value="Vector Ovens"],
input[value="Combitherm"] {
    background-color: #ffa0ef !important;
    border-color: #ffa0ef;
}
input[value="work instructions"],
input[value="Work Instruction"],
input[value="Rotisserie"],
input[value="Parts"] {
    background-color: #ffcfa0 !important;
    border-color: #ffcfa0;
}
}
