css:
@-moz-document domain("forum.lowyat.net") {
#ipbwrapper div:nth-child(3n-1) {
  display:none
}

td[style*="DFE6EF"]{
    display:none !important;
}
td a span:hover {
    background-color:green !important
}
a span {
    display:inline-block
}
.nopad, .like, .bar, .desc, .minipagelink, .minipagelinklast {
    display:none !important
}
.row2, .row1 {
    font-size: 18px;
    font-weight: 300;
    line-height: 1.3;
    white-space: nowrap;
}
.minipagelink, .minipagelinklast {
    border: 0;
    font-size: 10px;
    margin: 0 1px 0 0;
}
.pagelinklast, .minipagelinklast {
    background: #f5f5f5;
}
img {
    image-rendering: pixelated;
    image-rendering: -moz-crisp-edges;
    image-rendering: crisp-edges;
    -webkit-filter: blur(10px);
    /* Safari 6.0 - 9.0 */
    filter: blur(10px);
    transition:0.3s;
    width: 94%;
}
img:hover {
    -webkit-filter: blur(0px);
    /* Safari 6.0 - 9.0 */
    filter: blur(0px);
    transition:0.6s;
}
.pagecurrent {
    background-color: #747474;
    float: right;
    border-radius: 7px;
    border: 0;
    padding: 3px;
    margin-top: -3px;
    font-size: 20px;
    font-weight: 900;
    color: #fff;
}
.pagelink a:hover, .pagelinklast a:hover, .minipagelink a:hover, .minipagelinklast a:hover {
    background-color: #747474;
    border-radius: 7px;
    color: #fff;
    padding: 3px;
}
a[title*=Yesterday]:link, a[title*="2019"]:link, a[title*="2018"]:link, a[title*="2017"]:link, a[title*="2016"]:link, a[title*="2015"]:link, a[title*="2014"]:link, a[title*="2013"]:link, a[title*="2012"]:link, a[title*="2011"]:link, a[title*="2010"]:link {
    padding: 0;
    text-decoration: line-through;
}
a[title*=Today]:link {
    padding: 0px;
    color: #000;
    padding-right :10px;
}
a[href*="tag=Group"] {
    display:none
}
#ads33 {
    display:none
}
a span {
    color: #fffb00 !important;
    font-weight:bold;
    font-stretch: extra-condensed;
    width: 400px;
    overflow: hidden;
    text-overflow: ellipsis !important;
    margin-right:-50px !important;
    ;
    float:left;
}
a:visited span {
    text-decoration: line-through;
    color: #f00
}
.pagelink, .pagelinklast, .minipagelink, .minipagelinklast {
    background: none;
    border: 0;
    padding: 3px;
    float: right;
}
a[href^="/topic///+"], #forum_topic_emoticon {
    display:none
}
a[href*="tag=News"], a[href*="tag=Chat"] {
    display:none
}
a:link, element.style, span {
    color:#949494;
}
a:visited, body a:visited {
    text-decoration: line-through;
    color:#f00
}
a:active {
    animation:blinkingText 0.8s infinite;
    color:#949494
}
.spoilertop {
    background: #ffffff;
    /* border: 1px dotted #000; */
    color: #000;
    width: 90%;
    font-weight: bold;
    font-size: 10px;
    margin: 8px auto 0 auto;
    padding: 4px;
    border-radius: 10px;
}
.pagelink a:active, .pagelink a:visited, .pagelink a:link, .pagelinklast a:active, .pagelinklast a:visited, .pagelinklast a:link, .pagecurrent a:active, .pagecurrent a:visited, .pagecurrent a:link, .minipagelink a:active, .minipagelink a:visited, .minipagelink a:link, .minipagelinklast a:active, .minipagelinklast a:visited, .minipagelinklast a:link {
    text-decoration: none;
    padding: 3px;
}
body {
    font-family:"Roboto", Arial, sans-serif;
    color:#353c41;
    line-height: 1.6;
    font-size: 20px;
    background: none;
    margin-top: -20px;
    width:450px;
    padding:0;
    font-stretch: condensed;
    background-color:black;
}
#topic_replies {
    position: fixed;
    border: 0px solid black;
    background: #31310c;
    color: #03b90a;
    left: 2px;
    top: 2px;
    padding: 3px 5px 3px 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 19px;
    z-index: 9999999;
    margin-top: 20px;
    -webkit-animation: flash linear 1s infinite;
    animation: flash linear 1s infinite;
}
@-webkit-keyframes flash {
    0% {
        opacity: 1;
    }
    50% {
        opacity: .5;
    }
    100% {
        opacity: 1;
    }
}
@keyframes flash {
    0% {
        opacity: 1;
    }
    50% {
        opacity: .5;
    }
    100% {
        opacity: 1;
    }
}
.quotemain {
    background: #FAFCFE;
    border: 0px dotted #000;
    border-left: 0px solid #8394B2;
    border-top: 0;
    color: #465584;
    padding: 4px
}
.post1 a:link {
    color: #b1b1b1;
    /* text-decoration: underline;*/
    font-size:10px;
    padding:0px;
}
SPAN.member-tagged {
    background-color: rgb(85, 86, 86);
    padding: 5px;
    border:0;
    border-radius: 12px;
}
.post_text {
    font-size: 14px;
    line-height: 1.6;
}
strong, b {
    font-weight: 400;
}
#navstrip a:link, #navstrip a:visited {
    background: #656565;
    text-decoration: none;
    -moz-border-radius: 8px;
    -webkit-border-radius: 8px;
    border-radius: 16px;
    padding: 14px;
    color: #FFF;
    padding-left: 8px;
    padding-right: 8px;
    border:0
}
#navstrip {
    color: #ffffff;
    font-size: 12px;
    font-weight: bold;
    margin: 8px 2px 5px 520px;
    padding: 0;
    position: fixed;
    opacity: 0.9;
}
#fb-root, img[src*="gif"], a[href="/Lifestyle"], a[href="/"], #fc_23, br, #fo_23, .signature, .edit, .borderwrap h3, .ipbtable, th, .row2, .lastaction, td.row1 img, form, #ads_bottom, .barc, .quotetop, .catend, .subtitle, .formbuttonrow, .postdetails, .post_td_left, .maintitle, .expand, #gfooter, #rules, .copyright, .activeusers, .nopad, .globalmessage, #userlinksguest, #logostrip, #submenu, .background, .maintitlecollapse {
    display:none
}
td {
    background-color:#000
}
.borderwrap, img.attach {
    border: 0
}
.post1, .bg1, .post1 .post_text, .post1 .post_table {
    background-color:#484848;
    border:0;
    color:#b1b1b1
}
.post2, .bg3, .post2 .post_text, .post2 .post_table {
    background-color:#2d2d2d;
    border:0;
    color:#7b7b7b
}
.borderwrap {
    background-color:#000;
    margin:2px
}
.quotemain, .borderwrap, .post1, .post2 {
    border-radius:0px;
    padding:5px
}
.quotemain, img.attach {
    font-size: 20px;
    font-style: italic;
    font-stretch: extra-condensed;
}
.quotemain br, .quotemain br div a {
    display: none
}
#topic_content {
    padding:2px;
    background-color:#2d2d2d
}
.maintitle {
    margin-top:-115px
}
table th, .borderwrap table th, .subtitle, .subtitlediv, .postlinksbar {
    background: #2d2d2d;
    color: #ccc;
    font-size: 10px;
    font-weight: bold;
    letter-spacing: 1px;
    margin: 0;
    padding: 5px;
}
.post2 .post_text a img, .post1 .post_text a img {
    padding:15px;
    border-radius:40px;
}
.row1 {
    background: #2d2d2d;
    text-transform: capitalize;
    border: 0px solid #000;
    margin:0;
    padding:0;
}
#forum_topic_title {
    padding-left:5px;
}
.row2, .post_table, .post_text {
    background: #2d2d2d;
}
.darkrow1 {
    background: #ffe71224;
    color: #fff;
}
#ipbwrapper {
    margin:0
}
.post1 .quotemain, .post1 .post_text a img {
    background:#3e3e3e;
    border: 1px dashed #737373;
    color:#777;
    border-radius: 25px;
}
.post2 .quotemain, .post2 .post_text a img {
    background:#fbfbfb;
    border: 1px dashed #ccc;
    color:#777;
    border-radius: 25px;
}
.quotemain {
    overflow-y: hidden;
    resize: vertical;
    transition:0.3s;
}
html{padding-top: 1em;}
}

@-moz-document url-prefix("https://forum.lowyat.net/Kopitiam") {
#forum_topic_list {

    transition: transform 0.8s;

    -webkit-animation: slide-in-top 0.5s cubic-bezier(0.230, 1.000, 0.320, 1.000) both;

    animation: slide-in-top 0.5s cubic-bezier(0.230, 1.000, 0.320, 1.000) both;

}

@-webkit-keyframes slide-in-top {

    0% {

        -webkit-transform: translateY(-1000px);

        transform: translateY(-1000px);

        opacity: 0;

    }

    100% {

        -webkit-transform: translateY(0);

        transform: translateY(0);

        opacity: 1;

    }

}

@keyframes slide-in-top {

    0% {

        -webkit-transform: translateY(-1000px);

        transform: translateY(-1000px);

        opacity: 0;

    }

    100% {

        -webkit-transform: translateY(0);

        transform: translateY(0);

        opacity: 1;

    }

}

/*#forum_topic_list {

    margin-top:-8em;

    z-index:-1000000

} */
}

@-moz-document url-prefix("https://forum.lowyat.net/topic") {
#topic_content {
    transition: transform 0.8s;
    -webkit-animation: slide-in-bottom 0.9s cubic-bezier(0.390, 0.575, 0.565, 1.000) both;
    animation: slide-in-bottom 0.9s cubic-bezier(0.390, 0.575, 0.565, 1.000) both;
}
@-webkit-keyframes slide-in-bottom {
    0% {
        -webkit-transform: translateY(1000px);
        transform: translateY(1000px);
        opacity: 0;
    }
    100% {
        -webkit-transform: translateY(0);
        transform: translateY(0);
        opacity: 1;
    }
}
@keyframes slide-in-bottom {
    0% {
        -webkit-transform: translateY(1000px);
        transform: translateY(1000px);
        opacity: 0;
    }
    100% {
        -webkit-transform: translateY(0);
        transform: translateY(0);
        opacity: 1;
    }
}
}
