css:
@-moz-document url("https://www.easports.com/fifa/ultimate-team/web-app/"), url("https://www.easports.com/fifa/ultimate-team/web-app/#") {
body{
    background-image:none !important;
    background-color: #555;
    color: #CCC;
}
.slotDock .content-container{
	background-image: none !important;
}
#FIFAHeader{
    display: none;
}
.ut-button-group button.cta{
	background-color: #555;
}
.with-fifa-header .view-root{
    height: 100%;
}

.NavigationBar.navbar-style-landscape, .sectionHeader{
	background-color: #555;
    color: #CCC;
    border-bottom: solid 1px #111;
}
.tab-menu{
	background-color: #555;
    border-bottom: 1px solid #444;
}
.tab-menu-item, .ut-leaderboards-hub--category{
	color: #CCC;
}
.tab-menu-item.selected, .tab-menu-item.hover{
	color: #a2a2a2;
}
.tab-menu-item.hover::after, .tab-menu-item.active::after, .tab-menu-item.selected::after{
	background-color: #a2a2a2;
}
.tile, .tile.disabled, .tile.hover, .dialog-body, .ut-leaderboards-hub--header .ut-drop-down-control, .ut-leaderboards-hub--titles, .pseudo-table{
    background-color: #444;
    color: #CCC;
}
.pseudo-table > li:nth-child(2n){
	background-color: #464646
}
.ut-leaderboards-hub--titles{
	border-width: 0.5px 0;
    border-style: solid;
    border-color: #888;
}
.tile header, .tile .tileHeader, .ut-tile-content-squad .squad-name, .challenge-details .requirements-container .secondary.title, .challenge-details .rewards-container .secondary.title, .sbc-requirements-checklist li, .objective.tile.completed .secondary, .objective.tile.in-progress .secondary, .ut-popover, .ut-popover h3, .btn-squad-tab, .ut-weekend-league-progress-view--title{
    color: #CCC;
}
.tile:hover{
	background-color: #333;
}
.list-view-header h2{
	color: #999;
}
.ut-tile-content-graphic-info .image, .QuickListPanel .panelActions{
    background-color: #666;
}
.tile.hover .ut-tile-content-graphic-info .image, .ut-leaderboards-hub .ut-content-container .ut-content, .ut-leaderboards-hub--header, #HubMessagesTile .carousel, .panel-list, .challenge-details, .ut-sbc-requirements-popup footer, .ut-squad-overview .ut-squad-summary, .slotDock .content-container, .SBCSquadPanel, .SquadBuilder, #InGameRewards footer, .ut-weekend-league-progress-view{
    background-color: #444;
}
.ut-sbc-requirements-popup footer{
	border: none;
}
.ut-tile-content-transfers, #HubMessagesTile.disabled, .challenge-details .description, .objective.tile.completed, .objective.tile.in-progress, .SBCSquadPanel .sbc-status-container{
    background-color: #444;
    color: #CCC;
}
.SBCSquadPanel .sbc-status-container{
	border-top: solid 2px #666;
}
.tile.hover .ut-tile-content-transfers{
    background-color: #444;
}
.ut-popover, .ut-popover::before{
	background-color: rgba(68, 68, 68, 0.95);
}
.ut-squad-overview .ut-squad-summary::after, .ut-squad-overview .ut-squad-summary::before{
	border-top: 3.5em solid #444;
}
.btn-squad-tab::after{
	border-bottom: 5rem solid #444;
}
.btn-squad-tab::before{
	background-color: #444;
}
.btn-squad-tab.call-to-action.disabled::before, .btn-squad-tab.disabled::before{
	background-color: #333;
}
.btn-squad-tab.call-to-action.disabled::after, .btn-squad-tab.disabled::after{
	border-bottom-color: #333;
}
.btn-squad-tab.call-to-action::after{
	border-bottom-color: #401;
}
.btn-squad-tab.call-to-action::before{
	background-color: #401;
}
.btn-squad-tab.hover::before{
	background-color: #4A4A4A;
}
.btn-squad-tab.hover::after{
	border-bottom: 5rem solid #4A4A4A;
}
.ut-content-container.ut-content-container--split-view .ui-layout-right, .ut-content-container.ut-content-split-screen .ui-layout-right{
	border-left: 1px solid #484848;
}
.list-view{
	background-color: #999;
}
.emptySectionMessage{
	background-color: #333;
}
.listFUTItem, .ut-leaderboards-entry{
	background-color: #444;
    border-color: #555;
    border-width: 1px 0 0 0;
    border-style: solid;
}
.listFUTItem.selected .rowContent, .listFUTItem.selected .rowContent.active{
	background-color: #777;
}
.listFUTItem.selected .rowContent .name, .listFUTItem.selected .rowContent.active .name, .listFUTItem.selected .rowContent .secondary, .listFUTItem.selected .rowContent.active .secondary, .listFUTItem.selected .auction .label, .listFUTItem.selected .rowContent, .listFUTItem.selected .rowContent.active{
	color: #333;
}
.listFUTItem.expired .auction-state .time, .sbc-tile.sbc-set .tileHeader, .sbc-tile.sbc-set header, .sbc-tile.sbc-set .image, .sbc-tile.sbc-set .tileContent, .btn-squad-tab.hover{
	color: #CCC;
}
.ut-content-container.ut-content-container--split-view .ui-layout-right, .ut-content-container.ut-content-split-screen .ui-layout-right{
	background-color: #444;
}
.ut-button-group button{
	background-color: #444;
    color: #CCC;
}
.ut-button-group button.hover{
	background-color: #777;
    color: #333;
}
.NavigationBar.navbar-style-secondary{
	background-color: #999;
    color: #333;
}
.pagingContainer{
	background-color: #999;
    color: #333;
}
button.flat.hover, .listFUTSquad .rowContent, .ut-squad-summary, .sbc-tile.sbc-set-info h1{
	color: #CCC;
}
.DetailView .ut-item-details--metadata, .DetailView .bidOptions{
	background-color: #012;
}
input[type="text"], input[type="tel"], input[type="email"], input[type="password"], .price-filter .numericInput{
	background-color: #444;
    color: #CCC;
}
.ut-toggle-control--track, .ut-toggle-control--grip{
	background-color: #999;
}
.btn-standard.call-to-action, .ut-toggle-control.toggled .ut-toggle-control--track{
	background-color: #401;
}
.btn-standard.hover.call-to-action, .ut-toggle-control.toggled .ut-toggle-control--grip{
	background-color: #502;
}
.ut-pinned-list-container .ut-pinned-list, .button-container, .sort-filter-container{
	background-color: #555;
}
.ut-search-filter-control, .inline-list li:hover, .inline-list li.selected, .ut-drop-down-control, .sort-group-container, .StorePack, .sbc-tile.sbc-set, .panel-list > div:not(.btn), .panel-list > p:not(.secondary), .listFUTSquad, .sbc-tile.sbc-set-info, .sbc-tile.sbc-challenge .tileContent, .sbc-tile.sbc-challenge.sbc-tile, .objectives-progress{
	background-color: #444;
    color: #CCC;
}
.sbc-tile.sbc-set-info .sbc-status-container{
	background-color: #474747;
    color: #CCC;
}
.ut-search-filter-control.hover, .ut-search-filter-control.is-open, .inline-list li, .ut-drop-down-control.hover, .ut-drop-down-control.is-open{
	background-color: #777;
    color: #393939;
}
.ut-search-filter-control.has-selection .ut-search-filter-control--row{
	border-left-color: #ea7;
}
.ut-search-filter-control.has-selection:not(.disabled) .label, button.flat{
	color: #ea7;
}
.ut-content-container .ut-content{
	max-width: 1100px;
    max-height: 91%;
}
.inline-list-select .inline-list{
	max-height: 23rem;
}
#ut-search-wrapper .search-price-header h1, .price-filter, .sort-filter-container h4, .StorePack .details .title, .ut-list-header .ut-list-header-title, .listFUTSquad.selected .rowContent .name, .listFUTSquad.selected .rowContent.active .name, .challenge-details{
	color: #CCC;
}
.btn-standard{
	background-color: #AAA;
}
.btn-standard.hover{
	background-color: #999;
}
.ut-search-filter-control.disabled{
	background-color: #000;
    color: #111;
}
.ut-content--split-view-extend ut-content{
	background-color: #111;
}
.StorePack .details .packCounts .packCount span.value{
	color: #999;
}
.sbc-tile.sbc-set.hover, .sbc-tile .group-rewards, .ut-sbc-reward-preview-modal, .ut-livemessage .ut-content, .ut-list-header{
	background-color: #333;
    color: #888;
}
.listFUTSquad.hover .rowContent.has-tap-callback{
	background-color: #777;
    color: #333;
}
.sbc-tile.sbc-challenge.sbc-tile.hover, .sbc-tile.sbc-challenge.hover .tileContent{
	background-color: #4A4A4A;
}
.listFUTSquad.selected .rowContent, .listFUTSquad.selected .rowContent.active{
	background-color: #111;
}
.objective.tile.in-progress .status{
	color: #e44;
}
.objective.tile.active.redeemed, .objective.tile.redeemed{
    background-color: #1c2538;
}
.ListRowClubCategory{
	background-color: #555;
    border-color: #484848;
}
}
