css:
@-moz-document domain("disqus.com") {
img {
		background-color: #111 !important;
		filter: grayscale(30%) brightness(70%) !important;
	}
	.auth-container img {
		background-color: transparent !important;
		filter: brightness(90%) !important;
	}
	body {
		background: #111 !important;
		color: #bbb !important;
	}
	body.dark {
		background: #111 !important;
		color: #bbb !important;
	}
	body::before {
		background-image: linear-gradient(to bottom right, #111a22 30%, rgba(1, 1, 1, 0) 60%) !important;
		background: transparent;
	}
	.header {
		background: #111a22 !important;
	}
	.textarea-outer-wrapper .textarea-wrapper {
		background: #161616 !important;
		border: 2px solid #333 !important;
		color: #bbb !important;
	}
	.postbox .textarea-wrapper {
		background: #161616 !important;
		border: 2px solid #333 !important;
		color: #bbb !important;
	}
	.textarea div, .textarea p {
		color: #bbb !important;
	}
	.post-actions {
		border-top: solid 2px #333 !important;
		background: transparent !important;
	}
	.wysiwyg__item a.attach span {
		filter: invert(100%);
	}
	.site-nav--wrapper { 
		background: #161616 !important;
		background-size: auto 100%; 
		border-bottom: 1px solid #101010;
	}

	.module-header, .module-footer { 
		border-color: #333;
	}

	.cover-profile-avatar img, .fieldset__avatar--img img { 
		border: 1px solid #646464;
		box-shadow: 3px 3px 3px #000;
	}

	.cover-profile-name { 
		color: #2E9FFF;
	}

	.cover-nav-wrapper { 
		border-color: #373737;
	}

	.cover { 
		font-family: sans-serif; 
		background: rgba(25,25,25,.9) !important;
		background-clip: padding-box !important;
		border-bottom: 1px solid rgba(0, 0, 0, 0);
		box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8);
	}

	#container-main {
		background: #111 !important;
		background-repeat: no-repeat !important; 
		background-attachment: fixed !important; 
		background-position: 25% !important; 
		-webkit-background-size: cover !important;
		-o-background-size: cover !important; 
		background-size: cover !important;  
		overflow-y: auto !important; 
		overflow-x: hidden !important; 
	}

	#container-page.bg--light, .discussion-page {
		background: #111 !important;
		color: #bbb !important;
	}

	.link-gray-darker, 
	.module-header__title, 
	.card-content__body .discussion-title, 
	.card-profile .card-reason .link-community { 
		color: #bbb;
	}

	.card-reason, 
	.card-profile .card-reason .link-discussion, 
	.link-gray, 
	.link-inner-gray a, 
	.card-comment .comment-message, 
	.no-touch .link-gray-light:hover, 
	.card-comment .card-comment-truncated, 
	.no-touchevents .link-gray-light:hover { 
		color: #777;
	}

	.nav--aside .nav__item.active, 
	.nav-stacked .nav__item.active, 
	.nav--no-aside .nav__item.active { 
		background-color: #2B2B2B;
	}

	.nav-tabs, 
	.nav__item { 
		background-color: #161616;
		border-color: #333;
	}

	.nav--aside, 
	.nav-stacked, 
	.nav__spacing, 
	.nav--no-aside { 
		border: transparent;
	}

	.form, 
	.card-wrap, 
	.content__wrap, 
	body:not(.home-drawer) .card-notif, 
	body:not(.home-drawer) .card-profile, 
	body:not(.home-drawer) .card-profile-forum { 
		box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
		background-color: #161616;
		border: 1px solid #333;
		border-radius: 7px;
	}

	.card, 
	.form__header, 
	.form__footer, 
	.border-bottom, 
	.recommended-wrap, 
	.card__reason:first-child, 
	.card-notif-upvote .card-comment .comment-body, 
	.card-notif-reply .card-comment .parent .comment-body { 
		border-color: #333;
	}

	.aside__wrap { 
		box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
		background-color: #161616;
		border: 1px solid #333;
		border-radius: 3px;
	}

	.layout__aside .text-smaller { 
		box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
		background-color: #161616;
		border: 1px solid #333;
		border-radius: 3px;
		padding: 10px; 
	}

	div[data-role="content-area"] .card--empty { 
		box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
		background-color: #161616;
		border: 1px solid #333;
		border-radius: 3px;
		padding: 10px; 
		margin-bottom: 10px;
	}

	.input--textbox, 
	.fieldset__avatar--options select { 
		background: #2C2C2C;
		border-color: #666;
		color: #CCC;
	}

	.layout .more-wrapper { 
		background: linear-gradient(to bottom,rgba(73,69,72,1) 0, rgba(35,31,32,1) 100%);
		box-shadow: 0 -2px 5px 1px rgba(0,0,0,.7), 
		0 75px 20px 1px rgba(0,1,0,.5);
		border: 1px solid #333;
	}

	.aside-list__item { 
		border: transparent;
	}

	.dropdown-menu { 
		box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
		background-color: #161616;
		border: 1px solid #333;
		border-radius: 3px;
	}

	.dropdown-settings .divider { 
		border-color: #333;
	}

	.dropdown-menu:before { 
		border-color: transparent transparent #161616;
	}

	.dropdown-menu::after { 
		border-color: transparent transparent #333;
	}

	.card--notification .card-comment .reply, 
	.card--notification .card-comment .reply-disabled, 
	.card--notification .card-comment .view-comment, 
	.no-touch .card--notification .card-comment .view-comment, 
	.no-touchevents .card--notification .card-comment .view-comment { 
		background: #232323;
		border-color: #333;
		box-shadow: 0 0 0 1px #333 inset;
	}

	.label--count, 
	.no-touch .button-outline, .no-touchevents .button-outline, 
	.no-touch .button--recommend, .no-touchevents .button--recommend {
		border-color: #333 !important;
		background-color: #232323 !important;
	}

	.carousel-control { 
		background: rgba(25,25,25,.9);
	}

	.carousel-control:hover { 
		background: rgba(25,25,25,.7);
	}

	.module__wrapper::before { 
		background-color: #000;
	}

	.card__additional { 
		background-color: #222;
		border-color: #333;
		border-radius: 0 0 7px 7px;
	}

	.nav-tabs { 
		border-radius: 7px 7px 0 0;
	}

	.home-drawer #container-main > div, 
	.home-drawer.active .drawer-footer { 
		background-color: #222;
	}

	.home-drawer .card-profile, 
	.home-drawer .cover-profile, 
	.home-drawer .notifs-fixed, 
	.home-drawer #container-main, 
	.home-drawer.active .drawer-footer { 
		border-color: #333;
	}

	.curtain-truncate { 
		border-color: #2E9FFF;
	}
	.highlighted > .post-content {
		background-color: #111 !important;
	}
	.state-byline, .state-byline-default {
		color: #DAA520 !important;
	}
	.has-edit{
		color: #bbb !important;
		text-decoration: underline !important;
	}
	.media-container img {
		z-index: 0 !important;
	}
	.publisher-anchor-color a {
		color: #2e9fff;
	}
	.tab-conversation.active > a {
		color: transparent !important;
	}
	.site-nav--secondary {
		background: #161616 !important;
		border-top: 1px solid #222;
	}
	.container {
		background: #111 !important;
		background-repeat: no-repeat !important; 
		background-attachment: fixed !important; 
		background-position: 25% !important; 
		-webkit-background-size: cover !important;
		-o-background-size: cover !important; 
		background-size: cover !important;  
		overflow-y: auto !important; 
		overflow-x: hidden !important;
	}
	.form-row input {
		background-color: #161616 !important;
		border: 1px solid #333 !important;
		color: #bbb !important;
	}
	.submit {
		background: rgba(29,47,58,.6) !important;
		color: #ddd !important;
	}
	a {
		text-decoration: none;
		color: #2e9fff;
		-webkit-transition: color linear 0.1s;
		-moz-transition: color linear 0.1s;
		-ms-transition: color linear 0.1s;
		-o-transition: color linear 0.1s;
		transition: color linear 0.1s;
	}
	
	.bg--light, .bg--white {
		background-color: transparent !important;
	}

	.site-nav--marketing {
		background-color: #111a22 !important;
	}

	.panel--hero-home-reactions-promotion {
		background: transparent url('https://i.postimg.cc/wBbmQXTZ/reactions-promotion.png') no-repeat center;
	}
	
	.padding-top-none {
		background: #222;
	}
	
	.panel.-trusted {
		background: #111;
	}
	
	.case-study__wrapper {
		background: #222;
		border: none;
	}
	
	.text-gray-darker {
		color: #bbb;
	}
	
	.panel.-gray {
		background: transparent;
	}
	
	.site-footer {
		background-color: #111a22;
	}
    .newsletter-box {
        background: #222 !important;
    }
    .newsletter-box__input-group input {
        background:#222 !important;
        color:#bbb !important;
    }
    .button-fill--brand {
        border-color: transparent !important;
        background-color: rgba(29,47,58,.6) !important;
        color:#bbb !important;
    }
    .input--text {
        background:rgba(22, 75, 120, 0.1) !important;
        border: 1px solid rgba(28, 92, 146, 0.3) !important;
        color: #bbb !important;
    }
}
