css:
@-moz-document domain(mforum.ist) {
.p-pageWrapper {
    position: relative;
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    background-image: url('https://i.imgur.com/sHyNctV.jpg');
    background-color: #150303;
}
.block-container {
    color: #09ddf3fa;
    background: #1c3c4100 ;
    border-width: 1px;
    border-style: solid;
    border-top-color: #2a4d6300 ;
    border-right-color: #25455800 ;
    border-bottom-color: #1d374600 ;
    border-left-color: #07ddff;
    border-radius: 4px;
}
  
.p-footer {
    font-size: 13px;
    color: #ffffff;
    text-decoration: none;
    background: #3c3f4100;
    /* visibility: hidden !important; */
}
.pageWidth {
  background-color: #da0637 ;
  font-size: 12px;
  padding-bottom: 10px;
  color: #ffffff ;
}
.profileCover .profileCover-inner {
    /* border-color: blue; */
    padding: 3px 10px;
    border: #00dffb 2px solid;
    border-radius: 80px;
}
.p-header {
    text-decoration: none;
    background: transparent !important;
    background-image: none !important;
    background-repeat: repeat-x;
}
.reactionsBar {
    background: transparent !important;
    border-radius: 200px ;
    border-left: 2px solid #0088e6;
}
.username--style2 {
    color: #0eb6f7;
}
span.username {
    color: #0eb6f7;
}
.button.button--link, a.button.button--link {
    color: #f10969; 
}
.memberHeader-banners, .memberHeader-blurb {
    margin-top: 3px;
    color: #09b7de;
}
.pairJustifier {
    color: #00b8ff;;
}
.block--messages .message-cell:first-child {
    background-color: transparent;
}
.message-responseRow.message-responseRow--likes.is-active {
    background-color: transparent;
}
.message-footer {
    background-color: transparent;
}
.message-responseRow {
    background: transparent;
    border: 1px dashed #0eb6f7;
}
.block--messages .message, .block--messages .block-row {
  color: #fffffffa;
    background: #1c3c4100 ;
    border-width: 1px;
    border-style: solid;
    border-top-color: #2a4d6300 ;
    border-right-color: #25455800 ;
    border-bottom-color: #1d374600 ;
    border-left-color: #07ddff;
    border-radius: 4px;
}
  .avatar img:not(.cropImage) {
    border-radius: 50%;
    transition: width 2s, height 2s, transform 2s;
    -moz-transition: width 2s, height 2s, -moz-transform 2s;
    -webkit-transition: width 2s, height 2s, -webkit-transform 2s;
    -o-transition: width 2s, height 2s,-o-transform 2s;
}
.avatar img:not(.cropImage):hover{
        transform : scale(2);
        -moz-transform : scale(2);     
        -webkit-transform : scale(2);  
        -o-transform : scale(2);        
}
.memberHeader-main {
    background: transparent;
    border: #00dffb 2px solid;
    border-radius: 80px;
}
.node-main.js-nodeMain:hover {
    background-color: #09f7f752;
}
}
