css:
@-moz-document domain("younow.com"){
:root {
    --elight-bg: rgb(85, 85, 87) !important;
    --vlight-bg: rgb(68, 68, 70) !important;
    --light-bg: rgb(51, 51, 53) !important;
    --med-bg: rgb(41, 41, 43) !important;
    --dark-bg: rgb(34, 34, 36) !important;
    --vlight-text: rgb(210, 210, 212) !important;
    --light-text: rgb(177, 177, 179) !important;
    --med-text: rgb(153, 153, 155) !important;
    --dark-text: rgb(102, 102, 104) !important;
}
body,
.volume-background,
#momentpanel .moment-edit .moment-adjust,
#momentpanel .moment-edit .moment-caption input,
#sharepanel .share-message input,
#sharepanel .snapshot-href {
    background: var(--dark-bg);
}
#leftsidebar,
#sidebar-container,
.main-content .main-player,
.navbar {
    box-shadow: 0 0 10px #191919;
}
#sidebar-container #gifttray .gifttray-basic,
#leftsidebar,
#sidebar-container,
.main-content .main-player,
.navbar,
#playerheader,
#sidebar-container #chatcomments,
#sidebar-container #chatinput,
#sidebar-container #chatinput input[type=text],
#sidebar-container .next-fan,
#sidebar-container .prev-fan,
#sidebar-container .sidebar-header .tab-icon.selected,
.btn-fan,
.btn-subscribe,
#leftsidebar .panel-body .sidebar-icons .icon-wrapper,
.modal-body,
.profile-summary-wrapper .actions,
.navbar-content .user-actions .notifications .empty,
.comment-container,
.moment-container,
.dropdown-menu,
.leaderboard .lb-cell,
.leaderboard .lb-cell .fan-button-container,
.navbar-content .user-menu .dropdown-menu .user .user-progress .user-progress-bar,
#playerfooter,
#settingup-toolbar,
.player-toolbar,
.player-toolbar-right,
#momentpanel,
#momentpanel .moment-edit .slider-line,
#sharepanel,
#guestpanel .guest-preview.active,
.main-content.main-async .main-content-left .user-summary,
.main-content.main-async .main-content-left ul.nav {
    background-color: var(--light-bg);
}
.navbar-content .nav-logo {
    background-color: rgba(0, 0, 0, 0.1);
    width: 226px;
    height: 100%;
    display: inline-block;
    margin-right: 60px;
    text-decoration: none;
    text-align: center;
    vertical-align: middle;
    line-height: 55px;
    position: relative;
}
#leftsidebar .trending-users a:hover,
.btn-fan:hover,
.btn-subscribe:hover,
#leftsidebar .trending-tags-list a:hover,
#guestpanel .guest,
.settings-link,
.dropdown-menu > li > a:hover,
.leaderboard .lb-heading,
.navbar-content .user-menu .dropdown-menu .user,
#sidebar-container #gifttray .premium-selected,
#momentpanel .moment-edit .slider-handle,
#momentpanel .moment-edit .slider-wall,
#sidebar-container #chatcomments .chatcomment {
    padding: 6px 10px;
    -ms-word-break: break-all;
    word-break: break-all;
    width: 400px;
    overflow: hidden;
    -webkit-hyphens: auto;
    -moz-hyphens: auto;
    -ms-hyphens: auto;
    hyphens: auto;
    min-height: 40px;
    background: #444446;
    position: relative;
}
.navbar,
.tiny-search-active .search-box,
.tiny-search-active .search-field {
    background-color: #191919;
}
#leftsidebar .trending-users a.active,
.btn-fan:active,
.btn-subscribe:active {
    background-color: var(--elight-bg);
}
#leftsidebar .trending-users a.active,
#leftsidebar .trending-users a,
#leftsidebar .trending-tags-list a {
    border-radius: 5px;
}
#leftsidebar .panel-title,
#sidebar-container #chatinput,
#sidebar-container .topfan-slider-divider,
#sidebar-container #gifttray .toggle-tray-btn,
.profile-summary-wrapper .actions,
.modal-header,
#sidebar-container .sidebar-tab > div,
#guestpanel .guest,
.moment-feed .moment-education,
.moment-container .moment-metadata,
.leaderboard .lb-cell,
#sidebar-container #gifttray .premium-selected,
#momentpanel .moment-edit .moment-caption input,
.navbar-content .user-actions .notifications .dropdown-menu .list > *,
#sharepanel .share-message input,
#sharepanel .snapshot-href,
#sidebar-container #chatcomments .chatcomment.chat-mention,
.main-content.main-async .main-content-left .profile-actions,
#sidebar-container .sidebar-header .tab-icon {
    border-color: #585858;
    background-color: #444446;
}
#leftsidebar .panel-title span,
#leftsidebar .panel-body .sidebar-icons .icon-wrapper a i,
#leftsidebar .trending-user,
#leftsidebar .trending-tag,
#tag-queue .title,
#sidebar-container #chatcomments span,
#sidebar-container #chatinput input[type=text]::-webkit-input-placeholder,
#audiencepanel .viewer .viewer-name:hover,
#sidebar-container .sidebar-tab > div,
#audiencepanel .audience-summary.refresh,
.settings-link,
.moment-feed .main-content-right,
span[translate="profile_report_moment"],
.leaderboard .lb-heading .lb-time,
.player-toolbar-left .ynicon,
.toolbar-button span,
#momentpanel .panel-header .panel-title,
#momentpanel .moment-edit .moment-caption input,
#sharepanel .panel-header .share-title,
.profile-summary-wrapper .flagging-menu li a,
#sidebar-container div.fan-mail-widget .fan-mail-message,
#sidebar-container div.fan-mail-widget .fan-mail-sender,
.activity-panel .friends-list .friends-header,
[activity-panel] .friends-list .friends-header {
    color: var(--light-text);
}
body,
#playerheader .broadcaster-name,
#playerheader .broadcaster-name span,
#playerheader .short-text,
#playerheader .broadcaster-setup-description,
.selectUIInput,
#playerheader .broadcaster-description a,
#sidebar-container #chatcomments .chat-name,
#sidebar-container #chatcomments .ynicon-level,
#sidebar-container #topfan-slider,
#sidebar-container #chatinput input[type=text],
.profile-summary-wrapper .user-info .name,
.profile-summary-wrapper .summary,
.location,
#audiencepanel .viewer .viewer-name,
#guestpanel .guest-nav .guest-total,
span[translate="sorting_level"],
#guestpanel .guest-nav .btn-group .btn .ynicon-carrot-dwn,
.moment-container .moment-metadata .likes span,
.leaderboard .lb-heading .lb-title,
.leaderboard .lb-cell .lb-cell-inner .lb-username,
.navbar-content .user-menu .dropdown-menu li span,
.navbar-content .user-menu .dropdown-menu li .ynicon,
.navbar-content .user-menu .dropdown-menu .user,
.dropdown-menu > li > a,
.player-toolbar-right span,
.player-toolbar-right .volume-icon,
.player-toolbar-right,
.notification-item .description .name span,
.ellipsify > *:last-child,
.short-text > *:last-child,
.user-crowns,
.level,
.activity-panel .status span,
[activity-panel] .status span,
.main-content.main-async .main-content-left .name .a,
.main-content.main-async .main-content-left .name span,
#sidebar-container .sidebar-header .tab-icon.selected,
#sidebar-container .sidebar-header .tab-icon:hover,
#sidebar-container .sidebar-header .tab-icon:hover span,
#sidebar-container .sidebar-header .tab-icon.selected span,
#sidebar-container .sidebar-header .tab-icon.selected .short-text > *:last-child,
#sidebar-container .sidebar-header .tab-icon:last-child:hover .short-text > *:last-child {
    color: var(--vlight-text);
}
#leftsidebar .viewerCount,
#leftsidebar .panel-body .sidebar-links .links-left a,
#leftsidebar .panel-body .sidebar-links .links-right a,
#playerheader .broadcaster-description span,
span,
#audiencepanel div.audience-summary .ynicon-refresh {
    color: var(--med-text);
}
.nav-absolute .seen-dot,
#guestpanel .guest .thumbnail-placeholder,
#tag-queue .queue-item .thumb-label,
.btn-primary,
.navbar-content .user-menu .dropdown-menu .user .user-progress .user-progress-value,
.volume-foreground {
    background-color: #27b3f9;
}
#guestpanel .guest .thumbnail-placeholder {
    color: var(--elight-bg);
}
#guestpanel .guest .thumbnail-placeholder {
    border-radius: 5px;
}
#tag-queue .queue-item .thumb-label {
    border-radius: 5px 5px 0 0;
    box-shadow: 0 0 5px var(--dark-bg);
}
.btn-primary {
    border: none;
}
.tag-queue {
    background: #222224;
}
.btn-primary:hover {
    background-color: #6acaff;
}
.dropdown-menu .ynicon-carrot-up {
    color: var(--light-bg);
}
#playerheader .broadcaster-description span,
.main-content.main-async .main-content-left ul.nav > li > a {
    color: var(--med-text);
}
.leaderboard .lb-cell .fan-button-container {
    box-shadow: -10px 0 10px 0 var(--light-bg);
}
.leaderboard-lists .mini-scroll {
    background-color: var(--vlight-bg);
    color: var(--dark-text);
}
#leftsidebar .panel-body .language-panel .dropdown .dropdown-menu li div i,
#leftsidebar .panel-body .locale-panel .dropdown .dropdown-menu li div i,
.main-content.explore-content .result .ynicon-broadcast {
    color: #27b3f9;
}
.main-content.explore-content .result .user-profile,
.main-content.explore-content .result .box-thumb,
.tag-fade {
    border-radius: 10px;
}
.main-content.explore-content .result .user-tag {
    background: transparent;
}
.volume-slider {
    background-color: var(--med-text);
}
#momentpanel .moment-edit .moment-adjust {
    border-radius: 5px 5px 0 0;
}
#momentpanel .moment-edit .moment-picker img {
    border-color: var(--vlight-bg);
}
#momentpanel .moment-edit .moment-caption span {
    margin-right: 5px;
}
#momentpanel .panel-header .close,
#sharepanel .close-header .close {
    color: white;
}
.navbar-content .user-actions .notifications .feed .list li.new,
#sidebar-container .gifttray-wallet {
    background-color: var(--elight-bg);
}
.notification-item:hover {
    background-color: var(--vlight-bg);
}
#sidebar-container .gifttray-wallet .current-broadcaster {
    margin-left: 10px;
}
#sharepanel .share-message .character-count {
    margin-right: 17px;
}
#sharepanel .snapshot-href {
    border-radius: 4px;
}
#sharepanel .snapshot-image-img,
#guestpanel .guest-preview .preview-video-container #preview-video {
    border-radius: 4px;
}
#sharepanel .snapshot-image,
#guestpanel .guest-preview .preview-video-container #preview-video {
    box-shadow: 0 0 10px var(--dark-bg);
}
#sharepanel .social-icon-circle .social-share-icon .ynicon,
.btn-confirm {
    color: var(--light-bg);
}
.btn-fanned,
.btn-fanned:focus {
    background-color: #2795f9!important;
}
.btn-fanned:hover {
    background-color: #1f74c1!important;
    border-color: #1f74c1;
}
.profile-summary-wrapper .flagging-menu li a:hover,
#sidebar-container div.fan-mail-widget {
    background-color: var(--vlight-bg);
}
#guestpanel .guest-preview .preview-title .short-text {
    margin-left: 5px;
    color: var(--vlight-text);
}
.ynicon-icon-reply:before {
    filter: invert(100%);
}
#sidebar-container .sidebar-header .tab-icon,
#sidebar-container .sidebar-header .tab-icon span,
.short-text .guest-tab .ellipsify .ellipsify > *:last-child,
.short-text > *:last-child {
    color: var(--med-text);
}
#audiencepanel .audience-summary.refresh {
    background-color: var(--elight-bg);
}
.audience-summary {
    background-color: #393939;
}
.form-control,
.fans-tab .fans-tab-fan,
.fans-of-tab .fans-tab-fan {
    background-color: var(--light-bg);
}
.replies .comment-container,
.main-content.main-async .main-content-left .user-bio.editing,
.dropdown-menu,
.btn-fan,
.btn-subscribe {
    background-color: var(--med-bg);
}
.comment-container .comment-area,
.moment-container .comment-area,
.form-control,
.main-content.main-async .main-content-left .user-bio.editing {
    border-color: var(--elight-bg);
}
.comment-container .user-options,
.moment-container .user-options {
    filter: invert(50%);
}
.replies .comment-container {
    border-radius: 0;
}
.main-content.main-async .main-content-right .activity-panel .connect-social-accounts {
    border-radius: 3px;
}
#toolbar-like-button img {
    filter: invert(75%) hue-rotate(-60deg) brightness(1.2);
}
.toolbar-entry--btn {
    border-radius: 2rem;
    padding: .6em 1em;
    border: 1px solid #dddfe5;
    outline: 0;
    background: #8c7474;
}
.ynicon-carrot-up:before {
    content: "\24";
    color: #2795f9;
}
.ynicon-gift:before {
    content: "\52";
    color: #2795f9;
}
}
