	line-height: 1.75rem !important;
	padding-bottom: 12px !important;
	padding-top: 16px !important;
}
/* Scroll */
 .h1U9Be-xhiy4>.IZ65Hb-n0tgWb.IZ65Hb-QQhtn>.IZ65Hb-TBnied>.IZ65Hb-s2gQvd {
	max-height: calc(100% - 40px) !important;
