body {
	background: #fff;
	font-family: 'Roboto', sans-serif, 'Open Sans', sans-serif!important;
}








/**/
canvas#myCanvas {
	position: fixed;
	display: none!important;
}





/**/
/*logo*/
.mdl-layout__header-row {
	padding: 0 40px 0 15px!important;
	max-width: 1124px;
	width: 100%;
	margin: 0 auto;
}
.mdl-layout-title,
.mdl-layout__title {
	letter-spacing: 0;
}
