css:
@-moz-document domain("roblox.com")
{
  .ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,
.ad *,
.img_ad,
.ad-annotations,
#google_image_div,
#aw0,
#AdvertisingLeaderboard
  {
    display: none !important;
  }

  #header
  {
    opacity: 1;
    background-color: #0074bd;
    /*display: none;*/;
  }

  .rbx-scrollbar li .rbx-highlight
  {
    background: #00a2ff;
  }

  .rbx-header .rbx-nav-collapse .rbx-nav-notification
  {
    background: #007bc9;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #2400ff !important;
  }

  .rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button
  {
    background-color: #00a2ff !important;
    border-color: #ffffff00;
  }

  .rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover
  {
    background: #36b6ff !important;
    border-color: #e6ff00;
  }

  .navigation .notification-icon
  {
    background: #ffffff00;
  }

  #navbar-search-input
  {
    border: 1px solid #fff0;
  }

  #party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #e800ff, #00ffe0);
  }

  #ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }
/*body {
  	font-family: Tahoma;
    }*/

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    /*background-color: transparent !important;*/
    width: 100% !important;
  }

  #GamesPageRightColumnSidebar.sidebar-no-ad.games-page-right-sidebar
  {
    display: none;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(http://img.in.th/images/fc3035c785c5ad1dc2b4d524fbb2239f.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  body
  {
    -webkit-animation: fadein 0.4s;
    /* Safari, Chrome and Opera > 12.1 */
    -moz-animation: fadein 0.4s;
    /* Firefox < 16 */
    -ms-animation: fadein 0.4s;
    /* Internet Explorer */
    -o-animation: fadein 0.4s;
    /* Opera < 12.1 */
    animation: fadein 0.4s;
  }
@-o-keyframes fadein
  {
    from {
        opacity: 0;
  }

  to
  {
    opacity: 1;
  }
}
/* Firefox < 16 */

@-moz-keyframes fadein
{
  from
  {
    opacity: 0;
  }

  to
  {
    opacity: 1;
  }
}
/* Safari, Chrome and Opera > 12.1 */

@-webkit-keyframes fadein
{
  from
  {
    opacity: 0;
  }

  to
  {
    opacity: 1;
  }
}

#GamesListsContainer > div
{
  background-color: transparent !important;
  width: 100% !important;
}
/* Opera < 12.1 */

@-o-keyframes fadein
{
  from
  {
    opacity: 0;
  }

  to
  {
    opacity: 1;
  }
}

.badge-row .badge-data-container p,
.section-row,
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:hover,
.nav-tabs>li.active>a:focus,
.rbx-text-overflow,
.rbx-item-card .rbx-caption p,
div#testingSitePanelWrapper,
#BodyWrapper,
body,
#content,
.profile-avatar-left,
.tab-content-container,
.rbx-para-overflow,
.GroupWallPane .AlternatingItemTemplateEven,
#left-column .StandardBox,
#SearchControls,
.GroupControlsBox,
.SmallCatalogItemView,
.StandardPanelContainer .StandardPanelWhite,
.SquareTabGray.selected,
.SquareTabGray.selected:hover,
.SquareTabGray.ui-tabs-selected,
.SquareTabGray.ui-tabs-selected:hover,
.SquareTabGray.selected a:hover,
.SquareTabGray.ui-tabs-selected a:hover,
.SquareTabGray a:hover,
.CatalogItemInner,
.CatalogItemInner:hover,
#vorlias-nav
{
  background-color: rgba(255,255, 255, 1) !important;
}

.badge-row .badge-data-container p:after,
.rbx-text-overflow:after,
.rbx-item-card .rbx-caption p:after,
#Footer.footer-container,
.rbx-para-overflow:after,
.AjaxCommentsContainer .Post,
.AjaxCommentsContainer .PostBottom
{
  background-image: linear-gradient(to right, rgba(255, 255, 0, 1) 0, #fff 1000%) !important;
}

.rbx-item-card .rbx-caption,
img.original-image,
.vlist .list-item,
.game-main-content .game-play-buttons,
.section,
#horizontal-tabs,
.section,
.game-instances,
.developer-store,
.container-list,
.rbx-btn-primary-lg,
.rbx-btn-primary-lg:hover,
.rbx-btn-primary-lg:focus
{
  border: none !important;
}

.rbx-header
{
  background-color: rgba(0, 255, 255, 1);
}

#navigation
{
  background-color: rgba(255,255,255, 0.7);
}

.favorite-button-container span,
.game-calls-to-action .voting-panel .upvote span,
.game-calls-to-action .voting-panel .downvote span,
.rbx-link,
.rbx-link-sm,
.rbx-link-xs,
.vlist .list-item .list-body .list-content a,
.rbx-link:link,
.rbx-link-sm:link,
.rbx-link-xs:link,
.vlist .list-item .list-body .list-content a:link,
.rbx-link:visited,
.rbx-link-sm:visited,
.rbx-link-xs:visited,
.vlist .list-item .list-body .list-content a:visited,
.rbx-link:active,
.rbx-link-sm:active,
.rbx-link-xs:active,
.vlist .list-item .list-body .list-content a:active,
.game-main-content .game-calls-to-action .game-creator span
{
  color: #000 !important;
}

.rbx-link:hover,
.rbx-link-sm:hover,
.rbx-link-xs:hover,
.vlist .list-item .list-body .list-content a:hover,
.rbx-link:focus,
.rbx-link-sm:focus,
.rbx-link-xs:focus,
.vlist .list-item .list-body .list-content a:focus
{
  color: #00a2ff !important;
  text-decoration: none;
}

.rbx-btn-primary-lg
{
  background-color: rgba(0, 255, 0, 1);
}

.rbx-btn-primary-lg:hover,
.rbx-btn-primary-lg:focus
{
  background-color: rgba(0, 0,255, 1);
}
}
