css:
@-moz-document domain("anime-planet.com") {
  div.ttRating {
    visibility: hidden;
  }
  div.md-1-5.pure-1:nth-of-type(4) {
    display: none;
  }
  div.md-1-5.pure-1:nth-of-type(5) {
    display: none;
  }
  #entryDiscussion {
    display: none;
  }
  .reviewPreview {
    display: none; 
  }
  .pure-1.md-2-3 > h2 {
    visibility: hidden;
  }
  .halo.aligncenter {
    display: none;
  }
  .pure-table.striped.feed.short {
    display: none;
  }
  #entryFeed > .arrowMore:before {
    content: '';
  }
  #entryFeed > .arrowMore:after {
    content: ' Recent Activity';
  }
  #entryFeed > .smSidebar {
    visibility: hidden;
  }
}
